css:
@-moz-document url-prefix("https://trello.com/") {
/* change font to system */

body, button, html, input, select, textarea {
  font-family: "Proxima Nova", -apple-system, BlinkMacSystemFont, "Segoe UI", Helvetica, Arial, sans-serif, "Apple Color Emoji", "Segoe UI Emoji", "Segoe UI Symbol";
  letter-spacing: -.1px;
}


/* zoom out for more on screen */

body {
  zoom: 0.9 !important;
}


/* bigger line height */

p {
  line-height: 1.5;
}


/* list tweaks */

.list-wrapper {
  width: 300px;
}

.open-card-composer {
  transition: all 0.2s ease-out;
}

.open-card-composer:hover {
  background: #4786FF;
  color: #fff;
  text-decoration: none;
}




/* label tweaks */

.list-card-labels {
  margin-bottom: 4px;
}

.card-label.mod-card-front {
  min-width: 0;
  font-size: 11px;
  text-transform: uppercase;
}


/* tweak window stuff */
.window-overlay {
  background: rgba(0,0,0,0.3);
}

.window {
  box-shadow: 0 23px 40px rgba(0,0,0,0.15), 0 6px 10px rgba(0,0,0,0.12);
}




}
