css:
@-moz-document url-prefix("https://vk.com/") {
.nim-dialog:not(.nim-dialog_deleted).nim-dialog_hovered,
.nim-dialog:not(.nim-dialog_deleted).nim-dialog_unread.nim-dialog_classic,
.nim-dialog:not(.nim-dialog_deleted):hover {
    background: #5ea3f166;
}
.im-mess.im-mess_unread:not(.im-mess_light) {
    background: #c9e4ff;
}
.im-mess.im-mess_selected:last-child:before,
.im-mess.im-mess_unread:last-child:before {
    background: #c9e4ff;
}
.im-mess.im-mess_selected+.im-mess:before,
.im-mess.im-mess_unread+.im-mess:before {
    background: #c9e4ff;
}
.page_avatar {
    border-radius: 30px;
}
.page_block {
    border-radius: 10px;
}
.trg-b-banner-block {
    overflow: hidden;
}
.submit_post {
    border-radius: 0 0 20px 20px;
}
#post_field {
    border-radius: 20px;
}
.button_blue button,
.button_gray button,
.button_light_gray button,
.flat_button {
    border-radius: 20px;
}
#ads_left {
    display: none;
}
body {
    background: #2c2f3a;
}
#side_bar ol li .left_row {
    color: #ffffff;
}
.left_menu_nav {
    color: #bad7f3;
}
#page_header_cont .back {
    background: #647fb1;
    border-bottom: 1px solid #647fb1;
    border-radius: 0 0 50px 50px;
}
.wall_module>.page_block:first-child {
    border-radius: 20px 20px 0 0;
}
.top_home_link .top_home_logo {
    background: url(data:image/svg+xml;base64,);
    height: 48px;
    width: 48px;
    margin: -5px 10px 0 0;
}
.page_cover {
    border-radius: 10px 10px 0 0;
}
}
