css:
@-moz-document domain("smartmusic.com") {
/*Background*/
body {
    background-color: #080808;
    color: #aaa;
}
/*Only appears while loading*/
.search-category-cover {
    background: center center no-repeat #111;
}
.progress {
    background-color: #181818;
}
/*Input*/
input {
    background-color: #000;
}
input:not([type]),.input[type="email"], input[type="number"], input[type="password"], input[type="tel"], input[type="url"], input[type="text"] {
    background-color: #222;
    color: #aaa;
    border: 1px solid #333;
}
/*Topbar*/
.sm-navbar {
    background-color: #000;
    border: 1px solid #333634;
}
.sm-navbar-home-btn {
    background-color: #000;
    border-right: 1px solid #333;
}
.sm-navbar-nav {
    border-left: 1px solid #333634;
}
.sm-navbar-nav-item {
    background-color: #000;
}
.sm-navbar-nav-item.is-active {
    color: #aaa;
}
.sm-navbar-brand-icon {
    color: #aaa;
}
.sm-navbar-menu {
    border-left: 1px solid #333;
}
.sm-navbar-menu-btn {
    background-color: #000;
}
.sm-navbar-menu-item {
    border-right: 1px solid #333;
}
.sm-navbar-dropdown {
    background-color: #000;
    border: 1px solid #333;
}
.sm-navbar-dropdown:before {
    border-bottom-color: #333;
}
.sm-navbar-dropdown:after {
    border-bottom-color: #000;
}
.sm-navbar-dropdown-item {
    background-color: #000;
    color: #aaa;
}
.sm-navbar-dropdown-item:focus, .sm-navbar-dropdown-item:hover {
    background-color: #111;
    color: #aaa;
}
.sm-navbar-dropdown-item.is-disabled, .sm-navbar-dropdown-item:disabled {
    background-color: #000;
    color: #444;
}
.u-text-admin {
    color: #888 !important;
}
/*Bottom bar*/
footer {
    background-color: #111;
}
.sm-footer {
    background-color: #111;
}
/*Alerts*/
.alert-success {
    background-color: #3d7e1c;
    border-color: #3a7e1c;
    color: #dff0d8;
}
/*Login page*/
.section-divider {
    border-top: 1px solid #222;
}
.section-divider:before {
    background-color: #080808;
}
form .form-group label {
    color: #aaa;
}
/*User Profile*/
.form-control {
    border: 1px solid #222;
    background-color: #111;
}
.form-control[disabled], .form-control[readonly], fieldset[disabled] .form-control {
    background-color: #080808;
}
form input.form-control, form select.form-control, form textarea.form-control {
    color: #aaa;
}
input[type=checkbox]:after {
    background-color: #111;
    outline: 1px solid #222;
}
input[type=checkbox]:checked:after {
    border: 3px solid #111;
}
.btn-default, .btn-default-brand {
    color: #aaa;
    background-color: #111;
    border: 1px solid #222;
}
.btn:active, .btn:focus, .btn:hover, .btn:hover:active {
    color: #aaa;
    background-color: #222;
}
.bootstrap-select>.btn.dropdown-toggle:hover {
    color: #aaa;
    background-color: #111;
    border: 1px solid #222;
}
.bootstrap-select>.dropdown-toggle.bs-placeholder, .bootstrap-select>.dropdown-toggle.bs-placeholder:active, .bootstrap-select>.dropdown-toggle.bs-placeholder:focus, .bootstrap-select>.dropdown-toggle.bs-placeholder:hover {
    color: #555;
}
.dropdown-menu {
    border: 1px solid #111;
    background-color: #000;
    color: #111;
}
.dropdown-menu>li>a:focus, .dropdown-menu>li>a:hover {
    background-color: #111;
    color: #aaa;
}
.dropdown-menu>li>a {
    color: #aaa;
}
.bootstrap-select>.btn.dropdown-toggle:focus {
    background: #111 !important;
}
.btn-group .btn-default:focus {
    color: #aaa !important;
    border: 1px solid #222 !important;                 
}
.bootstrap-select.btn-group .no-results {
    background: #080808;
    color: #aaa;
}
.btn-success, .btn-danger {
    background-color: #111;
    border-color: #222;
}
/*Home Page*/
.h1, .h2, .h3, .h4, .h5, .h6, h1, h2, h3, h4, h5, h6 {
    color: #aaa;
}
.link-no-color {
    color: #999;
}
.ember-power-select-option[aria-current=true] {
    background-color: #111;
    color: #aaa;
}
.ember-power-select-dropdown.ember-basic-dropdown-content--below, .ember-power-select-dropdown.ember-basic-dropdown-content--in-place {
    border: 1px solid #222;
}
.deck, .deck-item-status, .deck-item-actions {
    background-color: #111;
}
.deck, .deck-item-status, .deck-item-actions {
	border-right: 1px solid #222;
}
.deck-item {
    border: 1px solid #222;
}
.deck-analysis-smclass {
    border-top: 1px solid #333;
    border-bottom: 1px solid #333;
}
.deck-footer-control[disabled] {
    color: #333;
}
.btn-info {
    background-color: #111;
    border-color: #222;
}
/*grades.smartmusic.com*/
.sm-submenu, .ember-power-select-trigger {
    background-color: #000;
    border: 1px solid #111;
}
.switch-input[type=checkbox] {
    background-color: #000;
    border: 1px solid #222;
}
.switch-input[type=checkbox]:before {
    background-color: #222;
}
.sm-table {
    color: #aaa;
}
.sm-table-row {
    border: 1px solid #222;
}
.sm-table-head .sm-table-row {
    border: 1px solid #282828;
}
.sm-table-head {
    background-color: #222;
}
.sm-table-sort-btn {
    color: #aaa;
}
.sm-table-body {
    background-color: #000;
}
.sm-table-clickable-row:hover {
    background-color: #080808;
}
.sm-table-header-cell-sticky {
    background-color: #222;
}
.split-panel-left .left-side, .split-panel-right .left-side {
    background-color: #111;
}
.profile-aside {
    background-color: #111;
}
hr {
    background-color: #222;
}
.student-assignments-container .right-side {
    background-color: #000;
}
.showcase-score-wrapper {
    background-color: #080808;
}
.circle-pie:before {
    background: #222;
}
.circle-pie::after {
    background: #333;
}
.assignment-info-title {
    border-top: 1px solid #282828;
}
.grade-scale-value {
    border-top: 1px solid #2a2827;
}
.part-content, .sm-submenu-btn {
    background-color: #000;
}
.showcase-score-wrapper {
    border-left: 1px solid #444;
}
/*Other big UI*/
.assignment-showcase-wrapper {
    background: linear-gradient(to right,#111 0,#111 40%,#000 40%,#000 100%);
}
.input-group-addon {
    background: #222;
    border: 1px solid #222;
    color: #aaa;
}
.modal-dialog .modal-content .modal-body, .modal-dialog .modal-content .modal-footer {
    background-color: #000;
}
.modal-dialog .modal-content .modal-header .modal-dialog .modal-content .modal-body, .modal-dialog .modal-content .modal-footer {
    background-color: #000;
}
.modal-dialog .modal-content .modal-header {
    background-color: #000;
}
.ember-power-select-option[aria-selected=true] {
    background-color: #181818;
}
.ember-power-select-option[aria-selected=false] {
    background-color: #000;
}
.split-panel-left .left-side, .split-panel-right .left-side {
    border-right: 1px solid #5a5a5a;
}
.split-panel-left .right-side, .split-panel-right .right-side {
    border-left: 1px solid #000;
}
/*Other UI*/
.sm-msgr-header {
    background-color: #111;
}
.sm-msgr-dropdown.has-items:after {
    border-bottom-color: #111;
}
.sm-msgr-item-header {
    border-bottom: 1px solid #222;
    color: #aaa;
}
.assessment-msg-item {
    color: #aaa;
}
.sm-msgr-item-actions {
    border-top: 1px solid #222;
}
a:focus, a:hover {
    color: #167af5;
}
/*Now onto https://asm.smartmusic.com (where music gets played and cool tools!)*/
.side-control.circle:hover {
    background-color: #0d0d0d;
}
.practice-header .tab-menu .circle-toggle-control button span {
    background-color: #555;
}
.practice-header .tab-menu .circle-toggle-control button {
    border: 1px solid #555;
}
.practice-header .tab-menu .circle-toggle-control button:active, .practice-header .tab-menu .circle-toggle-control button:focus, .practice-header .tab-menu .circle-toggle-control button:active:focus {
    border: 1px solid #777;
}
.tempo-control .tempo-input {
    background-color: #000;
    border: 1px solid #222;
}
#main-content .third-tier-menu input:focus, #main-content .third-tier-menu-bottom-border input:focus {
    background-color: #000;
}
.side-control.circle:focus {
    background-color: #444;
    border: 1px solid #333;
}
.side-control.circle:focus:hover {
    background-color: #555;
    border: 1px solid #383838;
}
.play-controls .core-control {
    background-color: #000;
    border: 1px solid #222;
}
.btn[disabled], .btn.disabled {
    border: 1px solid #222;
}
.play-controls .core-control[disabled] {
    background-color: #0a0a0a;
    cursor: not-allowed;
}
.dropdown button:hover {
    color: #aaa;
    background-color: #080808;
    border: 1px solid #222;
}
#main-content .third-tier-menu-bottom-border:after {
    background-color: #111;
}
/*  IMAGE FOR TUNING HERE  #Layer_1 {
    background-repeat: no-repeat; It's difficult to darken because the image is on a svg format. Ugh.. be a normal png or gif...
}*/
/*https://analytics.smartmusic.com/*/
.analysis-content {
    background-color: #111;
    border-left: 1px solid #333;
}
.analysis-wrapper {
    background: linear-gradient(to right,#222 0,#222 40%,#111 40%,#111 100%);
}
.analysis-section-container {
    background-color: #000;
}
.ct-label {
    fill: rgba(255,255,255,.4);
    color: rgba(255,255,255,.4);
}
.ct-grid {
    stroke: rgba(255,255,255,.2);
}
.time-list-table-label-cell {
    border-top: 1px solid #282828;
}
.radio[type=radio]:before, .checkbox[type=checkbox]:before {
    background-color: #000;
    border: 1px solid #111;
}
.ember-power-select-trigger[aria-disabled=true] {
    background-color: #111;
}
.content-list-item {
    border-bottom: 1px solid #222;
}

/*UNFINISHED search.smartmusic.com*/
.search-filters {
    background-color: #111;
    border-right: 1px solid #333;
}
.search-filters-title {
    border-top: 1px solid #444;
    border-bottom: 1px solid #444;
}
.search-filter-instruments-select .ember-power-select-trigger {
    border-bottom: 1px solid #222;
}
.search-filter-list {
    border-bottom: 1px solid #181818;
}
.search-result:hover {
    background-color: #080808;
}
.ember-basic-dropdown-content, .search-result.is-opened, .sm-pagination-item {
    background-color: #000;
}
.sm-pagination-item.is-disabled, .sm-pagination-item:disabled {
    border: 1px solid #181818;
}
.sm-pagination-item, .search-filter-expand-btn {
    border: 1px solid #222;
}
}
