css:
@-moz-document url-prefix("https://learn.freecodecamp.org/"), domain("learn.freecodecamp.org") {
.app-wrapper, header {
    color: whitesmoke;
}
header {
    background-color: #2D3439;
}
body:not([style="margin:8px;"]) {
    background-color: #1e1e1e;
}
#top-nav {
    background-color: #555;
}
.map-ui ul {
    color: #39c039;
}
a {
    color: #39c039;
}
a:hover {
    color: #64e964;
}
.btn-primary-invert {
    background-color: #303030;
    color: #39c039;
}
code {
    background-color: whitesmoke;
}
blockquote {
    background-color: #d4d4d4;
}
.test-result {
    background-color: #353535;
    padding: 7px;
}
.test-result:nth-child(2n+1) {
    background-color: #454545;
}
iframe {
    background-color: #fff;
}

/* TABLES */
.table-striped > tbody > tr:nth-of-type(2n+1) {
	background-color: #353535;
}

/* MODALS */
.modal-content {
    background-color: #1e1e1e;
}
.modal-header, .modal-footer {
    border: 0;
}
}

