css:
@-moz-document url-prefix("https://ggradio.net/") {
#content {
    margin-top: 16px;
    background-color: #121212;
}

.navTabs {
    font-size: 14px;
    background-color: rgb(18, 18, 18);
    border: 1px solid rgb(0,0,0);
    border: 1px solid rgba(0,0,0, .3);
    _border: 1px solid rgb(0,0,0);
    border-bottom-color: #121212;
    border-radius: 0;
    background-clip: content-box;
    box-shadow: inset 0 1px 0 rgba(255, 255, 255, 0);
    height: 36px;
}

body {
    -webkit-text-size-adjust: 100%;
    -moz-text-size-adjust: 100%;
    -ms-text-size-adjust: 100%;
    text-size-adjust: 100%;
    font-size: 13px;
    font-family: 'PT Sans', Arial, sans-serif;
    color: rgb(255, 255, 255);
    background-color: #121212;
    word-wrap: break-word;
    line-height: 1.28;
    min-width: 300px;
}

#navigation .navTabs, #userBar, .navTab.selected .tabLinks, .footer .pageContent, #uix_footer_columns {
    border-left: none;
    border-right: none;
    background-color: #383838;
}

.navTabs .navTab.PopupClosed .navLink {
    color: #fff;
}

.navTabs .navLink, .navTabs .SplitCtrl {
    font-weight: bold;
    color: #aaa;
    padding-right: 12px;
    padding-left: 12px;
    display: block;
    float: left;
    vertical-align: text-bottom;
    text-align: center;
    outline: 0 none;
    box-sizing: border-box;
    text-shadow: 0 0 0 transparent, 0 1px 0 #fff0;
    height: 36px;
    line-height: 36px;
}

.navTabs:hover .navLink:hover, .navTabs:hover .SplitCtrl:hover {
    font-weight: bold;
    color: #aaa;
    padding-right: 12px;
    padding-left: 12px;
    display: block;
    float: left;
    vertical-align: text-bottom;
    text-align: center;
    outline: 0 none;
    box-sizing: border-box;
    text-shadow: 0 0 0 transparent, 0 1px 0 #fff0;
    height: 36px;
    line-height: 36px;
}

.Menu {
    background-color: rgb(58, 58, 58);
    font-size: 13px;
    border: 1px solid #121212;
    border-top: 2px solid rgb(58, 58, 58);
    overflow: hidden;
    min-width: 200px;
    position: absolute;
    z-index: 7500;
    display: none;
}

.Menu .primaryContent {
    background-color: rgb(58, 58, 58);
    color: #fff;
}

.Menu .secondaryContent {
    background-color: rgb(59, 59, 59);
    color: #fff;
}

.blockLinksList a, .blockLinksList label {
    color: #fff;
    padding: 5px 10px;
    border-radius: 1px;
    display: block;
    outline: 0 none;
}

.navTabs .navTab.selected .tabLinks {
    border-bottom-color: #f15700;
    background-color: #212121;
}

#content .pageContent {
    background-color: #121212;
    padding: 16px;
    border: 1px solid #121212;
    border-radius: 0;
}

.breadcrumb {
    font-size: 13px;
    color: rgb(255, 255, 255);
    background-color: #1d1d1d;
    border: 1px solid #1e1e1e;
    border-radius: 0;
    overflow: hidden;
    zoom: 1;
    max-width: 100%;
    box-sizing: border-box;
    height: 40px;
}

.nodeList .categoryStrip .nodeTitle {
    font-weight: bold;
    font-size: 14px;
    color: #FFF;
    background-color: rgb(37, 37, 37);
    padding-right: 8px;
    padding-left: 8px;
}

.nodeList .categoryStrip .nodeTitle {
    font-weight: bold;
    font-size: 14px;
    color: #FFF;
    background-color: rgb(37, 37, 37);
    padding-right: 8px;
    padding-left: 8px;
}


.nodeList {
    zoom: 1;
    background-color: #000;
}

ol ul, ul ol, ul ul, ol ol {
    -webkit-margin-before: 0px;
    -webkit-margin-after: 0px;
    background-color: #121212;
}

.nodeList .node.level_2 .nodeInfo {
    background: #121212;
}

.nodeList .categoryForumNodeInfo, .nodeList .forumNodeInfo, .nodeList .pageNodeInfo, .nodeList .linkNodeInfo {
    background-color: transparent;
    box-shadow: inset 0 -1px 0 #121212;
    border-bottom: solid 1px #6b6b6b;
}

.node .nodeLastPost {
    border-left: solid 2px #f05a00;
    background-color: rgb(62, 62, 62);
    box-shadow: inset 0 -1px 0 #ffffff00;
}

.node.level_2:nth-child(odd) .nodeInfo .nodeLastPost {
    background-color: rgb(56, 56, 56);
}

.hasTabLinks #navigation .navTabs .navTab.selected .navLink {
    border-bottom-color: #212121;
    background-color: #212121;
    border: 0px;
    color: #fff;
}


.sidebar .section .secondaryContent {
    border: 1px solid #121212;
    border-radius: 0;
    background-color: #121212;
}

#SignupButton:hover .inner {
    background-color: #ff7100;
}

.sidebar .section .primaryContent h3, .sidebar .section .secondaryContent h3, .profilePage .mast .section.infoBlock h3 {
    font-weight: bold;
    font-size: 13px;
    font-family: 'Open Sans', Arial, sans-serif;
    color: rgb(255, 255, 255);
    background-color: rgb(27, 27, 27);
    padding: 8px;
    margin-bottom: 8px;
    border: 1px solid #1c1c1c;
    border-radius: 1px;
}

.textCtrl {
    font-size: 13px;
    font-family: 'Open Sans', Arial, sans-serif;
    color: #404040;
    background-color: #272727;
    padding: 5px 3px;
    margin-bottom: 2px;
    border: 1px solid #444444;
    border-radius: 1px;
    outline: 0;
    box-sizing: border-box;
    min-height: 30px;
}

html {
    color: #fff;
    background: #121212;
}

.sectionMain {
    background-color: #121212;
    padding: 8px;
    margin: 16px auto;
    border: 1px solid #121212;
    border-radius: 0;
}

.sectionFooter {
    overflow: hidden;
    zoom: 1;
    font-size: 13px;
    color: rgb(255, 255, 255);
    background-color: rgb(18, 18, 18);
    padding: 8px;
    border-bottom: 1px solid #121212;
    line-height: 16px;
}

#peForumStats .value {
    font-weight: bold;
    font-size: 10px;
    color: #fff;
    background-color: #1d1d1d;
    padding: 0 5px;
    margin-right: 20px;
    margin-left: 0;
    border: 1px solid rgb(30, 30, 30);
    border-radius: 2px;
    display: inline-block;
}

a:link, a:visited {
    color: #fff;
    text-decoration: none;
    transition: color 0.2s;
}

a:hover {
    color: #c7c7c7;
    text-decoration: none;
}

.node.level_2:nth-child(odd) .nodeInfo .nodeLastPost {
    background-color: rgb(39, 39, 39);
}

.node .nodeLastPost {
    border-left: solid 2px #f25700;
    background-color: rgb(39, 39, 39);
    box-shadow: inset 0 -1px 0 #272727;
}

.node .unread .nodeText .nodeTitle {
    font-weight: bold;
    color: rgb(255, 255, 255);
}

.pairs dt, .pairsInline dt, .pairsRows dt, .pairsColumns dt, .pairsJustified dt {
    color: rgb(189, 189, 189);
}

.node .unread .nodeIcon.hasGlyph {
    color: rgb(255, 255, 255);
}

.node .nodeText .nodeTitle {
    font-weight: bold;
    font-size: 14px;
    font-family: 'Open Sans', Arial, sans-serif;
    color: rgb(255, 255, 255);
}

body .muted, body a.muted, body .muted a {
    color: rgb(190, 190, 190);
}

#QuickSearchQuery {
    min-height: 0;
    background-color: #313131;
    line-height: normal;
    box-sizing: border-box;
    text-indent: 6px;
    width: 250px;
    height: 30px;
}

#QuickSearch .formPopup .secondaryControls {
    box-shadow: 0 0 5px rgba(0, 0, 0, 0);
    padding: 10px;
    border-radius: 1px;
    background: #353535;
    color: rgb(255, 255, 255);
    position: absolute;
    top: 40px;
    z-index: 7500;
}

.discussionList .discussionListItem.sticky {
    background-color: rgb(18, 18, 18);
    border-color: rgb(18, 18, 18);
}

.discussionListItem {
    padding: 8px;
    border-bottom: 1px solid #121212;
    box-sizing: border-box;
    background-color: #121212;
}

.discussionListItem .posterAvatar, .discussionListItem .stats {
    background-color: #ffffff00;
}

#DiscussionListOptionsHandle a {
    font-size: 12px;
    color: rgb(187, 187, 187);
    background-color: #2f2f2f;
    padding: 0 16px;
    margin: 0 auto;
    border: 1px solid #303030;
    border-top: 1px none black;
    border-bottom-right-radius: 1px;
    border-bottom-left-radius: 1px;
    display: inline-block;
    outline: 0;
    position: relative;
    right: -50%;
}

.DiscussionListOptions {
    margin: 8px auto;
    border: 1px solid rgb(18, 18, 18);
    border-radius: 1px;
    line-height: 30px;
    overflow: hidden;
    zoom: 1;
    background-color: #121212;
}

.DiscussionListOptions {
    margin: 8px auto;
    border: 1px solid rgb(18, 18, 18);
    border-radius: 1px;
    line-height: 30px;
    overflow: hidden;
    zoom: 1;
    background-color: #121212;
}

.messageUserBlock .extraUserInfo {
    font-size: 12px;
    background-color: #292929;
    padding: 4px 6px;
    border: 0 solid #292929;
    border-top-width: 1px;
    border-radius: 1px;
    border-top-left-radius: 0;
    border-top-right-radius: 0;
}

.messageUserBlock h3.userText {
    background-color: rgb(41, 41, 41);
    padding: 8px;
    border: 0 solid #292929;
    border-top-width: 1px;
}

.messageUserBlock div.avatarHolder {
    background-color: #292929;
    padding: 8px;
    border-radius: 1px;
    border-bottom-right-radius: 0;
    border-bottom-left-radius: 0;
    text-align: center;
    position: relative;
}

.messageUserBlock .arrow span {
    position: absolute;
    top: -10px;
    left: -11px;
    display: block;
    width: 0px;
    height: 0px;
    line-height: 0px;
    border: 10px solid transparent;
    border-left-color: #292929;
    -moz-border-left-colors: #FFFFFF;
    border-right: none;
}

element.style {
    position: absolute;
    top: 0px;
    left: 0px;
    z-index: 9998;
    background-color: rgb(8, 8, 8);
    opacity: 0.6;
    display: block;
    width: 1368px;
    height: 1854px;
}

.resourceListMain {
    background-color: #121212;
    padding: 16px;
    border: 1px solid #dbdbdb;
    border-radius: 0;
    border-radius: 0;
}

.resourceListItem .listBlockInner {
    padding: 10px;
    background-color: #121212;
}

.resourceListItem .resourceImage, .resourceListItem .resourceStats {
    background-color: #121212;
}

.resourceListSidebar .secondaryContent {
    border: 1px solid #dbdbdb;
    border-radius: 0;
    margin-bottom: 8px;
    background-color: #121212;
}

.primaryContent {
    background-color: #121212;
}

.Notices .panel .noticeContent {
    padding-right: 25px;
    background-color: #121212;
    color: #fff;
}

.staffTitle {
    font-size: 13px;
    background: #000000 
    url(https://ggradio.net/community/styles/xenblock/xenforo/gradients/form-button-white-25px.png) repeat-x top;
    padding: 8px 10px 2px 10px;
    border-bottom: 1px solid rgb(235, 235, 235);
    word-wrap: normal;
    display: table;
    width: 100%;
    box-sizing: border-box;
}

.profilePage .tabs.mainTabs {
    padding: 0 20px;
    margin-top: -23px;
    min-height: 23px;
    height: auto;
    position: relative;
    background: #121212;
}

.xenOverlay .xenForm#pageLogin {
    max-width: 400px;
    margin: 0 auto;
    padding: 40px;
    background-color: #121212;
    color: #fff;
}

.xenOverlay .formOverlay .textHeading {
    color: rgb(255, 255, 255);
}

.exposeMask{
    background-color: rgb(33,33,33);
    top: 0px;
    left: 0px;
    z-index: 9998;
    opacity: 0.8;
    display: block;
    width: 100%;
    height: 100%;
}

}
