css:
@-moz-document domain("pawoo.net") {
/*by shioko from cmx.im*/
/*请与草莓象原主题一起使用*/

:root {
    --bpg: rgb(250,153,134);
    --btt: rgb(235,230,232);
    --btt2: rgb(247,252,247);
    --btt3: rgb(236,222,210);
    --btxt: rgb(170,48,41);
    --bhd: rgb(249,235,221);
    --blb: rgb(239,199,179);
    --ctxt: rgb(123,29,29);
    --curl: rgb(255,125,117);
    --cui: rgb(225,130,125);
    --cui2: rgb(192,110,107);
    --cbut: rgb(255,80,80);
}

/*折叠TL*/
.pawoo-navigation-column .pawoo-navigation-column__subcolumns .pawoo-navigation-column__subcolumns__button>* {
	background: var(--blb); 
	border-bottom-left-radius: 10px;
	border-bottom-right-radius: 10px;
	color: var(--btt2);
}
.pawoo-navigation-column .pawoo-navigation-column__subcolumns .pawoo-navigation-column__subcolumns__button>:hover {
	background:var(--btt2);
	color: var(--blb)
}

/*搜索结果*/
.search-results__section h5 span {
	background: var(--blb);
	color:var(--ctxt);
	border-radius: 10px;
	box-shadow: 0 8px 20px -8px var(--cui) inset;
}
.search-results__hashtag {
	color:var(--cui);
}
.search-results__hashtag:hover {
	color:var(--curl);
}
.search-results__header {
	border-bottom:0;
}
.search-results__section h5:before {
	border-top:2px dashed var(--cui);
}

/*trends*/
.trend-tags {
	background:var(--btt2);
	border-radius: 10px;
}
.pawoo-subcolumn__header {
	background:var(--bhd);
	color: var(--cbut);
	border-radius: 10px 10px 0 0;
	border-bottom: 1px solid var(--cui);
	box-shadow: 0 8px 20px -8px var(--cui);
}
.suggestion-tags__name * {
	color:var(--cbut);
}
.suggestion-tags__name :hover {
	color:var(--curl);
}
.suggestion-tags__description {
	color:var(--ctxt)
}
.suggestion-tags__button {
	color: var(--btt2);
	background: var(--curl);
}
.suggestion-tags__button:focus,.suggestion-tags__button:hover {
	background: var(--cbut);
	color: var(--btt2);
	text-shadow: 0 0 5px #fff;
}
.suggestion-tags__body li {
	border-bottom:1px solid var(--cui);
}

/*正文*/
.status__time, .status__content a .fa {
	color: var(--curl);
}
.status__content a.hashtag, .status__content a span.ellipsis {
	color: var(--curl);
}

/*推荐用户*/
.pawoo-suggested-accounts-column--page .column-back-button {
	background: var(--bpg);
	box-shadow: 0 0 0 0 transparent;
	text-shadow: 0 0 25px #fff;
}
.pawoo-suggested-accounts-page .column-header * {
	background: var(--bhd);
}
.pawoo-suggested-accounts>.scrollable .item-list .account {
	border: 0 dashed var(--cui);
	border-left-width: 1px;
	border-top-width: 1px;
}

/*NSFW提示*/
.compose-form div div{
	color:#fff;
}

/*置顶背景*/
.status__wrapper[data-featured=true] {
	background: var(--btt);
}
}
