css:
@-moz-document domain(escritorioonline.jusbrasil.com.br) {
.NavSidebar {
    width: 56px;
    background-color: #FAFAFA;
}
.NavSidebar-item-icon {
    color: rgba(0, 0, 0, .26);
}
.OverviewTips-step-icon {
    background-color: #FAFAFA;
}
.EmptyView-icon {
    background-color: #FAFAFA;
}
.InlineFullView-content--gray {
    background-color: #FAFAFA;
}
.TimelineList-separator {
    color: rgba(0, 0, 0, .54);
    font-size:14px;
}
.TimelineItem--bordered {
    box-shadow: 0 1px 4px 0 rgba(0,0,0,0.14);
}
.icon--orange {
    color: rgba(0, 0, 0, .54);
}
.SideViewSection-head .icon--orange {
    color: rgba(0, 0, 0, .54);
}
.icon--blue {
    color: rgba(0, 0, 0, .54);
}
.SideViewSection-head .icon--blue {
    color: rgba(0, 0, 0, .54);
}
.icon--green {
    color: rgba(0, 0, 0, .54);
}
.SideViewSection-head .icon--green {
    color: rgba(0, 0, 0, .54);
}
.icon--yellow {
    color: rgba(0, 0, 0, .54);
}
.icon-green {
    color: rgba(0, 0, 0, .54);
}
.icon-yellow {
    color: rgba(0, 0, 0, .54);
}
.icon-blue {
    color: rgba(0, 0, 0, .54);
}
.icon-bluegray {
    color: rgba(0, 0, 0, .54);
}
.icon-orange {
    color: rgba(0, 0, 0, .54);
}
.icon-red {
    color: rgba(0, 0, 0, .54);
}
.SideViewSection-head .icon--yellow {
    color: rgba(0, 0, 0, .54);
}
.SingleComponent {
    width: 100vw;
    background-color: #FAFAFA;
}
.CardsFeed {
    background-color: #FAFAFA;
}
.DashboardFilterDesktop-header {
    color: rgba(0, 0, 0, .54);
}
.DashboardWidget {
    box-shadow: 0 1px 4px 0 rgba(0,0,0,0.14);
    width: 100%;
    height: 100%;
}
.DashboardWrapper {
    background-color: #f5f5f5;
}
::-webkit-scrollbar-thumb {
    background-color: #E0E0E0;
}
.NavSidebar {
    box-shadow: 0 1px 4px 0 rgba(0,0,0,0.14);
}
.NavSidebar-header {
    overflow: hidden;
    background-color: rgba(0, 0, 0, .0);
}
.NavSidebar-item-separator {
    margin: 12px 0;
    border-top: 1px solid rgba(0, 0, 0, 0.12);
}
body {
    font-size: 13px;
}
.SideFilter-group-list-item--selected .checkbox label, .SideFilter-group-list-item--selected>.SideFilter-group-list-item-link, .SideFilter-group-tree-list-item--selected>.SideFilter-group-list-item-link {
    font-size: 13px;
}
.PageHeader {
    background-color: #FFF;
}
.PageHeader-text-title {
    font-size: 20px;
    font-weight: 500;
    text-transform: uppercase;
    color: rgba(0, 0, 0, .80);
}
.OverviewNav--fixed .OverviewNav-nav {
    background: #FAFAFA;
}
.NavSidebar-item:hover {
    outline: none;
    background-color: #B9E1F9;
}
.JB-2 .topbar-center, .JB-2 .topbar-desktop, .JB-2 .topbar-mobile {
    box-shadow: 0 1px 8px rgba(0,0,0,.3);
}
.TimelineItem--bordered {
    box-shadow: 0 1px 4px 0 rgba(0,0,0,0.14);
}
.card, .modal-content, .popover, .navbar-submenu, .pagination, .toggle-box {
    box-shadow: 0 1px 4px 0 rgba(0,0,0,0.14);
}
}
