css:
@-moz-document domain("funimation.com"), domain("funimationfilms.com") {
html,
body,
header, 
.panel, 
.panel-group .panel.panel-default > .panel-heading > .panel-title,
#search-bar,
.schedule-list .date,
.graphic-schedule-list .date,
.content-template.content-series2 .gradient-bg, 
.content-template.content-series2.gradient-bg,
.funimation-popover,
.content .entry,
.site-container,
.comment-respond, 
.entry-comments, .entry-pings,
li.comment,
.navbar-default,
.funimation-includes header,
[style="background-color:#f2f2f2;"],
.td-main-content-wrap, 
.td-category-grid,
.tdi_47_f17 .tdb-head-search-btn i,
.tdi_47_f17 .tdb-drop-down-search .tdb-drop-down-search-inner,
.td_block_template_2 .td-block-title > *,
.tdb_header_menu .tdb-menu .sub-menu,
#funimation-popover-settings.funimation-popover ul.flex-row-custom li.flex-column-custom > ul.sidebar-sub-menu {
    background: #333 !important;
    color: #fefefe;
}

.skinny.open {
    border-bottom: 1px solid #fefefe;
}

a, 
header #main-navigation a,
.show-listing-section .show-listing-slider .show a .content,
#mainModuleContainer .container .headline h1 a, 
.quad-slider .name,
.new-releases .new-releases-slide-date,
.interior-custom-page p, 
.interior-custom-page li,
.content-template.content-series2 div.showOverview #also-liked-section .module-title-block .moduleTitle, 
.content-template.content-series2 div.showOverview #cast-crew-section .module-title-block .moduleTitle, 
.content-template.content-series2 div.showOverview #copyrightSection .module-title-block .moduleTitle, 
.content-template.content-series2 div.showOverview #reviews-section .module-title-block .moduleTitle, 
.content-template.content-series2 div.showOverview #show-about .module-title-block .moduleTitle, 
.content-template.content-series2 div.showOverview #special-features-sections .module-title-block .moduleTitle, 
.content-template.content-series2 div.showOverview #video-latest .module-title-block .moduleTitle,
.episodeListInfo .name,
.curated-section .curated-container .bx-wrapper .bx-viewport .curated-slider .show a .content,
.product-list .name,
header .header-icons ul.list-inline li .funimationAccountOptions.open a,
.tdb-head-search-btn i {
    color: #fefefe !important
}

footer {
    filter: saturate(0.2);
}

#desktop-logo, 
#mobile-logo,
header li:not(.fun) img,
#search-icon,
#queue-icon {
    filter: contrast(0);
}

header #trigger span,
.content-template.content-series2 .navbar-default .navbar-nav > .active,
body.show-details .navbar-default .navbar-nav > .active,
.dropdown-menu,
.navbar-default .navbar-nav > .active > a, 
.navbar-default .navbar-nav > .active > a:hover, 
.navbar-default .navbar-nav > .active > a:focus,
.pagination > .disabled > span, 
.pagination > .disabled > span:hover, 
.pagination > .disabled > span:focus, 
.pagination > .disabled > a, 
.pagination > .disabled > a:hover, 
.pagination > .disabled > a:focus{
    background: #808080;
}


#mainModuleContainer .container .headline h1 a:hover, 
#mainModuleContainer .container .headline h1 a:focus, 
.show-listing-section .show-listing-slider .show a:hover .content, 
.product-list a:hover .name, 
.product-list a:hover .description, 
.quad-container a:hover .name, 
.quad-container a:hover .description {
    color: #e30046;
}

.funimation-popover ul li a.arrow-sub-menu-toggle {
    filter: invert(1);
}

img[src$="purple.png"] {
	filter: grayscale(100%) contrast(0.6) invert(1);
}

.account-queue-drag img {
    filter: grayscale(100%) contrast(1) invert(1);
}


.list-inline > .cart,
#cart-icon {
    display: none;
}

.video-player-section, 
.video-player-section > .container {
    width: 100%;
}

.video-player-container {
    height: 800px !important;
    width: 100%;
}

#player {
    height: 805px;
    width: 100%;
    margin: 0 auto !important;
}

#funimation-player video {
    width: 95%;
    margin: 0 auto;
}

.video-js .vjs-progress-control {
    height: 8px !important;
}

.vjs-fullscreen video {
    width: 100%;
}

.product-list .show-wrapper .show-container::before,
.history-queue-async .queue-quad .view-all-queue a,
.navbar-default .navbar-collapse, 
.navbar-default .navbar-form,
.category > ul > li .tag-list .tag {
    background: #454545;
}

.pagination > li > a, 
.pagination > li > span,
.sidebar .widget,
input, 
select, 
textarea,
input:focus, 
textarea:focus,
.content-template.content-series2 #fullEpisodesFilters.affix,
li.list .list-dropdown {
    background-color: #454545;
    color: #fefefe
}

.pagination > li > a > span {
    color: #fefefe;
}

.pagination > li > a:hover {
    color: #fefefe;
    font-weight: 400;
}

.pagination > li.active a, 
.pagination > li.active a:hover {
    background: #404040;
    color: #e30046;
}

.list-dropdown,
.pageNotFound {
    color: #000;
}

#episode-list .no-slides,
.data-loader,
.nav-tabs > li.active > a, 
.nav-tabs > li.active > a:hover, 
.nav-tabs > li.active > a:focus,
.graphic-table-switcher-btn,
.table-striped > tbody > tr:nth-child(2n+1) > td, 
.table-striped > tbody > tr:nth-child(2n+1) > th,
header .header-icons ul.list-inline li .funimationAccountOptions,
.form-control[disabled], 
.form-control[readonly], 
fieldset[disabled] .form-control,
body.show-details #fullEpisodesFilters.affix,
.tdb_header_search .tdb-search-form-inner,
.tdb_header_search .tdb-search-form::before {
    background-color: #222;
    color: #fefefe;
}

.pagination > li > a:hover, 
.pagination > li > a:focus, 
.pagination > li > span:hover, 
.pagination > li > span:focus,
.table-schedule-list .table.table-schedule > thead > tr > th,
.table-hover > tbody > tr:hover > td, 
.table-hover > tbody > tr:hover > th,
.account-details .account-queue-list ul li, 
.account-details .account-history-list ul li, 
.account-details .account-following-list ul li {
    background: #404040;
}


#funimation-thumbnail {
    background-color: #454545;
    border: 1px solid #808080;
}

.showOverview .featured-product-section,
.vc_row_inner .td-element-style {
    background: #282828 !important;
}

li.comment {
    border: 0;
}

.nav-tabs > li.active > a,
.account-details .nav-tabs > li.active > a{
    padding: 5px 10px 0;
}

.mini-loader-wrap {
    background-color: rgba(0,0,0,.55);
}

.mini-loader-wrap.large .mini-loader {
    position: fixed;
    top: 50%;
    left: 50%;
    transform: translate(-50%);
}
}
