css:
@-moz-document domain("stardewvalleywiki.com") {
#p-logo {
    left: 13px;
    margin-top: 5px;
}
body {
    background: #0A0523 url(https://i.imgur.com/72TBoeL.png) no-repeat fixed center center / cover;
}
.mediawiki {
    background-color: #343434;
}
.responsiveadtop {
    background-color: transparent !important;
}
#stardew_bottom_responsive {
    background-color: rgb(78, 86, 80) !important;
}
.mw-body {
    border: 1px solid rgb(209, 255, 221);
    border: 1px solid rgba(209, 255, 221, 0.17);
    background-color: rgb(43, 56, 46);
    background-color: rgba(43, 56, 46, 0.92);
    color: #d0d0d0;
}
h1, h2, h3, h4, h5, h6 {
    color: #d0d0d0;
}
a {
    color: #3cff82;
}
a:visited {
    color: #3daf66;
}
#toc, .toc {
    background-color: rgb(136, 136, 136);
    background-color: rgba(136, 136, 136, 0.58);
}
div#mw-panel {
    border: 1px solid rgb(209, 255, 221);
    border: 1px solid rgba(209, 255, 221, 0.17);
    border-radius: 0;
    background-color: rgb(43, 56, 46);
    background-color: rgba(43, 56, 46, 0.92);
}
div#mw-panel div.portal div.body ul li a {
    color: #2fd26a;
}
div#mw-panel div.portal div.body ul li a:visited {
    color: #3daf66;
}
.mainmenuwrapper {
    background-color: #52615b;
}
table.wikitable > tr > th, table.wikitable > tr > td, table.wikitable > * > tr > th, table.wikitable > * > tr > td {
    border: 1px solid #339054;
    background-color: #52615b;
}
.mw-body a.external {
    color: #3daf66;
}
.mw-body a.external:visited {
    color: #3cff82;
}
.mw-body a.extiw, .mw-body a.extiw:active {
    color: #3daf66;
}
.mw-body a.extiw:visited {
    color: #3cff82;
}
table.wikitable {
    color: #d0d0d0;
}
#menuborder {
    border: none;
}
table.menulocations {
    border: 1px solid #3daf66;
    border-radius: 0;
}
div.vectorTabs li a {
    color: #2fd26a;
}
div.vectorTabs li.selected a, div.vectorTabs li.selected a:visited {
    color: #3daf66;
}
#p-personal a, #p-personal a.new, p-personal a:visited {
    color: #3daf66;
}
div#mw-panel div.portal {
    background-image: none;
    border-bottom: 1px solid gray;
}
#infoboxborder {
    border: 12px solid transparent;
    background-color: rgba(255, 255, 255, 0.58);
}
#infoboxborder {
    border: 12px solid transparent;
    background-color: #52615b;
}
#infoboxborder {
    border: 1px solid #3daf66;
    border-radius: 0;
    background-color: #52615b;
}
#roundedborder {
    border: 1px solid #3daf66;
    border-radius: 0;
}
.catlinks {
    border: 1px solid #3daf66;
    border-radius: 0;
    background-color: #52615b;
}
#navbox {
    border: 1px solid #3daf66;
    border-radius: 0;
    background-color: #52615b;
}
table.wikitable > tr > th, table.wikitable > * > tr > th {
    color: #d6d6d6;
}
ul {
    list-style-image: none;
}
div.vectorTabs li.new a {
    color: #2fd26a;
}
div#footer a {
    color: #c6d6b1;
}
#infoboxsection {
    background-color: #506d5a;
}
div.thumbinner {
    border: 1px solid #84d294;
    background-color: rgba(98, 115, 98, 0.65);
}
div#mw-panel div.portal h3 {
    color: #d0d0d0;
}
}
