css:
@-moz-document url-prefix("https://chocolatey.org/packages") {
	body {
		background-color: #333333;
		background-image: none;
	}
	#mainColumn,
	.package-page {
		position: relative;
		display: flex;
		flex-flow: column nowrap;
	}
	#content-wrapper {
		background-color: #eeeeee;
		width: 60%;
		box-shadow: 0 0 9px rgba(0, 0, 0, .3);
	}
	#content-wrapper > * {
		padding-left: 30px;
		padding-right: 30px;
		margin-top: 0;
	}
	#content-wrapper > #body {
		padding-bottom: 32px;
	}
	#layout-footer.group {
		margin-top: 0;
	}
	#searchResults {
		margin-bottom: 32px;
	}
	header.main {
		background-color: #a88a57;
		padding-bottom: 15px;
	}
	#sideColumn {
		width: 27%;
		padding-top: 45px;
		margin-right: -100%;
	}
	.hgroup {
		order: -2
	}
	.nuget-badge {
		order: -1;
	}
	.nuget-badge span,
	.nuget-badge-small span {
		-webkit-user-select: none;
		/* Chrome/Safari */
		-moz-user-select: none;
		/* Firefox */
		-ms-user-select: none;
		/* IE10+ */
		/* Rules below not implemented in browsers yet */
		-o-user-select: none;
		user-select: none;
	}
	nav.main {
		background-color: #333333;
		box-shadow: 0 0 9px rgba(0, 0, 0, .3);
	}
	nav.main ul {
		width: 100%;
		display: block;
	}
	.main #searchBox {
		position: relative;
		width: 100%;
		height: auto;
		margin: 15px auto;
		background-color: white;
		box-shadow: 0 0 0 1px #cccccc, 0 0 3px rgba(0, 0, 0, .3);
		border-radius: 3px;
	}
	.main #searchBoxInput,
	.main #searchBoxInput:focus {
		width: calc(100% - 60px);
		border-right: 1px solid #cccccc;
	}
	section.package header {
		margin-bottom: 15px;
	}
	.package .main p {
		font-size: 16px;
		line-height: 1.6;
		width: 80%;
		margin-bottom: 15px;
	}
}
