css:
@-moz-document url-prefix("https://v2.overleaf.com") {

#history aside.change-list {
    border-left: 1px solid #cfcfcf;
}

* {
    text-rendering: optimizeLegibility;
}

*, .glyphicon {
    -moz-osx-font-smoothing: grayscale;
    -webkit-font-smoothing: antialiased;
}

*, :after, :before {
    box-sizing: border-box;
}

.file-tree ul.file-tree-list {
    font-size: .8rem;
}

.file-tree ul.file-tree-list li {
    line-height: 2.6;
}

.file-tree ul.file-tree-list li .entity-name {
    color: #252525;
}

.file-tree:not(.multi-selected) ul.file-tree-list li.selected>.entity>.entity-name {
    background-color: white;
    color: #a93529;
}


.file-tree:not(.multi-selected) ul.file-tree-list li.selected>.entity>.entity-name i.fa, .file-tree:not(.multi-selected) ul.file-tree-list li.selected>.entity>.entity-name i.fa-folder-open {
    color: #a93529;
}

.toolbar .toolbar-right .btn-full-height {
    border-left: 1px solid #cfcfcf;
    border-right: 0;
}

.toolbar .toolbar-right .btn-full-height:hover {
    background-color: #e6e6e6;
}

.toolbar .btn-full-height {
    border-right: 1px solid #cfcfcf;
    border-left: 0;
}

.toolbar .btn-full-height:hover {
	background-color: #e6e6e6;
}
    
.toolbar.toolbar-alt {
    background-color: #fafafa;
}

a.btn.btn-default.btn-xs.synctex-control.synctex-control-goto-code {
    background-color: #777;
}

a.btn.btn-default.btn-xs.synctex-control.synctex-control-goto-pdf {
    background-color: #777;
}

.btn-full-height {
    border-left: 0px;
    border-right: 1px solid #cfcfcf;
}

.btn-full-height.active {
    background-color: #e6e6e6;
}

.toolbar .btn-full-height.active, .toolbar .btn-full-height:active {
    background-color: #e6e6e6;
}

.btn-full-height:hover {
    background-color: #e6e6e6;
}

.btn-recompile-group .btn-recompile {
    background-color: #405ebf;
    border: 1px solid #2d4286;
}

.btn-recompile-group .btn-recompile.active, .btn-recompile-group .btn-recompile.checked, .btn-recompile-group .btn-recompile:active, .btn-recompile-group .btn-recompile:focus, .btn-recompile-group .btn-recompile:hover, .open .dropdown-toggle.btn-recompile-group .btn-recompile {
    background-color: #405ebf;
    border: 1px solid #2d4286;
}

.btn-recompile-group .btn-recompile.disabled, .btn-recompile-group .btn-recompile.disabled.active, .btn-recompile-group .btn-recompile.disabled:active, .btn-recompile-group .btn-recompile.disabled:focus, .btn-recompile-group .btn-recompile.disabled:hover, .btn-recompile-group .btn-recompile[disabled], .btn-recompile-group .btn-recompile[disabled].active, .btn-recompile-group .btn-recompile[disabled]:active, .btn-recompile-group .btn-recompile[disabled]:focus, .btn-recompile-group .btn-recompile[disabled]:hover, fieldset[disabled] .btn-recompile-group .btn-recompile, fieldset[disabled] .btn-recompile-group .btn-recompile.active, fieldset[disabled] .btn-recompile-group .btn-recompile:active, fieldset[disabled] .btn-recompile-group .btn-recompile:focus, fieldset[disabled] .btn-recompile-group .btn-recompile:hover {
	background-color: #7e96e5;
    border: 1px solid #5069b7;
}

.btn-info {
    background-color: #405ebf;
    border: 1px solid #2d4286;
}

.btn-info.active, .btn-info.checked, .btn-info:active, .btn-info:focus, .btn-info:hover, .open .dropdown-toggle.btn-info {
    color: #fff;
    background-color: #364fa0;
    border-color: #1d2b58;
}


a.custom-toggler {
    background-color: #777;
}

a.custom-toggler:hover {
    background-color: #a93529;
}

div.day.ng-binding {
    border-bottom: 1px solid #cfcfcf;
}

div.description {
    border-bottom: 1px solid #cfcfcf;
}

div.entity-name.ng-isolate-scope.ui-draggable.ui-draggable-handle {
    background-color: white;
    color: #252525;
}

div.entity-name.ng-isolate-scope.ui-draggable.ui-draggable-handle:hover {
    background-color: #f0f0f0;
}

div.pdfjs-viewer.ng-isolate-scope {
    background-color: #cfcfcf;
}

div.toolbar.toolbar-alt {
    border-bottom: 1px solid #cfcfcf;
}

div.toolbar.toolbar-filetree.ng-scope {
    background-color: #fafafa;
    border-bottom: 1px solid #cfcfcf;
}

div.toolbar.toolbar-pdf {
    background-color: white;
    border-bottom: 1px solid #cfcfcf;
}

div.ui-layout-resizer {
    background-color: #F4F4F4;
    border-left: 1px solid #cfcfcf;
    border-right: 1px solid #cfcfcf;
}

element.style {
}

header.toolbar.toolbar-header.toolbar-with-labels {
    background-color: #FFF;
    border-bottom: 1px solid #cfcfcf;
}

i.fa.fa-compress {
    color: #a4a4a4;
}

i.fa.fa-expand {
    color: #a4a4a4;
}

i.fa.fa-fw.fa-bars.editor-menu-icon {
    color: #a93529;
    filter: brightness(6%) sepia(100) saturate(100) hue-rotate(25deg);
}

i.fa.fa-fw.fa-comment {
    color: #a93529;
}

i.fa.fa-fw.fa-download {
    color: #a4a4a4;
}

i.fa.fa-fw.fa-file {
    color: #a4a4a4;
}

i.fa.fa-fw.fa-file-text-o {
    color: #a4a4a4;
}

i.fa.fa-fw.fa-folder {
    color: #a4a4a4;
}

i.fa.fa-fw.fa-group {
    color: #a93529;
}

i.fa.fa-fw.fa-history {
    color: #a93529;
}

i.fa.fa-fw.fa-level-up {
    color: #a4a4a4;
}

i.fa.fa-fw.fa-pencil {
    color: #a4a4a4;
}

i.fa.fa-fw.fa-trash-o {
    color: #a4a4a4;
}

i.fa.fa-fw.fa-upload {
    color: #a4a4a4;
}

i.review-icon {
    color: #a93529;
    filter: brightness(6%) sepia(100) saturate(100) hue-rotate(25deg);
}

li {
    background-color: white;
}

li :hover {
    background-color: #f0f0f0;
}

li.ng-scope.selected {
    color: #ddd;
}

p.toolbar-label {
    color: #a93529;
}

span.name.ng-binding {
    color: #505050;
}

.chat .messages {
	background-color: #FFF;
}

.chat .new-message {
	background-color: #f0f0f0;
    border-top: 1px solid #cfcfcf;
}

textarea.ng-pristine.ng-untouched.ng-valid.ng-empty{
    background-color: white;
    border: 1px solid #cfcfcf;
}

textarea.ng-valid.ng-not-empty.ng-dirty.ng-valid-parse.ng-touched {
    background-color: white;
    border: 1px solid #cfcfcf;
}

textarea.ng-valid.ng-dirty.ng-touched.ng-empty {
    background-color: white;
    border: 1px solid #cfcfcf;
}

ul.list-unstyled.file-tree-list.ui-droppable {
    background-color: white;
}

.pdf .toolbar.toolbar-pdf {
    background-color: #FFF;
    border-bottom: 1px solid #cfcfcf;
}

.file-tree ul.file-tree-list li i.fa {
    color: #a4a4a4;
}

.file-tree:not(.multi-selected) ul.file-tree-list li.selected>.entity>.entity-name .entity-menu-toggle i.fa, .file-tree:not(.multi-selected) ul.file-tree-list li.selected>.entity>.entity-name>div>i.fa, .file-tree:not(.multi-selected) ul.file-tree-list li.selected>.entity>.entity-name>i.fa {
    color: #a93529;
}

.chat .new-message textarea {
    overflow: auto;
    resize: none;
    border-radius: 3px;
    border: 1px solid #cfcfcf;
    height: 100%;
    width: 100%;
    color: #1E2530;
    font-size: 14px;
    padding: 6.25px;
    background-color: #FFFFFF;
}

.chat .messages li.message .message-wrapper .message .message-content {
    padding: 5px 10px;
    overflow-x: auto;
    color: #505050;
    font-weight: 400;
    border-color: rgb(125, 196, 232); 
    background-color: rgb(243, 249, 252);
}

.chat .messages li.message .message-wrapper .message {
    border-left: 3px solid transparent;
    font-size: 14px;
    box-shadow: -1px 2px 3px #ddd;
    border-radius: 0;
}
}
