css:
@-moz-document domain('player.fm') {

div.prime-channel.page.channel {
    background-color: #272a2d ;
    color: #535361;
}

div.title span a {
    color: #7D8DA9;
}

time.timeago {
    color: #909199;
}

span.label {
    color: #7d7e85;
}

span.icon {
    color: #ebebeb;
}

span.menu-launcher i {
    color: #6f6d85;
}

i.fa.fa-gear {
    color: #7b7c82;
}

i.fa.fa-info-circle {
    color: #7b7c82;
}

i.fa.fa-plus {
    color: #7b7c82;
}

span.publicness {
    color: #7b7c82;
}

select.control.records-order.series-order.sort {
    color: #7b7c82;
}

i.fa.fa-align-left.stylize.control {
    background-color: #ffffff;
}

div.container {
    color: #535666;
    background-color: #191e29;
    border-style: dotted;
    border-width: 0;
}

span.factoid.duration {
    color: #7c83a6;
}

a.action.info-right.normal.playable.round-button {
    color: #cccccc;
    background-color: #383840;
}

i.fa.fa-play.play-icon.icon {
    color: #7784ba;
}

div.top-expansion-wrapper {
    background-color: #5c5c5c;
}

a.action-prompt {
    color: #8a8c94;
}

div.action-prompt.pointer.add-channel {
    color: #9f9fa6;
}

div.top-main {
    background-color: #282e38;
    border-style: solid;
    border-color: #59697a;
    border-width: 1px;
    border-left-width: 0;
    border-right-width: 0;
}

div.col-md-12.mobile-padded {
    color: #99a9bd;
    background-color: #191e29;
}

select.control.records-order.episodes-order.sort {
    color: #85839e;
}

span.menu-launcher {
    color: #7a888f;
}

time.short-timeago.timeago {
    color: #a7b1b8;
}

div.content {
    color: #7b7c82;
    background-color: #383840;
}

div.page-body.container {
    background-color: #24282e;
}

div.menu {
    color: #636a73;
}

div.slimmed-page.slimmer-page.page.user-edit {
    color: #60626b;
    background-color: #2d3138;
}

div.page-inner {
    background-color: #505766;
}

div.row {
    background-color: #292d33;
}

input.form-control.string.optional {
    background-color: #707782;
}

input.form-control.password.optional {
    background-color: #3c4047;
}

section.password-section input {
    background-color: #383b42;
}

select.form-control.select.optional {
    background-color: #383b42;
    color: #383b42;
}

input.form-control.string.url.optional {
    background-color: #383b42;
    color: #383b42;
}

