    background-color: #383b42;
}

a.tag {
    color: #6a748a;
    background-color: #383b42;
    border-style: solid;
    border-color: #417896;
}

a.link.channel-title {
    color: #66768f;
}

span.control.launch-query {
    display: none;
}

a.play-prompt.playable.button {
    background-color: #697075;
}

share-button.button {
    background-color: #697075;
}

div.subscribe.button.primary-button {
    background-color: #374a57;
}

span.when-medium-or-big {
    color: #868d99;
}

div.empty-page-heading.page-heading {
    color: #405259;
}

div.col-md-3.series-summary {
    border-style: none;
}

div.series.page {
    border-style: none;
    background-color: #323b42;
}

div.shape {
    color: #627078;
    background-color: #14232e;
}

div.corner-indicator {
    background-color: #383840;
    color: #ffffff;
}

i.fa.fa-thumb-tack.rotate45 {
    color: #546a7d;
}
}
