css:
@-moz-document url-prefix("http://www.halovault.org/"), url-prefix("http://halovault.org/"), domain("www.halovault.org"), domain("halovault.org") {
div.banner {
    width: 100%;
    color: black;
    text-align: center;
    font-family: 'Halo', sans-serif;
    
    background: url(https://i.imgur.com/IkwgfzU.png) no-repeat center #000;
    background-size: cover;
    background-size: auto auto;
    height: 200px;
    background-color: #33343c;
}
* {
    -webkit-box-sizing: border-box;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
}
.contentHeader {
    font-family: 'Neuro', sans-serif;
    font-size: 52px;
    color: white;
    border-bottom: 1px solid #6f919a;
    display: block;
    max-width: 100%;
    margin-bottom: 20px;
}
div.sidebar {
    margin-bottom: 34px;
    margin-top: 34px;
    float: right;
    border: 0px solid;
    max-width: 25%;
    min-height: 100% !important;
    color: #dfdfdf;
    background-color: transparent;
    
    padding-bottom: 40px;
    padding: 17px;
}
div.sidebar h4 {
    font-family: 'Arial', sans-serif;
    background: rgba(68, 68, 68, 0.73);
    max-width: 100%;
    padding: 5px;
    text-align: center;
    color: white;
    font-size: medium;
}

div.innerSidebar {
    border-bottom: 0px solid black;
    border-top: 0px;
    float: top;
    background-color: rgba(74, 74, 74, 0.50);
    max-width: 100%;
    margin-top: -22px;
    min-height: 100%;
    margin-bottom: 40px;
    box-shadow: 0px 0px 5px #000000;
}
body {
    background-image: url(http://i.imgur.com/mkv8jwe.jpg);
}
div.wrapper {
    position: relative;
    width: 95%;
    min-height: 100%;
    float: center;
    border: 2px solid black;
    margin: 0 auto;
    background: rgba(21, 30, 32, 0.73);
    box-shadow: 0px 0px 50px #FFF;
    -webkit-box-shadow: 0px 0px 50px #222;
    -moz-box-shadow: 0px 0px 50px #222;
}
div.content {
    float: left;
    padding: 10px;
    max-width: 72%;
    min-width: 72%;
    display: block;
    font-size: 125%;
    color: white;
}
.content div.contentHolder {
    display: block;
    border: 1px solid #303030;
    border-top: 0px;
    padding: 16px;
    opacity: 1.0!important;
    font-family: Arial;
    padding-top: 20px;
    padding-bottom: 20px;
    margin-top: -13px;
    min-width: 100%;
    background: rgba(74, 74, 74, 0.45);
     -webkit-box-shadow: 0px 0px 0px #303030; 
    -moz-box-shadow: 0px 0px 0px #303030;
    overflow: hidden;

}
h3 {
    font-family: 'Neuro', sans-serif;
    font-size: x-large;
    color: #0074AF;
    width: 100%;
    max-width: 100%;
    display: block;
    padding: 6px;
    border-radius: 10px 10px 0px 0px;
    background-color: rgba(48, 48, 48, 0.57);
}
.content h4 {
    font-family: 'Neuro', sans-serif;
    font-size: large;
    color: white;
    max-width: 100%;
    display: block;
    padding: 5px;
    background-color: rgba(48, 48, 48, 0.57);
}
div.innerSidebar {
    border-bottom: 0px solid black;
    border-top: 0px;
    float: top;
    background-color: rgba(74, 74, 74, 0.50);
    max-width: 100%;
    margin-top: -22px;
    min-height: 100%;
    margin-bottom: 40px;
    box-shadow: 0px 0px 5px #000000;
}
.sidebar h4 a {
    color: white;
}
div.forgeList div.row:nth-child(odd) {
        background: rgba(60, 60, 60, 0.45);
}
.row {
    color: white;
}
table.recent tr:nth-child(odd) {
    background-color: rgba(86, 86, 86, 0.34);
}
table.recent {
    background-color: #73737326;
    color: #b9b9b9;
}
a.rLink {
    color: white;
}
div.sidebar h5 {
    background-color: rgba(107, 107, 107, 0.33);
    border: 1px solid #323232;
    color: #e0e0e0;
    border-bottom: 0px;
    display: block;
    max-width: 90%;
    text-align: center;
    margin: 0 auto;
    margin-bottom: 0px;
    size: 9pt;
}
div.footer {
    display: block;
    max-width: 100%;
    padding-left: 15px;
    padding-right: 15px;
    color: black;
    text-align: center;
    bottom: 0px;
    margin-top: 30px;
    background-color: #33343cc2;
}
.footlinks {
    display: inline-flex;
    align-items: center;
    min-width: 60px;
    background-color: #33343c;
    color: white;
    max-height: 20px;
    padding: 4px;
    border: 1px solid #232323;
}
div.comment {
    background-color: #6f6f6f4d;
    color: white;
    float: center;
    max-width: 90%;
    box-shadow: 0px 0px 10px #202020;
    -webkit-box-shadow: 0px 0px 10px #202020;
    -moz-box-shadow: 0px 0px 10px #202020;
    margin-bottom: 30px;
}
textarea {
    background-color: #5656567d;
    border: 1px solid black;
    display: block;
    width: 100%;
    -webkit-text-fill-color: #e2e2e2;
    color: white;
}
input[type="text"], input[type="button"], input[type="password"], input[type="submit"] {
    -webkit-box-shadow: 0 0 0px 1000px #5656567d inset;
    background-color: #5656567d;
    border: 1px solid black;
    line-height: 17px;
    -webkit-text-fill-color: #ffffff;
    color: white;
    max-width: 75%;
}
button {
    background-color: #56565675 !important;
    color: white !important;
    -webkit-text-fill-color: white;
}
.dlMap {
    background-color: #56565675;
    padding: 7px;
    border: 1px solid #a0a0a033;
    font-weight: bold;
    float: left;
    text-align: center;
}

element.style {
    border: 1px solid #5f5f5f;
    padding: 1px;
    float: right;
    font-size: small;
}
div.modList div.row:nth-child(odd) {
    background: #ababab1f;
}
.discordBtn {
    padding: 20px;
    text-height: 0px;
    background-image: url(https://i.imgur.com/3nQGkAi.png);
    background-size: 100% 100%;
    background-position: center center;
    background-repeat: no-repeat;
    cursor: pointer;
    border: 0px solid transparent;
    max-width: 55px;
    height: 55px;
    margin: 0 auto;
}
.steamBtn {
    padding-top: 20px;
    padding-bottom: 20px;
    margin: 0 auto;
    text-height: 0px;
    background-color: transparent;
    border: 0px solid transparent;
    background-image: url(http://steamcommunity-a.akamaihd.net/public/shared/images/header/globalheader_logo.png?);
    background-size: contain;
    background-position: center center;
    background-repeat: no-repeat;
    max-width: 180px;
    height: 30px;
    cursor: pointer;
    font-family: 'Halo';
    font-weight: bold;
    color: black;
    text-align: top;
    font-size: 12px;
}
.hecBtn {
    padding: 20px;
    margin: 0 auto;
    text-height: 0px;
    background-color: transparent;
    border: 0px solid transparent;
    background-image: url(http://hec.haloshare.org/core/img/HCEtrnsparent.png);
    background-size: contain;
    background-position: center center;
    background-repeat: no-repeat;
    max-width: 180px;
    height: 30px;
    cursor: pointer;
    font-family: 'Halo';
    font-weight: bold;
    color: transparent;
    text-align: top;
    font-size: 0px;
}
}
