 /* 
 * Name:    WorkFlowy Total Minimalist
 * Author:  Pedro Baesse   
 * Desc: The best layout to workflowy for me. A mix of three other styles listed below:
    * WorkFlowy Total Minimalist: https://userstyles.org/styles/132659/workflowy-total-minimalist
    * Workflowy Dark: https://userstyles.org/styles/143680/workflowy-dark
    * WorkFlowy : Zen Colour Labels: https://userstyles.org/styles/120974/workflowy-zen-colour-labels
 */


 /* 
 * Name:    WorkFlowy Total Minimalist
 * Author:  Malcolm Ocean
 * URL: https://userstyles.org/styles/132659/workflowy-total-minimalist
 */


  #documentView
  {
    font-family: "/*[[font]]*/", sans-serif;
    font-size: 12pt;
    font-weight: 400;
  }

  .page
  {
    margin: 0 !important;
    padding: 30px 90px 20px;
    width: 100% !important;
    max-width: 100%;
    box-sizing: border-box;
    transform: none !important;
  }

  .corner, .edge
  {
    display: none;
  }

  #pageContainer
  {
    margin-top: 0;
  }

  @media (max-width: 1200px)
  {
    .page{margin: 0 !important;
    padding: 30px 60px 20px;
  }
  }

  @media (max-width: 992px)
  {
    .page
    {
      margin: 0 !important;
      padding: 30px 50px 20px;
    }
  }

  @media (max-width: 768px)
  {
    .page
    {
      margin: 0 !important;
      padding: 30px 30px 20px;
    }
  }

  #getMoreSpaceButtonTopLeft, #bottomLinks, #proPitchBottomRight, .weekly-report__support .freeOnly
  {
    display: none;
  }

  #header
  {
    opacity: 0;
    transition: opacity 100ms ease-in-out;
  }

  #header:hover
  {
    opacity: 1;
  }

  .siteSlogan
  {
    font-size: 0;
  }

  .siteSlogan::after
  {
    font-size: 12px;
    content: "Smile, breathe, and go slowly.";
  }

/* 
 * Name:    Workflowly Dark
 * Author:  Froyok
 * URL: https://userstyles.org/styles/143680/workflowy-dark;
 */

  body
  {
    background-color: #121212;
    color: white;
  }

  .page
  {
    background-color: #212121;
    border: 1px solid #000;
  }

  #header
  {
    background: #3B3B3B;
  }

  .project.open.highlighted
  {
    background-color: #000;
  }

  .project.highlighted::before
  {
    background: #3D3D3D;
  }

  #documentView
  {
    color: #d8d8d8;
  }

  .children
