        font-size: 13pt;

        font-family:"Merriweather", Georgia, serif;

        font-weight: 300;

    }

    dt {

        font-family: Menlo, Monaco,

        /* MacOS */

        'Segoe UI Mono, Lucida Console',

        /* Windows */

        'Fira Mono',

        /* Firefox OS */

        'Roboto Mono', 'Noto Mono',

        /* Google stuff */

        'Droid Sans Mono',

        /* Old Google stuff */

        'Ubuntu Mono', 'DejaVu Sans Mono', 'Liberation Mono',

        /* Linux */

        'Courier New',

        /* MacOS and Windows */

        monospace;

        /* Everything else */

    }

    dt {

        font-weight: normal;

    }

    body .article_page :-webkit-any(h1, h2, h3, h4, h5, h6, pre, form, fieldset, blockquote, ul, ol, dl) {

        margin-top: 30px;

        margin-bottom: 15px;

        margin-left: 0px;

        margin-right: 0px;

    }

    p, dl {

        margin-top: 15px;

        margin-bottom: 15px;

        margin-left: 0;

        margin-right: 0;

    }

    h1 + h2, h2 + h3, h3 + h4, h4 + h5, h5 + h6 {

        margin-top: 15px;

