css:
@-moz-document url-prefix("https://github.com/") {
  .Header {
    background: linear-gradient(to right, #22276c, #586aa5, #a9c7f6);
  }
  .Header .header-search-wrapper {
    border-radius: 0;
    border-top: inset 2px #999;
    border-left: inset 2px #999;
    border-bottom: inset 2px #ddd;
    border-right: inset 2px #ddd;
    background-color: #fff;
    color: black !important;
  }
  .pagehead {
    background-color: #d4d2cd !important;
    border-bottom: none;
  }
  .social-count {
    background-color: #d4d2cd !important;
    border-radius: 0;
  }
  .reponav .selected {
    border-top: 2px solid #ddd;
    border-left: 2px solid #ddd;
    border-right: 2px solid #999;
    background-color: #d4d2cd !important;
    border-radius: 0;
    padding-top: 15px;
    margin-top: 5px;
  }
  .reponav .js-selected-navigation-item:not(.select):first-child {
    border-left: 2px solid #ddd;
  }
  .reponav .js-selected-navigation-item:not(.selected) {
    border-top: 2px solid #ddd;
    border-right: 2px solid #999;
    border-radius: 0;
    color: #000;
    margin-top: 15px;
  }
  .repohead {
    margin-bottom: 0px !important;
  }
  .mr-2.header-search-key-slash {
    display: none;
  }
  #user-links .dropdown ul.dropdown-menu {
    border-radius: 2px;
    border-bottom: 2px solid #999;
    border-right: 2px solid #999;
    border-left: 2px solid #ddd;
    border-top: 2px solid #ddd;
    background-color: #d4d2cd;
  }
  #user-links .dropdown .dropdown-item:hover {
    background-color: #32477a;
  }
  body {
    background-color: #d4d2cd;
  }
  .select-menu-button,
  .select-menu-button:hover {
    border-radius: 2px;
    border-bottom: 2px solid #999;
    border-right: 2px solid #999;
    border-left: 2px solid #ddd;
    border-top: 2px solid #ddd;
    background-image: none;
    background-color: #d4d2cd;
  }
  .select-menu-modal {
    border-radius: 2px;
    border-bottom: 2px solid #999;
    border-right: 2px solid #999;
    border-left: 2px solid #ddd;
    border-top: 2px solid #ddd;
    background-image: none;
    background-color: #d4d2cd;
  }
  .select-menu-modal .select-menu-header, .select-menu-modal .select-menu-divider {
    background: linear-gradient(to right, #22276c, #586aa5, #a9c7f6);
  }
  .select-menu-modal .select-menu-header span, .select-menu-modal .select-menu-divider span {
    color: white;
  }
  .select-menu-modal .select-menu-item {
    background: none;
  }
  .select-menu-modal .select-menu-item a {
    color: black;
  }
  .select-menu-modal .select-menu-item.navigation-focus {
    background-color: #32477a;
  }
  .repository-content .Box {
    border-radius: 2px;
    border-bottom: 2px inset #ddd;
    border-right: 2px inset #ddd;
    border-left: 2px inset #999;
    border-top: 2px inset #999;
  }
  .commit-tease {
    border-radius: 2px;
    border-bottom: 2px inset #ddd;
    border-right: 2px inset #ddd;
    border-left: 2px inset #999;
    border-top: 2px inset #999;
  }
  .blob-wrapper {
    background-color: white;
    border-radius: 2px;
    border-bottom: 2px inset #ddd;
    border-right: 2px inset #ddd;
    border-left: 2px inset #999;
    border-top: 2px inset #999;
  }
  .topic-tag {
    border-radius: 1px;
    border-bottom: 1px solid #999;
    border-right: 1px solid #999;
    border-left: 1px solid #ddd;
    border-top: 1px solid #ddd;
    border-top: 0;
    background-color: #f3f3f3;
  }
  .topic-tag:hover {
    background-color: #d4d2cd;
  }
  .UnderlineNav .UnderlineNav-body .UnderlineNav-item {
    border-radius: 2px;
    border-bottom: 2px solid #999;
    border-right: 2px solid #999;
    border-left: 2px solid #ddd;
    border-top: 2px solid #ddd;
    border-bottom: 0;
    margin-top: 4px;
    border-radius: 4px 4px 0 0;
    padding: 8px 4px;
  }
  .branch-name {
    background-color: #d4d2cd !important;
    border-radius: 0;
    border-top: 2px solid #ddd;
    border-left: 2px solid #ddd;
    border-bottom: 2px solid #999;
    border-right: 2px solid #999;
  }
  .branch-name:hover {
    text-decoration: none;
  }
  .branch-name:focus {
    border-top: 2px solid #999;
    border-left: 2px solid #999;
    border-bottom: 2px solid #ddd;
    border-right: 2px solid #ddd;
  }
  .Box {
    background-color: #d4d2cd !important;
  }
  .Box .Box-header {
    background-color: #d4d2cd !important;
    border-radius: 0;
    border: 0;
    margin: 0;
  }
  /*Dashboard*/
  .dashboard {
    /*Discover repositories Recommendation*/
  }
  .dashboard .border-bottom {
    border-bottom: 0 !important;
  }
  .dashboard .dashboard-sidebar {
    border-radius: 2px;
    border-bottom: 2px solid #999;
    border-right: 2px solid #999;
    border-left: 2px solid #ddd;
    border-top: 2px solid #ddd;
  }
  .dashboard .message {
    border-radius: 2px;
    border-bottom: 2px solid #999;
    border-right: 2px solid #999;
    border-left: 2px solid #ddd;
    border-top: 2px solid #ddd;
  }
  .dashboard .message .notice-dismiss {
    width: 24px;
    height: 24px;
    text-align: center;
    border-radius: 2px;
    border-bottom: 2px solid #999;
    border-right: 2px solid #999;
    border-left: 2px solid #ddd;
    border-top: 2px solid #ddd;
    background-image: none;
    background-color: #d4d2cd;
  }
  .dashboard .push {
    border: initial;
    border-radius: 2px;
    border-bottom: 2px solid #999;
    border-right: 2px solid #999;
    border-left: 2px solid #ddd;
    border-top: 2px solid #ddd;
    border-bottom: 2px solid #999 !important;
  }
  .dashboard .push .body {
    padding: 4px;
  }
  .dashboard .push .commits .message {
    border-radius: 0;
    border-bottom: 0 inset #ddd;
    border-right: 0 inset #ddd;
    border-left: 0 inset #999;
    border-top: 0 inset #999;
  }
  .dashboard .push .Box {
    padding: 4px;
    margin: 4px;
    border: initial;
    border-radius: 2px;
    border-bottom: 2px inset #ddd;
    border-right: 2px inset #ddd;
    border-left: 2px inset #999;
    border-top: 2px inset #999;
    border-bottom: inset 2px #ddd !important;
  }
  .dashboard .watch_started {
    padding: 4px;
    border-radius: 2px;
    border-bottom: 2px solid #999;
    border-right: 2px solid #999;
    border-left: 2px solid #ddd;
    border-top: 2px solid #ddd;
  }
  .dashboard .release {
    padding: 4px;
    border-radius: 2px;
    border-bottom: 2px solid #999;
    border-right: 2px solid #999;
    border-left: 2px solid #ddd;
    border-top: 2px solid #ddd;
  }
  .dashboard .Details .Box {
    padding: 4px;
    margin: 4px;
    border: initial;
    border-radius: 2px;
    border-bottom: 2px inset #ddd;
    border-right: 2px inset #ddd;
    border-left: 2px inset #999;
    border-top: 2px inset #999;
    border-bottom: inset 2px #ddd !important;
  }
  .dashboard .Details.Box {
    border-radius: 2px;
    border-bottom: 2px inset #ddd;
    border-right: 2px inset #ddd;
    border-left: 2px inset #999;
    border-top: 2px inset #999;
  }
  .dashboard .Details.Box .Box-body {
    background-color: #d4d2cd !important;
  }
  .dashboard .Details.Box .Box-header {
    margin: 0;
  }
  .dashboard .AvatarStack-body {
    background-color: #d4d2cd !important;
  }
  .dashboard .news .Details {
    border: 0;
  }
  .dashboard .js-notice form.js-notice-dismiss button.btn {
    border-radius: 2px;
    border-bottom: 2px solid #999;
    border-right: 2px solid #999;
    border-left: 2px solid #ddd;
    border-top: 2px solid #ddd;
    color: black;
    background-color: #d4d2cd;
  }
  .jump-to-field-active-non-empty {
    border: 0;
    border-radius: 0;
  }
  .form-control input {
    color: black !important;
  }
  .overall-summary.overall-summary-bottomless {
    border-radius: 0;
    border-top: inset 2px #999;
    border-left: inset 2px #999;
    border-bottom: inset 0px #ddd;
    border-right: inset 2px #ddd;
    background-color: #fff;
  }
  .repository-lang-stats-graph.js-toggle-lang-stats {
    border-radius: 0;
    border-top: inset 0px #999;
    border-left: inset 2px #999;
    border-bottom: inset 2px #ddd;
    border-right: inset 2px #ddd;
  }
  .repository-lang-stats-graph.js-toggle-lang-stats span {
    border-radius: 0 !important;
  }
  .commit-tease.js-details-container.Details.d-flex {
    background-color: #fff;
    border-radius: 0;
    border-top: inset 2px #999;
    border-left: inset 2px #999;
    border-bottom: inset 2px #ddd;
    border-right: inset 2px #ddd;
  }
  .file-wrap {
    margin-top: 12px;
    border: initial;
    border-top: 2px solid #ddd;
    border-left: 2px solid #ddd;
    border-bottom: 2px solid #999;
    border-right: 2px solid #999;
  }
  table.files {
    margin: 10px;
    width: 98%;
    border-radius: 0;
    border-collapse: inherit;
    border-top: inset 2px #999;
    border-left: inset 2px #999;
    border-bottom: inset 2px #ddd;
    border-right: inset 2px #ddd;
    background-color: #fff;
  }
  #readme {
    border-top: 2px solid #ddd;
    border-left: 2px solid #ddd;
    border-bottom: 2px solid #999;
    border-right: 2px solid #999;
  }
  #readme article {
    margin: 12px;
    border-top: inset 2px #999;
    border-left: inset 2px #999;
    border-bottom: inset 2px #ddd;
    border-right: inset 2px #ddd;
  }
  .btn.btn-sm {
    border-radius: 2;
    border-top: 2px solid #ddd;
    border-left: 2px solid #ddd;
    border-bottom: 2px solid #999;
    border-right: 2px solid #999;
    background-color: #d4d2cd;
    background-image: inherit;
    color: black !important;
    font-weight: 200;
  }
  .btn:hover {
    border-right-width: 2px !important;
  }
  .numbers-summary, .repository-lang-stats, .branch-infobar, .commit-tease, .files {
    background-color: #d4d2cd !important;
  }
  .readme > h3:first-child {
    background-color: #d4d2cd !important;
    border: none;
  }
  .navigation-focus {
    background-color: #c0c0c0 !important;
  }
  .repository-content {
    border: 2px solid #ddd;
    border-top: 0;
    padding: 5px;
  }
  .issues-listing .subnav form input.subnav-search-input {
    border-radius: 2px;
    border-bottom: 2px inset #ddd;
    border-right: 2px inset #ddd;
    border-left: 2px inset #999;
    border-top: 2px inset #999;
  }
  #discussion_bucket .discussion-timeline .js-discussion.js-socket-channel .timeline-comment-wrapper.js-comment-container {
    border-radius: 2px;
    border-bottom: 2px inset #ddd;
    border-right: 2px inset #ddd;
    border-left: 2px inset #999;
    border-top: 2px inset #999;
  }
  #discussion_bucket .discussion-timeline .js-discussion.js-socket-channel .timeline-comment-wrapper.js-comment-container .avatar-parent-child.timeline-comment-avatar {
    margin-top: 4px;
    margin-left: -56px;
  }
  #discussion_bucket .discussion-timeline .discussion-timeline-actions {
    border-radius: 2px;
    border-bottom: 2px solid #999;
    border-right: 2px solid #999;
    border-left: 2px solid #ddd;
    border-top: 2px solid #ddd;
    padding: 4px;
  }
  .logged-in.env-production.page-profile .user-profile-nav {
    background-color: #d4d2cd;
  }
  .logged-in.env-production.page-profile .user-profile-nav .UnderlineNav-item {
    padding: 8px 4px;
  }
  .logged-in.env-production.page-profile .is-stuck {
    background-color: white;
  }
  .logged-in.env-production.page-profile .pinned-repo-item {
    background-color: white;
  }
  .logged-in.env-production.page-profile .profile-timeline-year-list {
    background-color: #d4d2cd !important;
  }
  .logged-in.env-production.page-profile .profile-timeline-year-list li a {
    border-radius: 2px;
    border-bottom: 2px solid #999;
    border-right: 2px solid #999;
    border-left: 2px solid #ddd;
    border-top: 2px solid #ddd;
  }
  .logged-in.env-production.page-profile .profile-timeline.discussion-timeline {
    border-radius: 2px;
    border-bottom: 2px inset #ddd;
    border-right: 2px inset #ddd;
    border-left: 2px inset #999;
    border-top: 2px inset #999;
    background-color: white;
    padding: 4px;
  }
  .logged-in.env-production.page-profile .h-card .btn {
    border-radius: 2px;
    border-bottom: 2px solid #999;
    border-right: 2px solid #999;
    border-left: 2px solid #ddd;
    border-top: 2px solid #ddd;
    background-color: #d4d2cd;
    background-image: none;
  }
}
