css:
@-moz-document domain("reddit.com") {
/* Hide annoying stuff */
.give-gold-button,
.goldvertisement,
.embed-comment,
.nub {
    display: none !important;
}
/* Reset */
*, *:before, *:after {
    background-color: transparent !important;
    border: none !important;
    color: #777 !important;
}
/* Default links */
a {
    /*color: #48b !important;*/
    color: #5294E2 !important;
}
a:visited {
    color: #369 !important;
}
a:hover {
    color: #5ad !important;
}
/* Background */
html,
body,
#sr-more-link {
    background-color: #222 !important;
}
/* Top bar */
#header-img,
#mail,
.sidebox.create .spacer a {
    filter: invert(80%) hue-rotate(180deg) !important;
}
.drop-choices,
.hover-bubble,
.modal-dialog {
    background: #111 !important;
}
.tabmenu .selected a {
    color: #999 !important;
}
/* Side bar */
.listing-chooser-collapsed .grippy {
    width: 1px !important;
}
.listing-chooser-collapsed .listing-chooser {
    padding-right: 1px !important;
}
.listing-chooser li.selected {
    box-shadow: none !important;
}
.grippy:after {
    display: none !important;
}
/* Inputs and buttons */
input,
textarea,
.linkinfo .shortlink input,
.new-comment .usertext-body,
.morelink a,
.morelink:hover a,
.fancy-toggle-button a,
.usertext button {
    /*background: #333 !important;*/
    background: white !important;
    color: #999 !important;
    font-weight: normal !important;
    text-indent: 10px;
    border-radius: 5px;
}
/* Listing */
.thing .title {
    color: #aaa !important;
}
.title:visited {
    color: #777 !important;
}
.thing .title:hover {
    color: #ccc !important;
}
.arrow {
    filter: brightness(45%);
}
.arrow.upmod,
.arrow.downmod {
    filter: brightness(50%) contrast(120%);
}
.link .score.likes {
    color: #b3684d !important;
}
.link .score.dislikes {
    color: #7070c2 !important;
}
.expando-button {
    filter: brightness(45%) contrast(180%);
    background-color: transparent !important;
}
.moderator,
.green {
    color: #3a3 !important;
}
.admin,
.nsfw-stamp * {
    color: #a66 !important;
}
.pagename a,
.trophy-name {
    color: #999 !important;
}
.buttons li {
    padding: 0 !important;
}
.buttons a {
    margin-right: 8px !important;
    color: #888 !important;
}
.buttons a:visited {
    color: #666 !important;
}
.buttons a:hover {
    color: #aaa !important;
}
.pagename,
.tabmenu li,
.link .midcol,
.buttons a,
.subreddit {
    font-weight: normal !important;
}
.search-expando.collapsed:before,
.comment-fade {
    display: none !important;
}
.recommended-link {
    border-color: #333 !important;
}
/* Comments */
.link .usertext .md,
blockquote,
pre,
code,
.gold-accent {
    background-color: #777 !important;
    border-radius: 5px !important;
    border-width: thick thick thick 10px !important;
    border-color: white !important;
    color: white !important;

    -moz-transition: all .2s ease-in;
    -o-transition: all .2s ease-in;
    -webkit-transition: all .2s ease-in;
    transition: all .2s ease-in;
}
blockquote:hover {
    background-color: #5294E2 !important;
}
.md blockquote {
    border-left: solid 4px #333 !important;
}
.md td {
    border: solid 1px #333 !important;
}
hr {
    border-bottom: solid 1px #333 !important;
}
.comment .author,
.morecomments a {
    font-weight: normal !important;
}
.kYTLgH,
blockquote > p > .kYTLgH:hover {
    color: #c5c564 !important;
}
blockquote > p,
blockquote > p > .kYTLgH {
    color: #2F343F !important;
}
.hhpuSt > span {
    color: white !important;
}
.iEJDri {
    color: #D1D1D2 !important;
}
.icon-gild {
    color: gold !important;
}
.threadline {
    border-right: 2px solid #5294E2 !important;
    opacity: 0;
}
.hovered .threadline {
    opacity: 1;
}
/* RES */
.guider,
.guiders_button,
.res-fancy-toggle-button,
#RESConsoleContainer,
#RESShortcutsAddFormContainer {
    /*background: #222 !important;*/
    background: #2F343F !important;
}
.RESDialogSmall,
.RESDropdownOptions,
.RESNotification,
#alert_message {
    background: #111 !important;
}
.RES-keyNav-activeElement,
.RES-keyNav-activeElement .md-container {
    background: #333 !important;
}
.res-nightmode .arrow {
    filter: none !important;
}
/* new.reddit.com */
#header,
#lightbox,
#hamburgers,
body > div,
div[style^="left:"]
div[role="button"] {
    background-color: #2F343F !important;
}
#header svg {
    filter: invert(90%) hue-rotate(180deg);
}
div[contenteditable="true"] {
    background: white !important;
    border-radius: 5px !important;
}
#overlayFixedContent + div {
    background: rgba(0, 0, 0, 0.7) !important;
}
div[data-slot] {
    display: none !important;
}
.icon, .icon:before {
    color: inherit !important;
}
a[data-click-id="timestamp"] {
    color: #777 !important;
}
a[data-click-id="body"] h2 {
    color: #aaa !important;
}
a[data-click-id="body"]:visited h2 {
    color: #777 !important;
}
.scrollerItem {
    box-shadow: none !important;
}
.joxGhD, .dwAvMG {
    color: white !important;
}
.iECVcW {
    background-color: #2F343F !important;
}
}
