css:
@-moz-document domain(linuxacademy.com) {
body {color:white;background-color:#13181b;}
a, a:link, a:visited {color:#337ab7;}
.alert-warning {background-color:#333; color:#c5a670;}
.course-page-header-container { border:1px solid #29485b;}
.course-page-nav { border:1px solid #29485b;}
.dashboard-bookmarks {border: 1px solid #29485b;}
.dashboard-bookmarks .bookmarks-holder {background-color:#333;}
.dashboard-bookmarks .boomarks-top-row {border-bottom: 1px solid #29485b;}

.panel {background-color:#333;}
.panel-default>.panel-heading {background-color:#333;}
.panel-default-course-page {border: 1px solid #337ab7;}
.panel-default-course-page .panel-heading+.panel-collapse>.panel-body {border-top-color: #29485b;}

.border-left-f7 { border:1px solid #29485b;}
.exercise-page-header-container { border:1px solid #29485b;}
.exercise-page-nav {border-top: 1px solid #29485b;}
.syllabus-section-header { border-bottom:1px solid #29485b; }

.jquery-msgbox { background-color:#29485b!important; overflow:hidden;}

.modal-content { color:white; background-color:#182b37; }
}
