css:
@-moz-document domain("youtube.com") {
:root {
    --colour: /*[[mainColour]]*/;
    --searchBarBg: rgba(12,12,12,0.3);
}

/* Switch to COLOR VARIABLE */
.ytp-swatch-background-color, .ytp-volume-slider-handle::before, .ytp-menuitem[aria-checked="true"] .ytp-menuitem-toggle-checkbox, .ytp-scrubber-button, .ytp-volume-slider-handle, .ytp-menuitem[aria-checked="true"] .ytp-menuitem-toggle-checkbox::after, .ytp-settings-button.ytp-hd-quality-badge::after, .ytp-settings-button.ytp-4k-quality-badge::after, .ytp-settings-button.ytp-5k-quality-badge::after, .ytp-settings-button.ytp-8k-quality-badge::after, paper-button.ytd-subscribe-button-renderer, .ytp-chrome-controls .ytp-button[aria-pressed]::after, paper-toggle-button[checked]:not([disabled]) .toggle-button.paper-toggle-button, #progress.yt-page-navigation-progress, ytd-button-renderer.style-primary[is-paper-button], ytd-sentiment-bar-renderer[activated_] #like-bar.ytd-sentiment-bar-renderer, ytd-sponsorships-tier-renderer yt-button-renderer #button.yt-button-renderer, ytd-subscribe-button-renderer[new-subscribe-color] paper-button.ytd-subscribe-button-renderer, #newness-dot.ytd-guide-entry-renderer, #progress.ytd-thumbnail-overlay-resume-playback-renderer {
	background-color: var(--colour);
}
.ytp-swatch-color, ytd-guide-entry-renderer[is-guide-refresh][active] .guide-icon.ytd-guide-entry-renderer, .guide-entry-badge.ytd-guide-entry-renderer, .badge-style-type-red.ytd-badge-supported-renderer, #link-list-container a, ytd-expander.ytd-video-secondary-info-renderer a, #content-text a, .sbpqs_a, ytd-toggle-button-renderer.style-default-active[is-icon-button], #confirm-button.yt-confirm-dialog-renderer, [page-subtype="subscriptions"] .grid-subheader ytd-button-renderer #button.ytd-button-renderer, ytd-active-account-header-renderer yt-formatted-string[ellipsis-truncate] a.yt-formatted-string:last-child, .badge-style-type-live-now.ytd-badge-supported-renderer {
    color: var(--colour) !important;
}
.ytp-volume-slider-handle::after {
    z-index: -1;
}
ytd-topbar-logo-renderer.style-scope:nth-child(4) > a:nth-child(1) > div:nth-child(1) > svg:nth-child(1) > g:nth-child(1) > g:nth-child(1) path, ytd-topbar-logo-renderer.style-scope:nth-child(2) > a:nth-child(1) > div:nth-child(1) > svg:nth-child(1) > g:nth-child(1) > g:nth-child(1) > path:nth-child(1) {
    fill: var(--colour) !important;
}
paper-toggle-button[checked]:not([disabled]) .toggle-bar.paper-toggle-button {
    background: var(--colour);
    opacity: 1;
}
paper-button[subscribed].ytd-subscribe-button-renderer:hover {
    background: var(--colour);
    color: #fff !important;
}
paper-button[subscribed].ytd-subscribe-button-renderer:hover .deemphasize.yt-formatted-string, paper-button.ytd-subscribe-button-renderer .deemphasize.yt-formatted-string {
    color: #aaa !important;
}
paper-button[subscribed].ytd-subscribe-button-renderer:hover .deemphasize.yt-formatted-string, ytd-sponsorships-tier-renderer yt-button-renderer #button.yt-button-renderer, ytd-comment-simplebox-renderer ytd-button-renderer #button.ytd-button-renderer, ytd-subscribe-button-renderer[new-subscribe-color] paper-button.ytd-subscribe-button-renderer .deemphasize.style-scope.yt-formatted-string, ytd-subscribe-button-renderer[new-subscribe-color] paper-button.ytd-subscribe-button-renderer[subscribed] yt-formatted-string.ytd-subscribe-button-renderer, paper-button.ytd-button-renderer {
    color: #fff !important;
}
ytd-button-renderer.style-suggestive[is-paper-button] paper-button.ytd-button-renderer, .badge-style-type-live-now.ytd-badge-supported-renderer {
    border-color: var(--colour);
}







/* CHANGES */
ytd-searchbox[mode="legacy"] #container.ytd-searchbox, ytd-searchbox[mode="legacy-centered"] #container.ytd-searchbox {
    box-shadow: none;
    border-color: transparent;
    padding: 10px 15px;
    box-sizing: border-box;
    border-radius: 5px 0 0 5px;
    background: var(--searchBarBg);
}
#search-icon-legacy.ytd-searchbox {
    background: var(--searchBarBg);
    border-color: transparent;
    border-radius: 0 5px 5px 0;
}
#search-icon-legacy.ytd-searchbox:hover {
    background: var(--searchBarBg) !important;
    border-color: transparent !important;
}
#search-form {
    border-radius: 5px;
}
#search[has-focus=""] #container {
    border-color: transparent;
    box-shadow: none;
}
#search[has-focus=""] #search-form {
    border: 1px solid var(--colour);
}
ytd-video-primary-info-renderer {
    border-bottom: none;
}
#primary-links.ytd-c4-tabbed-header-renderer, #secondary-links.ytd-c4-tabbed-header-renderer {
    background: rgba(0,0,0,0.6);
}
#sentiment.ytd-video-primary-info-renderer {
    width: 99.5% !important;
}
ytd-button-renderer.style-primary[disabled][is-paper-button] {
    background: rgba(200,200,200,0.1)
}
ytd-button-renderer.style-suggestive[is-paper-button] {
    background: transparent;
    border-color: var(--colour);
    color: var(--colour);
}
ytd-subscribe-button-renderer[button-style="COMPACT_GRAY"] paper-button.ytd-subscribe-button-renderer {
    background: var(--colour);
    color: #fff;
}

#guide-wrapper {
    margin-right: -18px;
}
app-drawer #contentContainer {
    width: 240px !important;
    overflow-x: hidden;
}
#toggle.ytd-grid-renderer {
    background: rgba(50,50,50,0.5);
    padding: 10px 18px;
    color: #eee;
    transition: 0.1s ease;
}
#toggle.ytd-grid-renderer:hover {
    background: rgba(50,50,50,0.7)
}
#search-input.ytd-searchbox-spt input {
    color: #eee;
}
ytd-searchbox[mode="legacy"] #search-icon-legacy.ytd-searchbox:focus, ytd-searchbox[mode="legacy-centered"] #search-icon-legacy.ytd-searchbox:focus {
    background: #1c1c1c;
}
.sbfl_b {
    background: transparent;
}
#enhancer-for-youtube-progress::-moz-progress-bar {
    background: var(--colour);
}
.sbsb_a, .sbdd_b {
    background: #222 !important;
    border-color: #222 !important;
}
.sbqs_a, .sbqs_c {
    color: #eee;
}
.sbsb_d {
    background: #2c2c2c;
}
}
