css:
@-moz-document url-prefix("https://ololo.to/") {
.hp-main {
	
	background-color: black !important;
	
}

.header, .search-input {
   
    background-color: #140d0d !important;
	
}

.search-input  {
	
	border: 2px solid #4b2b2b !important;
	
}

.search-btn {
	
	background-color: #4b2b2b !important;
	
}
}

@-moz-document url-prefix("https://ololo.to/s/"), url-prefix("https://ololo.to/search") {
.hp-main, body {
	
	background-color: black !important;
	
}

.results-filter-nav li a {
	
	background-color: #140d0d !important;
	border: 2px solid #4b2b2b !important;
	
}

.results-filter-nav li a.active {
	
	border: 2px solid #4b2b2b !important;
	background-color: #4b2b2b !important;

}

.item {
	
	background-color: black !important;
	border:  #140d0d   !important;
}

.results-heading h2, .results .item-title, .results-filter-nav li a {
   
    color: #a72a2a;
}
}

@-moz-document url-prefix("https://ololo.to/video"), url-prefix("https://ololo.to/search") {
body, .video-detail-content, #recaptcha-anchor-label {
	
	background: #020101 !important;
	
}

.video-detail-content h2, .video-detail-time, .video-detail-content h3 {
   
    color: #a72a2a;
}

.video-detail {
	
	background-color: #080606 !important;
	
	border: 2px solid #080606 !important;
	
}

.item {
	
	background-color: #0b0909 !important;
	border: 2px solid #140d0d   !important;
}

 .video-player.captcha {
			
			background-color: #0b0909 !important;

}

.video-title {
   
    color: #a72a2a;
}
}

@-moz-document url-prefix("https://ololo.to/top-searches"), url-prefix("https://ololo.to/about.html"), url-prefix("https://ololo.to/webmasters.html") {
body {
	
	background: black !important;
	
}

.article-container {
	
	background-color: #0b0909 !important;
	border: 2px solid #140d0d   !important;

}
}
