/*--------------VERTICAL-NAV----------------*/

div#nav {
    width: 65px;
    height: 100%;
    position: fixed!important;
    top: 0!important;
    left: 0!important;
    transition: none!important;
}

div#nav.transparent {
    background: rgba(var(--color-nav))!important;
}

.nav .wrap .links {
    font-size: 1rem;
    height: 355px;
    margin-left: 0px;
    padding-left: 0px;
    width: 65px;
    flex-direction: column;
}

.nav .wrap .links .link {
    width: 65px;
    padding: 5px 0px;
    margin-bottom: 10px;
    text-align: center;
    color: rgba(var(--color-blue2),0.6);
    transition: 0.3s;
}

.nav .wrap .links .link:hover {
    background: rgba(var(--color-blue2),0.1);
}

.nav .wrap .links .link:before {
    display: block;
    content: '';
    height: 24px;
    width: 65px;
    background-size: 24px;
    margin-left: 0;
    margin-bottom: 5px;
    background-repeat: no-repeat;
    background-position: center;
}

.nav .link[href="/home"]:before {
    background: url(https://puu.sh/AvnoD/65ab2968a6.png);
}
.nav .link[href*="/user/"]:before {
    background: url(https://puu.sh/AvnlD/7a305ea3c6.png);
}
.nav .link[href*="/animelist"]:before {
    background: url(https://puu.sh/AvnpD/74ec4b003b.png);
}
.nav .link[href*="/mangalist"]:before {
    background: url(https://puu.sh/AvnpD/74ec4b003b.png);
