css:
@-moz-document domain("feedly.com") {
  #feedlyPageHolderFX {
      background: #212121;
  }
  #searchBarFX, .fixed-bar, #headerBarFX header {
      background: #2A2B2F;    
      color: #757575;
  }
  #headerBarFX h1 {
      color: #d2d2d2;
  }

  .heading {
      color: #d2d2d2;
  }
  .fx .entry .summary, .fx .entry .metadata {
      color: #777777;
  }
  .fx .entry.read .title {
      color: #4F5051;
      font-style: italic;
  }
  .fx .entry.u4.selected {
      border-left: 4px solid #4F5051;
  }

  .fx .entry.unread .title {
      color: #fff;    
  }

  .fx .entry.u4 {
      border-left: 4px solid #212121;
  }

  .fx .header .AllPage__page-button:hover, .fx .header .button-icon-only:hover,
  .fx button.button-icon-only.tertiary:hover, .fx button.tertiary.AllPage__page-button:hover {
      background-color: #2A2B2F;
  }
  .fx-button.secondary:hover {
      background-color: #2A2B2F;
      border: 1px solid #2A2B2F;
  }
  .fx button.secondary:hover {
      background-color: #2A2B2F;
  }

  .fx .EnterpriseGroupEditor__initial, .fx .form .public-DraftEditor-content, .fx .form .public-DraftEditorPlaceholder-root, .fx .form [type=date], .fx .form [type=datetime-local], .fx .form [type=email], .fx .form [type=number], .fx .form [type=password], .fx .form [type=search], .fx .form [type=tel], .fx .form [type=text], .fx .form [type=time], .fx .form [type=url], .fx .form input:not([type]), .fx .form textarea, .fx.fx.fx .TextInput, .StripeElement {
      background-color: #2A2B2F;
      color: #fff;
      border: 1px solid #212121;    
  }
  .fx .EnterpriseGroupEditor__initial:focus, .fx .form .public-DraftEditor-content:focus, .fx .form .public-DraftEditorPlaceholder-root:focus, .fx .form [type=date]:focus, .fx .form [type=datetime-local]:focus, .fx .form [type=email]:focus, .fx .form [type=number]:focus, .fx .form [type=password]:focus, .fx .form [type=search]:focus, .fx .form [type=tel]:focus, .fx .form [type=text]:focus, .fx .form [type=time]:focus, .fx .form [type=url]:focus, .fx .form input:focus:not([type]), .fx .form textarea:focus, .fx.fx.fx .TextInput:focus, .StripeElement:focus {
      background-color: #2A2B2F;    
      border: 1px solid #212121;
      color: #fff;
  }
  .fx .form .fx-input .clear-input {
      background-color: #2A2B2F;
  }

  .fx-item-header, .fx .item-header {
      color: #757575;
  }
  .fx .form .field .label, .fx .form .field label:not(.ButtonToggle), .fx .picker .item-header {
      color: #757575;    
  }

  .fx .form .fx-select button {
      color: #757575;
  }

  .fx .organize--cell-actions {
      background: linear-gradient(90deg,hsla(0,0%,100%,0),#757575 14px);    
  }

  .fx-button.secondary:hover, .fx .button.secondary:hover, .fx button.secondary:hover {
      background: #fff;
      border: 1px solid #333333;
  }
  .organize--hover-button.secondary.small.button-icon-only {
      border: 0;
  }

  .fx-button.secondary:hover, .fx .button.secondary:hover, .fx button.secondary:hover {
      background: transparent;
  }

  .list-cards .card {
      box-shadow: 0 1px 5px 0 rgba(0,0,0,.15),inset 0 0 100px 0 #333333!important;    
      color: #fff;
  }

  .floatingEntryScroller, .headerInfo, .entryholder {
      background-color: #212121;
  }
  .floatingEntryContent .headerInfo {
      border-bottom: 0;
  }
  .headerInfo-expanded-img:hover, .headerInfo .headerInfo-links .fx button.button-icon-only-micro:hover, .shareBarHolder .headerInfo-links .fx button.button-icon-only-micro:hover {
      background-color: #333333;
      color: #ffffff;
  }

  .preference-sections h3 {
      color: #d2d2d2;
  }
  .fx .feed-preview .preference-sections ul, .preference-sections .fx .feed-preview ul, .preference-sections p {
      color: #757575;
  }

  .fx .profile-bubble button:hover {
      border: 0;
  }

  .slideEntryContent .u100Entry .title, .entryBody h3, .entryBody h2, .entryBody h1 {
      color: #d2d2d2;
  }

  .entryBody .summary b, .entryBody .content b, .entryBody td {
      color: #757575;
  }

  .entryBody {
      color: #757575;
  }
  .entryBody img, .content img {
      border: 1px solid #111111 !important;
  }

  .slideBumper div {
      background-color: #111111;
  }
  .slideBumper-left:hover div, .slideBumper-right:hover div {
      background-color: #111111;
  }

  .mark-as-read-button-group button.secondary:hover {
      border: 1px solid transparent;
  }
  .fx .entry.u0 {
    border-top: 1px solid #2A2B2F;
  }
  .fx .entry.u0:hover {
    background: #2A2B2F;    
    color: #757575;
  }
  .fx a.item-header:hover {
    color: #9e9e9e;
  }
  .fx .feed-preview .entry.read .title {
    color: #9e9e9e;    
  }
}
