css:
/***********

Userstyle for Rocket Chat https://open.rocket.chat/ 
Created by: Neotarlax
Based on: https://userstyles.org/styles/136988/rocket-chat-inverted-dark
	From: JakeBathman
Version: v1.1.2
RocketChat Latest Version Supported: v0.65.1

What's new?
-fixed some styles
-fancy emojis
-highlighted mentions

***********/
@-moz-document domain(open.rocket.chat) {
code {
    font-family: Monaco, Menlo, Consolas, "Courier New", monospace !important;
}

.rc-message-box__container.editing{
    background-color: gray !important;
}

.account-box .info {
    background-color: #0c0c0c !important;
}

.side-nav .rooms-list {
    background-color: #171717 !important;
}

.side-nav .unread-rooms {
    background-color: #6D6D6D !important;
}

.side-nav .unread {
    background-color: #6D6D6D !important;
}

.side-nav .unread-rooms.bottom-unread-rooms {
    bottom: 0px;
}

.side-nav .footer {
    background-color: #0c0c0c !important;
}

.flex-nav footer {
    background-color: #191919;
}

.fixed-title {
    background-color: #0c0c0c !important;
    color: white !important;
    border-bottom-color: #696969;
}

.messages-box {
    background-color: #252525;
}

.rocket-search{
    background-color: #303035;
}

.messages-box ul {
    color: #CDCDCD;
}

.rc-header__wrap{
    background-color: slategray !important;
}

.message-actions{
	color: white !important;
}

.rc-message-box__container{
    background-color: #252525;
	border-color: slategray !important;  
}

.messages-box ul>li.message:nth-child(odd) {
    background-color: #252525;
}

.messages-box ul>li.message:nth-child(even) {
    background-color: #252525;
}

.message .user {
    color: #D0D0D0;
}

div.body a {
    color: #D0D0D0 !important;
}

.message.new-day:before {
    background-color: #424242 !important;
    color: #EEEEEE;
    font-size: 1.1em;
}

.message.new-day:after {
    border-top-color: #424242;
}

.message .user:hover {
    color: #EEEEEE;
}

.messages-container .footer {
    background-color: #0c0c0c !important;
    border-top-color: #696969;
}

.messages-container .message-form>div>.file {
    background-color: #2b2c2f !important;
    border-color: #999 !important;
}

.messages-container .message-form textarea {
    background-color: #2b2c2f !important;
    border-color: #696969;
    color: #EEEEEE;
}

.messages-container .message-form.editing {
    border: 1px solid #E6DE0A !important;
}

.icon-paper-plane, .send-button {
    background-color: #434343 !important;
}

.flex-tab-bar, .tab-button {
    color: black;
}

.flex-tab-bar {
    background-color: #171717 !important;
}

.message.new-day .day-divider {
    background-color: #1e2125;
}

.flex-tab .content>div, .content, .flex-tab .message.new-day .day-divider {
    background-color: #1e2125 !important;
    color: white;
}

.message.new-day .day-divider span, .flex-tab .message.new-day .day-divider span {
    background-color: #696969;
    color: white;
}


/* ---Message InputBox--- */

.messages-container .message-form .stream-info {
    background: #0c0c0c;
}

.messages-container .message-form>.message-input>.file, .messages-container .message-form>.message-input .message-buttons, .messages-container .message-form>.message-input {
    color: #a2a2a2;
    background-color: #2b2c2f;
    border-color: #0c0c0c;
    border-radius: 0px;
}

.messages-container {
    border-right-color: #0c0c0c;
}

.message .info .is-bot, .message .info .role-tag, .user-view .info .role-tag {
    background-color: #4F4F4F;
}

.list-view.uploaded-files-list i {
    color: #fff;
}


/* ---Made the code box dark--- */

.hljs, code {
    background: #1f1f1f;
    color: #fff;
    border-color: #636363;
}


/* ---Syntax Highlighting Patches--- */

.hljs-deletion {
    background: #903a3a;
}

.hljs-addition {
    background: #2b842b;
}

.hljs-tag {
    color: #ffb0e2;
}


/* ---Fixed icons to make them more viewable on dark background--- */

.message .message-dropdown ul li {
    color: #fbfbfb;
}

.message .message-dropdown {
    background-color: #171717;
    border: 1px solid #555555;
}


/* ---Made message edit options dark on selection and highlight--- */

.message .message-dropdown ul li:hover, .message .message-dropdown ul li:first-child {
    background-color: #000000;
}


/* ---Prevent page from blasting white in your face when loading--- */

#rocket-chat {
    background-color: #0c0c0c;
}

.messages-container .message-form .formatting-tips {
    color: #fff;
}

.messages-container .message-form .users-typing {
    color: #fff;
    background-color: inherit;
}


/* ---Fix Dark Links--- */

a {
    color: #33fdff;
}

a:hover {
    color: #fff;
}


/* ---Edit Message--- */

.messages-box .editing .body {
    background-color: #696969;
    color: white;
}


/* ---Fix Admin Area--- */

.flex-tab .content>div, .content, .flex-tab .message.new-day .day-divider {
    background-color: #191919 !important;
    color: #a2a3a5;
}

.statistics-table {
    border-color: #696969;
}

.statistics-table tr {
    background-color: #191919;
}

.statistics-table tr:nth-of-type(even) {
    background-color: #24282d;
}

.page-settings .section {
    background-color: #191919;
    color: white;
}

.page-settings .section .section-content .input-line>label {
    color: white;
}

.page-list .list table tbody tr:nth-child(odd) {
    background-color: #282c31;
}

.page-list .list table tbody tr.row-link:hover {
    background-color: #4c4c4c;
}


/* ---Searchbox on Rooms Admin--- */

textarea, select, input[type='text'], input[type='number'], input[type='email'], input[type='url'], input[type='password'], input[type='time'] {
    border-color: #696969;
    background-color: #131313;
    color: white;
    border-radius: 0px;
}


/* Primary background color */

.primary-background-color {
    background-color: #171717;
}


/* Global search box results pane */

.message-popup.search-results-list {
    background-color: #0c0c0c;
    height: calc(100vh - 100px);
}


/* ---Admin Intergrations Page--- */

.admin-integrations-new-panel .admin-integrations-new-item:hover {
    background-color: #4c4c4c;
}

.admin-integrations-new-panel .admin-integrations-new-item {
    color: #fff;
}

.input-line>label {
    color: #fff;
}

.page-settings .section .section-content .input-line input {
    color: #fff;
    background-color: #1e2125 !important;
}

.page-settings .section .section-title {
    color: #a2a2a2;
}

.page-settings .content {
    color: #9a9a9a;
}


/* ---Disabled Input Boxes--- */

input[readonly], input[disabled], textarea[readonly], textarea[disabled] {
    background-color: #424242 !important;
    color: #9e9e9e;
}


/* ---Alert--- */

.page-settings .section .section-content .input-line .settings-alert {
    background-color: #000000;
    border: 1px solid #ff0000;
    color: red;
}


/* ---Permission Table--- */

.permissions-manager .permission-grid thead tr {
    background-color: #4c4c4c;
}

.permissions-manager .permission-grid td * {
    color: #fff;
}


/* ---Buttons--- */

.button, .button-primary, .button.secondary {
    background-color: #2b2e33 !important;
    color: #adadad !important;
}

.button:hover, .button-primary:hover, .button.secondary:hover {
    background-color: #3C3C3C !important;
    color: #BCBCBC !important;
}

.messages-container .message-form>.message-input>.file:hover, .messages-container .message-form>.message-input .message-buttons:hover {
    color: white;
    background-color: #0c0c0c;
}


/* ---Video Input Popup Box--- */

.vrec-dialog {
    background-color: #1e2125;
}


/* ---Unread Message Bar and Marker---*/

.container-bars .unread-bar {
    background-color: #333;
    color: #068fe4;
}

.first-unread .body::before, .cozy .first-unread .body::before, .compact .first-unread .body::before, .first-unread.first-unread-opaque .body::before {
    background-color: rgba(44, 127, 198, .3);
}

.first-unread .body::after, .first-unread.first-unread-opaque .body::after {
    color: #fff;
}


/* ---Emoji Picker--- */

.emoji-picker {
    background-color: #1e2125;
}

.emoji-picker .filter>ul>li, .emoji-picker .filter>ul>li:hover {
    border-bottom: 2px solid #1e2125;
}

.emoji-picker .filter>ul>li.active {
    border-bottom: 2px solid #ffffff;
}

.emojione{
	image-rendering: auto;
}

ul.reactions li.selected {
    background-color: #383838 !important;
    border-color: #b5b5b5 !important;
}

ul.reactions li:not(.selected) {
    background-color: rgba(255, 255, 255, 0) !important;
    border: 1px solid #444444 !important;
}


/* ---Avatar Selection--- */

.avatar-suggestion-item {
    background-color: #1e2125;
    border-color: #696969;
}


/* ---Fix white login page text--- */

#login-card .input-text input, #login-card .input-text select {
    color: black;
}


/* ---Fix mention color to stand out--- */

.body a.mention-link {
    color: #70bdff !important;
    font-weight: bold !important;
    background-color:#001b33;
}

.body a.mention-link-me {
    color: #ffffff !important;
    background-color: #2C7FC6 !important;
    font-weight: normal !important;
}


/* ---Text highlight styling--- */

.highlight-text, .message .highlight-text {
    background-color: #efdd62;
    color: #000000;
}

.account-box .options, .side-nav .options button {
    background-color: #111111;
}

.account-box .options button, .account-box .options a {
    border-bottom-color: #313131;
}

.flex-nav header {
    background-color: #0c0c0c;
}


/* ---Remove footer image--- */

aside .footer {
    display: none;
}

.side-nav .rooms-list {
    height: calc(100% - 100px);
}

.account-box .options {
    height: calc(100% - 60px);
}

.flex-nav .content {
    height: calc(100% - 100px);
}


/* ---Sidebar fixes--- */

.message:hover {
    background-color: #2d2d2d;
}

.flex-nav .input-line input[type='text'], .flex-nav .input-line input[type='password'], .flex-nav .input-line select {
    background-color: #2b2c2f;
}


/* ---User status colors--- */

.user-image.status- .avatar:after {
    background-color: #464646;
}

.user-image.status- .avatar:after, .status-offline, .icon-at.status-offline {
    color: #A0A0A0;
}

.user-image.status-online .avatar:after, .status-online, .icon-at.status-online {
    color: #30d40b;
}

.user-image.status-away .avatar:after, .status-away, .icon-at.status-away {
    color: #fecf09;
}

.user-image.status-busy .avatar:after, .status-busy, .icon-at.status-busy {
    color: #FF1E1E;
}

.main-content header, .cms-page header, .page-container header {
    background-color: #191919;
    border-color: #eaeaea;
}

.main-content {
    background-color: #252525;
}

.message .body {
    color: #CDCDCD;
}


/*  Syntax highlighting fixes    */


/* 
Color everything poorly, for development 

span[class*="hljs-"]{
    color:#A0699B;
}
*/

.code-colors {
    background-color: #1f1f1f;
    color: #D4D4D4;
    border-color: #636363;
    font-weight: normal !important;
}

.hljs, code {
    color: #D4D4D4;
    font-weight: normal !important;
}

.hljs-keyword, .hljs-common {
    color: #F4F4F4 !important;
    font-weight: bold;
}

.hljs-string {
    color: #FF4574 !important;
}

.hljs-literal, .hljs-number {
    color: #6FD1FF !important;
}

.hljs-tag {
    color: #CCCCCC !important;
}

.hljs-comment, .hljs-shebang, .hljs-preprocessor {
    color: #BABABA !important;
}

.hljs-variable {
    color: #FFBF35 !important;
}

.hljs-built_in {
    color: #f754d2 !important;
}

.hljs-title {
    color: #F4F4F4 !important;
}

.hljs-attr_selector {
    color: #FFFFFF !important;
}

.hljs-attribute {
    color: #FFBF35 !important;
}

.hljs-value {
    color: #ccc !important;
}

.hljs-function {
    color: #EDE62A !important;
}

.hljs-params {
    color: #47FEC8 !important;
}

.hljs-deletion {
    background-color: #852B2B !important;
}

.hljs-addition {
    background-color: #226922 !important;
}

.hljs-change {
    background-color: #157696 !important;
}

.hljs-header, .hljs-bullet, .hljs-blockquote {
    color: #999999 !important;
}

.hljs-chunk {
    color: #aaaaaa !important;
}

.hljs-request {
    color: #CCCCCC !important;
}

.hljs-prompt {
    color: #999999 !important;
}

.hljs-constant {
    color: #47FEC8 !important;
}

.hljs-subst {
    color: #CCCCCC !important;
}

.hljs-regexp {
    color: #51e075 !important;
}

.hljs-strong {
    font-weight: bold;
}

.hljs-emphasis {
    font-style: italic;
}

.hljs-pseudo, .hljs-hexcolor {
    color: #47FEC8 !important;
    font-style: italic;
}


/*  Fix background blues at login and refresh  */

.full-page {
    background-image: linear-gradient(#1e2123, #131415);
}

body {
    background-color: #131415;
}


/* Fix issue when viewing non-styled pages, such as /api/info */

body:not([class]) {
    background-color: #fff;
}


/* Quoted text left bar */

.background-transparent-darker-before::before {
    background-color: #616161;
}
}
