css:
@-moz-document domain("tproger.ru") {
/*Backgrounds*/

body {
    background: #000;
    color: #EEE;
}

.post-title {
    background: #060606;
}

.entry-content {
    background: #060606;
}

blockquote {
    background: #060606;
}

.box {
    background: #060606;
}

.native-ads-footer {
    background: #060606;
}

.navbar-inverse .navbar-inner,
.widget-head,
header#masthead {
    background: #060606 !important;
}

.widget_tag_cloud .tagcloud a {
    background: #060606 !important;
}

/*Fonts*/

#above-content-button {
    color: #EEE;
}

.entry-content p {
    color: #EEE;
}

blockquote p {
    color: #EEE !important;
}

.news-title a {
    color: #EEE !important;
}

.news-show-all {
    color: #EEE !important;
}

.entry-title a {
    color: #DDD !important;
}

.entry-content p,
.entry-content ul li,
.entry-content p,
.entry-content ol li {
    color: #EEE;
}

.event-wrapper>a>span {
    color: #EEE !important;
}

.grf-list__title {
    color: #EEE !important;
}

.tag-menu-item>a {
    color: #FFF !important;
}

.switcher_content>span {
    color: #EEE !important;
}

#feed_mode_select {
    background: #060606 !important;
    color: #EEE !important;
}

.pagination>a {
    background: none;
}

textarea,
input[type="text"],
input[type="password"],
input[type="datetime"],
input[type="datetime-local"],
input[type="date"],
input[type="month"],
input[type="time"],
input[type="week"],
input[type="number"],
input[type="email"],
input[type="url"],
input[type="search"],
input[type="tel"],
input[type="color"],
.uneditable-input {
    background-color: #060606;
    border: 3px solid #222 !important;
    border-radius: 0 !important;
}

.bluth.tproger,
.btn {
    background: #222;
    color: #CCC;
    border: 3px solid #222;
    border-radius: 0;
}

.btn.bluth.tproger:hover,
.btn.bluth.tproger:focus {
    background: #444;
    color: #FFF;
    border-radius: 0;
}

#masthead .bluth-navigation.fixed {
    border: none;
}

#masthead .bluth-navigation {
    /* background: #000; */
}

.jobs-baner-meta {
    color: inherit !important;
}

.about-company-jobs-title a {
    color: #04a49c !important;
}

.grf-list__link {
    background-color: #060606 !important;
}

.share-story>li>a>i::before {
    color: #CCC;
}

footer.entry-meta ul a {
    color: #04a49c;
}
}
