css:
@-moz-document domain("theconservativetreehouse.com") {
/*medium red*/
div#container,
div#access {
	background: #B14973
}
/*pink*/
div#wrapper {
	background: #d8a2b6
}
/*gray*/
div#container div#content {
	background: #bbb;
	padding-left: 4px;
	font-size: 14px;
	font-family: sans, opens sans, verdana !important
}
NW div#container div#content   div[class^="post-"]:nth-chlid(even) {
	background: blue !important
}
#main a {
	color: #111 !important
}
h2.entry-title {
	font-size: 17px !important;
	text-decoration: underline;
}
span.comments-link {
	font-weight: bold
}
div[class^="post-"] {
	padding-bottom: 5px ;
	border-bottom: 1px !important;
	border-style: solid !important;
	border-color: #333 !important;
}
div#content p {
	font-family: sans, opens sans, verdana !important
}
div#content a {
	color: brown !important
}
NW [id^="comment-"]:nth-child(odd) {
	background: redX
}
div.comment-body p {
	font-size: 14px;
	line-height: 130%
}
cite.fn,
div#main cite.fn a.url {
	color: #ddd !important
}
NW #main #container #content #comments div[id^="comment-"]:nth-child(odd) div.comment-body  {
	background: red-NW !important
}
td.recentcommentstexttop,
td.recentcommentstextend,
 td.recentcommentstextend a {
	color: #d6a9c3 
}



}
