css:
@-moz-document domain(karachan.org)
{

body:not(#menu) 
{
	background-image: url("https://i.imgur.com/RupewKU.png"), url("https://i.imgur.com/MfSUi4M.png");
	background-repeat: no-repeat, repeat;
	background-attachment: fixed, fixed;
    background-position: right top;
	color: #7e1500;
}

.haze {
	color: #fff;
	background-color: #7e1500;
    font-size: 10px;
    font-family: "ＭＳ ゴシック";
    font-weight: bold;
    padding-left: 2px;
}

h2.boardHeader {
	background-image: url("https://i.imgur.com/7Uv6rYR.png");
    border-radius: 0px;
}

.ladda-button {
	border: 1px solid #a8a8a8;
	color: #7e1500;
    background-color: #f0ebeb!important;
    border-radius: 0px;
}

.ladda-button:hover {
	border: 1px solid #a8a8a8;
    background-color: #fff!important;
    border-radius: 0px;

}

.ladda-button[data-loading] {
	background-color: #fff!important;
	border: 1px solid #a8a8a8;
    border-radius: 0px;
}

.ladda-button[data-color="mint"] {
	background-color: #F0F0F0;
    border-radius: 0px;
}

.ladda-button[data-color="mint"]:hover {
	background-color: #D4D4D4;
    border-radius: 0px;
}

.btn {
    border: 1px solid #a8a8a8;
    background-color: #f0ebeb;
	color: #7e1500;
    border-radius: 0px;
}

.btn:hover {
	background-color: #fff;
}

.BBButton {
    border: 1px solid #a8a8a8;
    background-color: #f0ebeb;
	color: #7e1500;
    border-radius: 0px;
}

.BBButton:hover {
	background-color: #fff;
}

tr.rules > td:nth-child(1) {
    background: none;
}

.tmoved {
	color: blue;
}
.adminControls > a {
    color: #c35892!important;
}
.adminControls > a:hover {
    color: #f60085!important;
    text-shadow: 0px 0px 1px #f60085;
}

a,a:visited {
	color: #510d00!important;
	text-decoration: none!important;    
}

a.replylink:not(:hover) {
	color: #510d00!important;
}

a:hover, #menu a:hover {
	color: #7e1500!important;
}

hr {
	border: none;
	border-top: 0px;
    padding: 7px;
}

input,textarea,select { 
	border: 1px solid #7e150066!important;
    color: #7e1500;
}

.deleteform > input:nth-child(4) {
	background-color: #f0ebeb;
}

.deleteform > input:nth-child(8) {
	background-color: #f0ebeb;
}

.next > form:nth-child(1) > input:nth-child(1) {
	border-radius: 0px;
    background-color: #f0ebeb;
	color: #7e1500;
}

.prev > form:nth-child(1) > input:nth-child(1) {
	border-radius: 0px;
    background-color: #f0ebeb;
	color: #7e1500;
}


a,#absbot a,.button {
    color: #888888;
}

div.boardBanner>div.boardTitle { 
	font-family: Tahoma,sans-serif;
}


div.sideArrows {
	color: #7e1500;
}

div.reply {
   background-image: url("https://i.imgur.com/7Uv6rYR.png");
	color: #510d00;
	-moz-border-radius: 0px;
	-webkit-border-radius: 0px;
	border-radius: 0px;
}

div.post div.postInfo span.postNum a {
	text-decoration: none;
    color: #510d00!important;
}

div.post div.postInfo span.postNum a:hover,.posteruid .hand:hover {
	color: #7e1500!important;
    text-shadow: 0px 0px 1px #7e1500;
}

div.op div.postInfo {
	padding: 5px;
	background-image: url("https://i.imgur.com/7Uv6rYR.png");
	max-width: 99.4%;
}

div.post div.postInfo span.nameBlock span.name {
	color: #510d00;
}

div.post div.postInfo span.nameBlock span.postertrip {
	color: #c35892;
}

.quoteLink,.quotelink,.deadlink {
	color: #510d00!important;
    text-shadow: 0px 0px 1px #510d00;
}

a.postlink {
    color: #901b05!important;
    text-shadow: 0px 0px 1px #901b05;
}

div.postingMode {
	background: none;
	color: #510d00;
}

div.pagelist {
	background-image: url("https://i.imgur.com/7Uv6rYR.png");
	border: none;
	color: #6e3c3c;
	border-radius: 0px;
}

div.pagelist a.selected {
	color: #7e1500!important;
}

div.pagelist div.cataloglink {
	border: none;
}

table.postForm>tbody>tr>td:first-child {
    background: none;
	color: #7e1500;
}

table.logForm>tbody>tr>td:first-child {
	background: none;
	color: #7e1500;
}

#postForm input[type=text],input[type=password],textarea {
	border: 1px solid #7e150066!important;
}

#postForm input[type=text]:focus,input[type=password]:focus,input:not([type]):focus,textarea:focus {
	border: 1px solid #7e150066!important;
}


/* nie wiem co to

.postblock {
	background-color: #EA8;
	color: #800;
	border: 1px solid #800;
}

.highlightPost {
    border: solid 2px;
}*/

.op:target, .op.highlight, .reply:target,.reply.highlight {
    border: 1px dashed #7e1500!important;
    padding: 2px;
}


.useremail:not(:hover) .name,.useremail:not(:hover) .postertrip {
	color: #252590!important;
	text-decoration: none!important;
}

.useremail:hover * {
	color: #002fff!important;
}

#quote-preview {
    background-image: url("https://i.imgur.com/7Uv6rYR.png");
	border: 1px solid #7e1500;
}

#captcha > td:nth-child(2) > div:nth-child(2) {
	border: 1px solid rgb(170, 170, 170)!important;
}

.blockquoteReply > div:nth-child(2) {
	border: 1px solid rgb(170, 170, 170)!important;
}

.captchaClickHere {
	background-image: url("https://i.imgur.com/84bh35r.png"), url("https://i.imgur.com/7Uv6rYR.png");

}

.reloadCaptcha {
	border: 1px solid rgb(170, 170, 170)!important;
}

.fastform > .blockquoteReply > .captchaBlock {
	border: 1px solid rgb(170, 170, 170)!important;
}

#menu {
	background-image: url("https://i.imgur.com/7Uv6rYR.png");
	color: #7e1500;
}

#menu .coll {
	background-color: #f0ebeb;
}

#menu .coll:hover {
	background-color: #fff;
}

#menu li a {
	color: #7e1500!important;
	text-decoration: none!important;
}

#menu li a:hover {
	text-decoration: none!important;
}

#menu h2 {
	background: none;
	color: #7e1500;
	border: none;
}

.skandalBox {
	background-image: url("https://i.imgur.com/7Uv6rYR.png");
	border: none;
    border-radius: 0px;
}

.skandalBox h2 {
	background: none;
	border: none;
    border-bottom: 1px solid #7e1500;
    border-radius: 0px;
	color: #7e1500;
}

.skandalBox button {
	border: 1px solid #7e1500;
	background: none;
    color: #7e1500;
    border-radius: 0px;
}

.skandalBox button:hover {
	background: rgba(0, 0, 0, .1);
    cursor: pointer;
}

table:not(#postForm)>thead>tr>td {
	background: none;
	color: #7e1500;
    border: none;
    border-bottom: 1px dotted white;
    border-right: 1px dotted white;
}
table:not(#postForm)>thead>tr>td:last-child {
    border-right: none;
}

table:not(#postForm)>tbody>tr>th {
	background: none;
	color: #7e1500;
    border: none;
}

table:not(#postForm):not(#fastReplyButtons) tbody tr:nth-of-type(odd) {
	background-color: rgba(0, 0, 0, .1);
}

textarea.fastReply {
	background-color: #FFF;
}

.box-outer {
	border: none;
}

.boxbar {
	background: rgba(0, 0, 0, 0.1);
	color: #7e1500;
}

.top-box {
	background-image: url("https://i.imgur.com/7Uv6rYR.png");
}

a:visited {
	color: #7e1500;
}

.content {
	color: #7e1500;
	background-image: url("https://i.imgur.com/7Uv6rYR.png")!important;
}

.content h3 {
	background: rgba(0, 0, 0, 0.1);
	border: none;
	color: #7e1500;
}
.boxcontent a {
    color: #7e1500;
    text-shadow: 0px 0px 1px #7e1500;
}
.boxcontent a:hover {
	text-shadow: 0px 0px 1px #ff2a00;
}

.lnkSmall {
	color: #7e1500;
}

.lnkSmall:hover {
	color: #7e1500 !important;
	text-decoration: underline;
}
.lnkSmall:visited {
	color: #000000;
}

.postnew {
    border: 1px dotted #7e1500;
}

#settingsDiv {
	background: #f1e3ca;
	border: 1px solid #000;
}

#settingsTitle {
	background: #EDCF8E;
}

#watcher_box {
	border: solid 1px #7e1500;
	background-image: url("https://i.imgur.com/7Uv6rYR.png");
}
#watcher_box a:hover {
    text-shadow: 0px 0px 1px #7e1500;
}

#watcher_title {
	background: rgba(0, 0, 0, 0.1);
}

div.sub-menu {
	box-shadow: 0px 3px 3px rgba(0, 0, 0, 0.1);
	background-image: url("https://i.imgur.com/7Uv6rYR.png");
    color: #7e1500;
}

.modal-cont {
	background-image: url("https://i.imgur.com/MfSUi4M.png")!important;
}

.modal-cont label {
	color: #7e1500!important;
}

.modal-cont input[type="button"] {
	background: none;;
	border: 1px solid #7e1500!important;
	color: #000!important;
}

.modal-cont input[type="button"]:hover {
	background: rgba(0, 0, 0, 0.1);
}

#menu .modal-nav li {
	border-right: 1px solid #7e1500;
	border-bottom: 1px solid #7e1500;
	background-image: url("https://i.imgur.com/7Uv6rYR.png");
}

#menu .modal-nav li:hover {
	background-image: url("https://i.imgur.com/MfSUi4M.png");
}

.tab-opened {
    background-image: url("https://i.imgur.com/MfSUi4M.png")!important;
}

.group-options a:hover, .group a:hover {
	background-color: rgba(0, 0, 0, 0.1)!important;
}

.fastform #captchaField {
	border: 1px solid #AAA;
}

#fastReplyWindow {
    background-image: url("https://i.imgur.com/7Uv6rYR.png")!important;
}

.fastform {
	background: none;
}

.ui-corner-all {
	background: none;
	border: none;
    border-radius: 0px;
}

.ui-widget-header {
	background-image: url("https://i.imgur.com/7Uv6rYR.png");
	color: #7e1500;
	border: none;
}

.ui-state-default {
	border: none!important;
}

button.ui-button.ui-widget.ui-state-default.ui-corner-all.ui-button-icon-only.ui-dialog-titlebar-close {
    background: none;
}

#charsCounterNormal {
	color: #7e1500;
}

#charsCounterFastReply {
	color: #7e1500;
}

.postOptions {
    color: #7e1500;
	border: none;
    background-image: url("https://i.imgur.com/7Uv6rYR.png");
}

.postOptions a:hover {
	color: #7e1500;
	background-image: url("https://i.imgur.com/MfSUi4M.png");
}

s {
    background: black;
    color: white;
    text-decoration: none;
}

blockquote > span.quote {
    color: #335d00;
}
}
