css:
@-moz-document url-prefix("https://www.douban.com/group") {
a:visited {
    color: #75b717;
}
a:link {
    color: #bbb;
}
body {
    background:#121212;
    color:#bbb;
}
h1, h2 {
    color:#fff;
}
body #db-global-nav {
    height:50px;
    background:#232323;
    border-bottom: 1px dashed #313131;
}
body #db-global-nav .global-nav-items li {
    line-height: 50px;
}
body #db-global-nav .global-nav-items a {
    height:initial;
    padding: 0 24px;
    transition: all 0.15s;
}
body #db-global-nav .global-nav-items a:hover {
    background-color:#464646;
}
body #db-nav-group, body #db-nav-sns {
    background: #1C1C1C;
}
body #db-nav-group .nav-logo, body #db-nav-sns .nav-logo {
    background-image:none;
}
body .nav .nav-logo a {
    line-height: 52px;
    font-size: 28px;
    color: #eee;
    font-style: italic;
}
#db-nav-group a:link, #db-nav-group a:visited {
    color:#eee;
}
body #wrapper {
    background: #242424;
    padding: 35px;
    border-radius: 3px;
    box-shadow: 0 0 20px 6px black;
    border: 1px dashed #00560b;
}
body .group-board {
    color: #bbb;
    background: #151515;
}
body .group-intro {
    color:#bbb;
}
body .group-intro a:link {
    color:#00560b;
}
.group-misc {
    color:#fff;
}
#group-info h1 {
    color:#fff;
}
.nav-search .inp input {
    background:#121212;
}
.topic-content .topic-doc .topic-richtext p, .topic-content .topic-doc .topic-richtext blockquote, .topic-content .topic-doc .topic-richtext .DRE-unstyled {
    color:#bbb;
}
.back-to-top {
    left:0 !important;
    right:0 !important;
    margin: auto;
    bottom: 36px !important;
    opacity: 0.8;
    overflow: visible;
}
.back-to-top a {
    position: absolute;
    left: 0;
    background: #75b717;
    width: 70px;
    height: 70px;
    border-radius: 70px;
    color: #fff !important;
    text-align: center;
    line-height: 70px;
}
.topic-reply li {
    background: #2b2b2b;
    padding: 15px;
    border-radius: 3px;
    border: 1px dashed #00560b;
}
.topic-reply li .reply-doc h4 {
    margin: 0 0 15px 3px;
    padding-left: 3px;
}
.bg-img-green h4 {
    background-color: #333;
}
.bg-img-green {
    background: #00560b;
}
.popular-hd {
    background: #2b2b2b;
    border: 1px dashed #ff4d4d;
    border-bottom: none;
}
.popular-bd {
    border: 1px dashed #ff4d4d;
}
.comment-wrapper textarea {
    background: #3a3a3a;
    color:#ddd;
    padding: 10px;
    width: 100%;
}
.comment-wrapper {
    border:none;
    padding:0;
}
}
