css:
@-moz-document url-prefix("http://darksouls.wikidot.com/"), url-prefix("http://demonssouls.wikidot.com/") {
/*
	Bunny (Gwyndolin-chan)
	https://github.com/gwyndolin-chan
	public.hydra.chan@gmail.com
*/
h1 a, h2, #container, #header, #side-bar, #header-extra-div-1, #header-extra-div-2, #header-extra-div-3 {
	background-color: #101010 !important;
}

iframe.html-block-iframe {
	background: none;
	background-color: #404040;
}

img.image {
	border: 1px solid #909090;
	/*background-color: #000000;*/
}

.tr {
	background: none !important;
}

body, #toc {
	color: #909090;
}

a, #login-status a, #toc-list a, #toc #toc-action-bar a {
	color: #9090F0;
}

a:hover, #login-status a:hover, #toc-list a:hover, #toc #toc-action-bar a:hover {
	color: #6060F0;
}

table.wiki-content-table {
	font-size: 0.9em;
	color: #a0a0a0;
	background-color: #202020;
}

table.wiki-content-table th {
	font-size: 1.0em;
	color: #101010;
	background-color: #606060;
}

input.text {
	background-color: #C0C0C0;
}

#toc #toc-action-bar::before, #toc #toc-action-bar a::before, #toc #toc-action-bar a::after {
	color: inherit;
}

#top-bar ul li, #top-bar ul li ul li {
	border-color: #F0F0F0;
}

#top-bar ul li ul li {
	background-color: #909090;
}
}
