css:
@-moz-document domain("brick-hill.com") {
#welcome {
    /* float: left; */
    /* margin: 5px 20px 20px 20px; */
    color: white;
    text-align: left;
    font-size: 16px;
    font-weight: 600;
    background: #77b9ff;
border: none!important;
    padding: 2px;
    padding-left: 5px;
    padding-right: 5px;
    position: fixed;
    z-index: 3;
    margin-top: -25px;
    margin-left: 1px;
}
#banner {
    height: 0px!important;
    background-image: none;
    padding-top: 25px;
}
#info ul {
    padding: 0px;
    list-style: none;
    display: -webkit-inline-box;
    margin-top: 0px;
}
#info span[style="display:inline-block;float: left;margin-left: -5px;"] {
float: right!important;
}
#info li {
margin-right: 5px;
    margin-left: 5px;
}
#navlower {
     background: url(https://i.imgur.com/Bz4sxyE.png);
    background-position: center -39px;
    background-size: 65%;
    background-repeat: repeat-y;
    
    text-align: center;
    border: 1px solid black;
    border-top: 0;
    padding: 4px;
    font-size: 16px;
    background-color: #99CBFF;
    color: #fff;
    position: fixed;
    padding-top: 23px;
    width: 69.7%;
    transition: .3s ease-in;
    z-index: 1;
}
div#navlower:hover {
    padding-top: 46px;
}
#navbar {
    background: url(https://i.imgur.com/Bz4sxyE.png);
    background-position: center -39px;
    background-size: 65%;
    background-repeat: repeat-y;

    color: #fff;
    font-size: 16px;
    font-weight: bold;
    border: solid #000000;
    border-width: 0px 1px 1px 1px;
    background-color: #77B9FF;
    margin: 0px;
    padding-left: 0px;
    padding-right: 0px;
    text-align: left;
    position: fixed;
width: 70.3%;
    z-index: 2;
}

#navbar a[href*="/user?id="]:first-of-type:before {
background-image: url('https://alpha.brick-hill.com/favicon.png');
content: '......|||';
    background-size: 20px;
    background-repeat: no-repeat;
    color: transparent;
    z-index: -6;
    
}
#navbar a[href*="/user?id="]:first-of-type:after {
color: white;
    content: '|';
    position: absolute;
    margin-left: -42px;
    margin-top: 0px;
}
#body {
margin-top: 75px;
}
#abutton {
    margin-top: 75px;
    margin-bottom: -65px;
}
#info {
     background: url(https://i.imgur.com/Bz4sxyE.png);
    background-position: center -16px;
    background-size: 65%;
    background-repeat: repeat-y;
    
    background-color: #77B9FF;
    border: #000000 1px solid;
    padding-right: 0px;
    color: white;
    width: 70.3%;
    text-align: left;
    font-size: 14px;
    float: left;
    margin-top: -26px;
    height: 24px;
    margin: -26px;
    padding-left: 0px;
    margin-left: 0px;
    position: fixed;
}
}
