css:
@-moz-document domain("www.sickipedia.net") {
body {
	background: #aaaaaa;
	color: black;
}

.nav-tabs, .active, .dropdown-menu, .dropdown-toggle, .widget-head, .joke-heading, #feed .item .content, .marginNone, .widgetnav-tabs > li.active > a:hover, .widget {
	background-color: #eeeeee;
	color: #222222;
}

.categoryRightHeading, .noStyle {
	color: black;
}

.nav-tabs > li.active > a, .panel, .panel-default>.panel-heading, .nav-tabs > li.active > a:hover, .nav > li > a:hover, .nav-tabs > li.active > a, .open > .dropdown-menu a, .label-default {
	background-color: #cccccc;
	color: black;
}

.nav-tabs > li.active > a:hover, .nav > li > a:hover, .open > .dropdown-menu, .navbar-inverse, .dropdown-menu > li > a:focus, .dropdown-menu > li > a:hover {
	background-color: #999999;
	color: black;
	border: none;
}

input#mainSerachBox {
    box-shadow:  0 1px 3px 2px inset #eeeeee;
    background: white;
} 

.nav-tabs > li.active > a, .panel, .panel-default > .panel-heading, .widget, .nav-tabs > li.active > a:hover, .nav > li > a:hover, .nav-tabs > li.active > a, .open > .dropdown-menu a {
    color: #333333;
} 

.dropdown-menu > li > a {
    color: #333333 !important;
} 

.dropdown-menu > li > a:hover {
    border: none;
    border-top: none;
} 

.nav-tabs > li.active > a:hover, .nav > li > a:hover, .open > .dropdown-menu, .navbar-inverse, .dropdown-menu > li > a:focus, .dropdown-menu > li > a:hover {
	background-color: #999999;
	color: black;
	border: none;
}

.nav-tabs > li > a {
	margin-right: 2px;
	line-height: 1.42857143;
	border: none;
	border-radius: 0;
    color: black;
}
}
