css:
@-moz-document regexp("http.*\\.shotgunstudio\\.com.*") {
div.sgw_new_grid div.sg_no_entities_returned {
	background-color: /*[[bg-mid]]*/;  
	color: /*[[text-color]]*/;
}
.sgw_new_grid .body td.sg_cell, .sgw_new_grid .body .row_selector, .gw_new_grid .body .row_entity_icon {
	border-color: /*[[bg-dark]]*/;
	border: none;
}
div.sgw_new_grid .body td.sg_cell, div.sgw_new_grid .body div.row_selector, div.sgw_new_grid .body div.row_entity_icon {
	border-color: /*[[bg-dark]]*/ !important;
	border: none;
}
div.sgw_new_grid td.sg_cell {
	border-color: /*[[bg-dark]]*/;
	border: none;
}
div.sgw_new_grid td.sg_cell {
	background-color: /*[[bg-mid]]*/;    
}
.sg_cell {
	background: /*[[bg-mid]]*/;
	background-color: /*[[bg-mid]]*/;
	color: /*[[text-color]]*/;
	text-decoration: none;
}
.sg_cell a {
	text-decoration: none;
}

.sg_page > .contents > .body {
	background: /*[[bg-mid]]*/;   
}
div.entity_query_page_toolbar {
	background-color: /*[[bg-mid]]*/;
}
.sg_button {
	background-color: /*[[bg-light]]*/;
	box-shadow: none;
	color: /*[[text-color]]*/;
	background-image: none;
	border-color: rgba(0,0,0,0);
}
div.sg_page > .contents > div.canvas_header {
	background-color: /*[[bg-mid]]*/;
}
div.sgw_page_title, #sg_global_nav div.project_nav {
	background-color: /*[[bg-mid]]*/;
	color: /*[[text-color]]*/;
	border-color: /*[[bg-dark]]*/;
}
div.sgw_new_grid div.header, div.sgw_new_grid table.header_table {
	background-color: /*[[bg-mid]]*/;
	color: /*[[text-color]]*/;
}
div.sgw_new_grid {
	background: /*[[bg-dark]]*/;
}
div.sgw_entity_query_entity_query_page > div.contents > div.footer {
	background: /*[[bg-mid]]*/;
	color: /*[[text-color]]*/;
	border-color: /*[[bg-dark]]*/;
}

div.sgw_new_grid td.heading, div.sgw_new_grid div.header {
	border-color: /*[[bg-dark]]*/;
}
div.sgw_new_grid div.row.selected td.sg_cell[cm_id], div.sgw_new_grid div.row.selected div.row_entity_icon {
	background-color: /*[[accent]]*/ !important;
}
div.sgw_new_grid td.heading[sorting] {
	background-color: /*[[accent]]*/;
}
div.sgw_new_grid div.header.double_height td.heading div.pivot_headers.minor_header {
	background-color: /*[[bg-mid]]*/;
}
div.sgw_new_grid div.row_selector {
	background-color: /*[[bg-mid]]*/;
	border-color: /*[[bg-mid]]*/;
}
div.sgw_new_grid div.row_entity_icon {
	background-color: /*[[bg-mid]]*/;
}
div.sgw_new_grid td.heading.selected, div.sgw_new_grid td.heading.selected[sorting], div.sgw_new_grid div.header.double_height td.heading.selected[sorting], div.sgw_new_grid td.column_management.selected, div.sgw_new_grid td.pivot_heading.selected {
	background-color: #797979 !important;
}

/* project name */
#sg_global_nav div.project_name {
	text-shadow: none;
	color: /*[[text-color]]*/;
}
/* project nav */
#sg_global_nav div.project_nav div.nav_item {
	color: /*[[text-color]]*/;
}
#sg_global_nav .button:hover {
	background: none !important;
}
#sg_global_nav a.button_link:hover {
	color: white;
}
#sg_global_nav div.project_nav div.nav_item.selected, #sg_global_nav div.project_nav div.nav_item.active div.overlay_button, #sg_global_nav div.project_nav div.nav_item.active div.button {
	background-color: #444444;
}

div.sgw_new_grid td.heading div.content div.display_name, div.pivot_headers.minor_header div.header_name_box div.display_name {
	color: /*[[text-color]]*/;
}
div.pivot_headers > div.header_name_box div.display_name {
	color: /*[[bg-mid]]*/ !important;
}

/* search */
div.sgc_toolbar div.sgc_quick_filter div.quick_filter div.quick_search_input {
	border: none;
	background: /*[[text-color]]*/;
}
div.sgw_new_grid td.pivot_bar {
	opacity: 0.5;
}
td[sorting~=asc] {
	background-color: #444444 !important;
}

/* group header */
div.sgw_new_grid div.group table.group_header_table td.group_header_cell.first {
	color: /*[[text-color]]*/;
}

/* detail page */
div.sgw_stream_detail div.detail_wrapper > div.header {
	background-color: /*[[bg-mid]]*/;
	color: /*[[text-color]]*/;
}
div.sgc_toolbar div.toolbar_item div.latest_version_checkbox {
	text-shadow: none;
	color: /*[[text-color]]*/;
}

/* overview */
div.sgw_project_overview_page div.body > table {
	background-color: /*[[bg-dark]]*/;
	color: /*[[text-color]]*/;
}
div.sgc_activity_stream div.update.block div.update_body_wrapper, div.sgc_activity_stream_note_thread div.thread_body, div.sgw_project_overview_page div.body div.left_pane div.left_pane_header {
	background-color: /*[[bg-mid]]*/;
	color: /*[[text-color]]*/;
	border-color: /*[[bg-dark]]*/;
}
div.sgc_activity_stream_note_thread div.note_link {
	background-color: /*[[accent]]*/;
}
div.sgw_project_overview_page div.body td.right_pane {
	border-color: /*[[bg-dark]]*/;
}
div.sgw_new_grid div.row_even td.sg_cell {
	background-image: none;
	background: rgba(255,255,255,0.09);
}
div.sgw_new_grid div.row_odd td.sg_cell {
	background-image: none;
	background: rgba(255,255,255,0.05);
}
div.sgw_project_overview_page {
	background-color: /*[[bg-dark]]*/;
}
/* canvas page */
div.sg_page > .contents > .body.sgw_canvas_body {
	background-color: /*[[bg-mid]]*/;
	color: /*[[text-color]]*/;	
}
div.sgw_canvas_wrapper > div.body.border, div.sgw_entity_query_entity_query_page > div.sgc_footer {
	border-color: /*[[bg-light]]*/;
}
/* graphs */
div.sgw_graph div.pie_graph, div.sgw_graph div.vert_graph, div.sgw_graph div.horiz_graph, div.sgw_graph .sg_reset_table, .sg_reset_table {
	background-color: /*[[bg-mid]]*/;
	color: /*[[text-color]]*/;	
}
tspan {
	fill:  /*[[text-color]]*/;	
}
/* footer */
div.sgw_entity_query_entity_query_page > div.sgc_footer {
	background-color: /*[[bg-mid]]*/;
	color: /*[[text-color]]*/;		
}
div.height_drag_bar {
	background-color: #444444 !important;
}
/* form */
div.form_header, div.form_body, div.form_footer {
	background-color: /*[[bg-mid]]*/ !important;
	color: /*[[text-color]]*/;
	border: none;
}
div.sg_new_entity_form.sg_dialog div.form_header > div.dialog_drag_div {
	background-color: /*[[bg-dark]]*/ !important;
}
/* inbox */
div.sgw_inbox div.inbox_contents_header, div.sgw_inbox div.right_pane,
div.sgw_my_tasks div.my_tasks_header, div.sgw_my_tasks div.right_pane, 
div.sgw_entity_query_mode {
	background-color: /*[[bg-mid]]*/ !important;
	color: /*[[text-color]]*/;
	border-color: /*[[bg-dark]]*/;
}
div.sgw_inbox div.left_pane, div.sgw_my_tasks div.left_pane, div.sgw_entity_query_mode div.left_pane {
	background-color: /*[[bg-dark]]*/ !important;
	color: /*[[text-color]]*/;
	border-color: /*[[bg-dark]]*/;
}
div.sgw_my_tasks div.left_pane div.task {
	background-color: /*[[bg-mid]]*/;
	color: /*[[text-color]]*/;
	border-color: /*[[bg-dark]]*/;
}
div.sgw_my_tasks div.left_pane div.task.selected {
	background-color: /*[[accent]]*/;
}
div.sgw_my_tasks div.sgc_grouped_list div.group_header {
	background-color: /*[[bg-dark]]*/;
	color: /*[[text-color]]*/;
}
div.sgw_update_detail, div.sgc_task_card_renderer div.task_name, div.sgc_task_card_renderer div.link_entity_name {
	background: none;
	color: /*[[text-color]]*/;
	border-color: /*[[bg-dark]]*/;
}
div.sgw_my_tasks div.sgc_grouped_list div.floating_group_header div.group_header, div.sgw_my_tasks div.left_pane div.task:last-child {
	border-color: /*[[bg-dark]]*/;
}
/* unread */
div.sgc_inbox_contents div.update.unread, div.sgc_update_detail div.header, div.sgc_scroll_header_floating_header.update_scroll_header {
	background-color: /*[[bg-dark]]*/ !important;
	color: #e8e8e8 !important;
	border-color: /*[[bg-dark]]*/;
}
div.sgc_follow_button {
	background-color: /*[[accent]]*/;
}
/* thumbnail view */
div.sgw_entity_query_entity_query_page[filter_panel_docked=no] > div.contents > div.sgw_entity_query_mode {
	background-color: /*[[bg-dark]]*/;
}
div.sgw_thumbnail_grid div.group_header {
	background-color: /*[[bg-mid]]*/;
	color: /*[[text-color]]*/;
}
div.sgw_thumbnail_grid li.entity > div.content > div.thumbnail_grid.sg_cell {
	background-color: /*[[bg-dark]]*/;
}
div.sgw_thumbnail_grid li.entity > div.content > table.fields td.cell {
	border-color: /*[[bg-dark]]*/;
}
div.entity_query_page_toolbar, div.sgw_thumbnail_grid {
	border-color: /*[[bg-dark]]*/;
}
div.sgw_stream_detail div.body_content, div.sgw_stream_detail div.sidebar_wrapper, div.sgc_stream_detail_info_fields div.sgc_fields td {
	background-color: /*[[bg-mid]]*/;
	color: /*[[text-color]]*/;
	border-color: /*[[bg-dark]]*/;
}
div.header {
	border-color: /*[[bg-dark]]*/ !important;
}
.sg_cell_content {
	border: none !important;
}
td.pivot_heading {
	border-color: /*[[bg-dark]]*/ !important;
}
.sgw_new_grid.sg_scroll_container::-webkit-scrollbar, .sgw_new_grid.sg_scroll_container::-webkit-scrollbar-corner {
	background: /*[[bg-dark]]*/;
}
.sgw_new_grid.sg_scroll_container::-webkit-scrollbar {
	width: 12px;
	height: 12px;
}
.sgw_new_grid.sg_scroll_container::-webkit-scrollbar-thumb {
	background: #444444;
	border-radius: 10px;
}
}
