css:
@-moz-document url-prefix("https://kibana.") {
  /* Global*/
  body,
  .global-nav {
      color: #FFFFFF;
      background-color: #2d2d2d;
  }
  
  h1,
  h2,
  h3,
  h4,
  h5,
  h6 {
      color: #FFF;
  }
  
  /* Side Navigation Bar */
  .global-nav-link.active,
  .global-nav-link:hover,
  .sidebar-container {
      background-color: #585858;
  }
  
  /* Pink Links */
  a,
  a:active,
  .kuiLink,
  .kuiLink:visited,
  .kuiLink:active {
      color: #e9478b;
      text-decoration: none!important;
  }
  
  a:hover,
  .kuiLink:hover {
      color: #d96f9c;
      text-decoration: none!important;
  }
  
  a:visited {
      color: inherit;
  }
  
  /* Pink charts */
  rect[data-label=Count] {
      fill: #e9478b;
      stroke: #e9478b;
  }
  .legend-value-truncate > .fa {
      color: #e9478b!important;
  }
  
  /* Content areas */
  .application {
      background-color: #2d2d2d;
  }
  .app-wrapper {
      background-color: #2d2d2d;
  }
  
  /* Tables */
  .table .table,
  visualize-spy {
      background-color: #2d2d2d;
  }
  
  td {
      border-top: 1px solid #424242;
  }
  
  .agg-table-paginated tr:hover td,
  .agg-table-paginated tr:hover td .table-cell-filter {
      background-color: #424242;
  }
  
  .kbn-table dl.source dt,
  kbn-table dl.source dt,
  tbody[kbn-rows] dl.source dt {
      background-color: #585858;
      color: #b1b1b1;
      border-radius: 5px;
  }
  .table > tbody > tr > td,
  .table > tbody > tr > th,
  .table > tfoot > tr > td,
  .table > tfoot > tr > th,
  .table > thead > tr > td,
  .table > thead > tr > th {
      border-top: 1px solid #7f8182;
  }
  .table-cell-filter {
      border-radius: 1px;
  }
  .docTableRowFilterButton,
  .kuiModal,
  filter-editor > div {
      background-color: #2d2d2d;
      padding: 1px;
      border-radius: 1px;
  }
  
  /* Date picker and top links */
  .kuiLocalMenuItem {
      color: #FFF;
  }
  
  /* Search & filters */
  .filter-bar,
  .kuiLocalNav {
      background-color: #2d2d2d;
      border-bottom: 2px solid #000;
  }
  .filter-bar .filter-description {
      overflow: inherit;
  }
  .filter-bar .filter-description > a {
      padding: 3px 10px;
      border-radius: 4px;
  }
  
  /* Secondary sidebar */
  .sidebar-container {
      box-shadow: 4px 0 3px rgba(0, 0, 0, .2);
  }
  
  .sidebar-container,
  .sidebar-container .sidebar-well {
      background-color: #2d2d2d;
  }
  
  .sidebar-container .sidebar-list-header {
      color: #b1b1b1;
  }
  
  .sidebar-list {
      border: 2px;
      border-color: #000;
  }
  
  /* Tabs in records */
  .nav-tabs {
      border-color: #585858;
  }
  
  .nav > li > a,
  .nav-tabs > li > a {
      padding: 10px 15px;
      margin: 0;
      color: #fff;
      background-color: #585858;
      border-color: #585858;
      text-decoration: none;
  }
  
  .nav > li > a:hover,
  .nav > li > a:active,
  .nav-tabs > li.active > a,
  .nav-tabs > li.active > a:focus,
  .nav-tabs > li.active > a:hover {
      padding: 10px 15px;
      margin: 0;
      color: #fff;
      background-color: #656565;
      border: 0;
      text-decoration: none;
  }
  
  /* Default button state */
  .btn-default,
  .kuiButton--primary,
  .kuiLocalSearchInput,
  .kuiButton--basic,
  .kuiSelect,
  .form-inline .form-control,
  .form-control,
  .vis-editor-field-ui-select .ui-select-match .btn,
  a[role=button],
  .dropdown-menu,
  .ui-select-choices-row,
  .ui-select-bootstrap .ui-select-choices-row > span,
  .kuiTextInput,
  .vis-editor-subnav-link,
  .index-pattern {
      color: #fff;
      background-color: #585858;
      border-color: #585858;
      text-decoration: none;
  }
  
  /* Active or hovered button state */
  .btn-default:hover,
  .btn-default:active,
  .kuiButton--primary:enabled:hover,
  .kuiButton--primary:enabled,
  .kuiButton--primary:hover,
  .kuiButton--primary:active,
  .kuiButton--basic:enabled:hover,
  .kuiButton--basic:enabled,
  .kuiButton--basic:hover,
  .kuiButton--basic:active,
  .sidebar-container .sidebar-list .sidebar-item .active,
  .ui-select-bootstrap .ui-select-choices-row.active > span,
  .kuiTextInput:active,
  .kuiTextInput:focus,
  .vis-editor-subnav-link:active,
  .vis-editor-subnav-link:focus,
  .vis-editor-subnav-link:hover {
      color: #fff;
      background-color: #656565;
      border: 0;
      text-decoration: none;
  }
  
  /* Disabled button state */
  .kuiButton--primary:disabled,
  a.kuiButton--primary.kuiButton-isDisabled {
      color: #fff;
      background-color: #383838;
      border-color: #424242;
  }
  
  /* Icons */
  .kuiIcon {
      color: #fff;
  }
  
  /* Simplify styles with inheritance overrides */
  .vis-editor-sidebar .sidebar-container,
  .navbar-default,
  .kibanaWelcomeView {
      background: inherit!important;
  }
  .navbar-default .navbar-nav > li > a,
  .navbar-default .navbar-text,
  .vis-editor-subnav-link {
      color: inherit!important;
  }
  }
