/* Profile colour selection */
.color[style="background: rgb(61, 180, 242);"] {background: rgba(var(--color-blue)) !important;}
.color[style="background: rgb(192, 99, 255);"] {background: rgba(var(--color-purple)) !important;}
.color[style="background: rgb(76, 202, 81);"] {background: rgba(var(--color-green)) !important;}
.color[style="background: rgb(239, 136, 26);"] {background: rgba(var(--color-orange)) !important;}
.color[style="background: rgb(225, 51, 51);"] {background: rgb(214,78,78) !important;}
.color[style="background: rgb(252, 157, 214);"] {background: rgba(var(--color-pink)) !important;}
.color[style="background: rgb(103, 123, 148);"] {background: rgba(var(--color-gray)) !important;}

/* Placeholder text */
input::placeholder, textarea::placeholder {color: rgba(var(--placeholder)) !important;}

/* Nav */
#nav .links {border-left: none !important; margin-left: 0 !important; padding-left: 0 !important; width: 100% !important;}
.nav.transparent, #app #nav.nav.transparent {background: rgba(var(--color-black),.5) !important;}
.nav.transparent:hover, #app #nav.nav, #app #nav.nav:hover {background: rgba(var(--color-black)) !important;}
.nav .wrap .link:not(.router-link-active) {color: rgb(206,212,219) !important;}
.nav .wrap .link:hover:not(.router-link-active) {color: rgba(var(--color-text-white)) !important;}
#app #nav.nav {border-top: 3px solid rgba(var(--color-blue)) !important; z-index: 1002;}
.nav .user .avatar {border-radius: 50% !important;}
.nav .user .chevron, .nav .search svg {color: rgba(var(--color-text-lighter)) !important;}
.nav .search svg {opacity: .5; transition: opacity .3s ease;}
.nav .search:hover svg {opacity: 1;}
.nav .router-link-active {color: rgba(var(--color-blue)) !important; font-weight: bold !important;}
.user .notification-dot {background: rgba(var(--color-pink)) !important; color: rgba(var(--color-text-white)) !important;}

/* Nav icon colours */
.user-wrap .dropdown .fa-user {color: rgba(var(--color-blue)) !important;}
.user-wrap .dropdown .fa-envelope {color: rgba(var(--color-green)) !important;}
.user-wrap .dropdown .fa-cog {color: rgba(var(--color-orange)) !important;}
.user-wrap .dropdown .fa-sign-out-alt {color: rgba(var(--color-pink)) !important;}
.browse-wrap .primary-links svg {color: rgba(var(--color-blue)) !important;}

/* Quick search */
.quick-search .result, .quick-search .result a, .quick-search .result .info, .quick-search .result .open-list-editor {transition: none !important;}

/* Left panels */
.filter-group, .settings .nav-group, .sidebar .tags {
    background: rgba(var(--color-foreground)) !important;
    padding: 15px !important;
    border-radius: 3px !important;
    box-shadow: var(--box-shadow) !important; }
.forum-feed .filter-group a, .nav-group a[href^="/settings/"], .sidebar .tags .tag a {transition: none !important;}
.sidebar .tags .tag a:hover, .sidebar .tags .tag:hover .rank {color: rgba(var(--color-text-white)) !important;}
.search .filter-group .link {background: rgba(var(--color-foreground-blue)) !important;}
.search .filter-group .link, .filter-group .link, .forum-feed .filter-group a, .nav-group a[href^="/settings/"] {padding: 8px !important;}

/* Left (Active and hovered) */
.search .filter-group .router-link-active, .filter-group .link.active,
.nav-group .router-link-active, .filter-group .router-link-exact-active,
.site-theme-dark .filter-group span.active, .filter-group a:hover, .search .filter-group .link:hover,
.filter-group .link:hover, .nav-group a[href^="/settings/"]:hover, .sidebar .tags .tag:hover {
    background: rgba(var(--color-blue-swap)) !important;
    color: rgba(var(--color-text-white)) !important; }

/* Left (Dark theme adjustments) */
.site-theme-dark .search .filter-group:nth-child(1) .router-link-active,
.site-theme-dark .search .filter-group:nth-child(1) .link:hover {
    background: rgba(var(--color-foreground-light)) !important;
    color: rgba(var(--color-text-lighter)) !important; }

/* Left (Search and filter boxes) */
input[placeholder="Search"], input[placeholder="Filter"] {background: rgba(var(--color-foreground)) !important; box-shadow: var(--box-shadow);}

/* Left (Year slider) */
.el-slider__runway {background: rgba(var(--color-foreground-blue)) !important; margin: 12px 5% !important; width: 90% !important;}
.el-slider__button {background-color: rgba(var(--color-bw)) !important;}

/* Left (Dropdowns) */
.el-select-dropdown__item.selected {color: rgba(var(--color-blue)) !important;}
.el-select-dropdown__item.selected:hover {color: rgba(var(--color-text-white)) !important;}
.el-select-dropdown__item.hover {background: rgba(var(--color-blue)) !important; color: rgba(var(--color-text-white)) !important;}
.filter-group .el-input__inner::placeholder {color: rgba(var(--color-text-white)) !important; font-style: normal !important;}
.search .el-select .el-input__inner, .site-theme-dark .medialist .el-select .el-input__inner {
    background: rgba(var(--color-blue-swap)) !important;
    color: rgba(var(--color-text-white)) !important;
    font-style: italic; }
.search .el-select .el-input .el-select__caret, .medialist .el-select .el-input .el-select__caret {color: rgba(var(--color-text-white)) !important;}

/* Left (Anime/Manga tags) */
.tags .vote-dropdown .el-dropdown-link {color: rgba(var(--color-text-white)) !important; font-size: 12px !important;}
.sidebar .tags .spoiler-toggle {font-size: 1rem !important; letter-spacing: 1px; text-align: center;}

/* Success message */
.el-message--success {background: rgba(var(--color-green)) !important; border-color: rgba(var(--color-green)) !important;}
.el-message--success .el-message__content, .el-message .el-icon-success {color: rgba(var(--color-text-white)) !important;}

/* Theme buttons */
.theme-preview {border: none !important; color: var(--color-text) !important;}

/* Checkboxes */
.el-checkbox__input.is-checked .el-checkbox__inner, .el-radio__input.is-checked .el-radio__inner {
    background-color: rgba(var(--color-blue)) !important;
    border-color: rgba(var(--color-blue)) !important; }
.el-radio__input.is-checked+.el-radio__label {color: rgba(var(--color-blue)) !important;}
.el-checkbox.is-bordered.is-checked {border-color: rgba(var(--color-blue)) !important;}
.el-checkbox__input.is-checked+.el-checkbox__label {color: rgba(var(--color-blue)) !important;}

/* Star colour */
.rated .icon {color: rgba(var(--color-purple)) !important;}

/* Tags */
.categories .category {border-radius: 3px !important; padding: 8px !important;}
.categories .category:hover {color: rgba(var(--color-text-white)) !important; opacity: 0.8;}
.categories .category[href$="category=1"] {background: rgba(var(--color-blue)) !important;} /* Anime */
.categories .category[href$="category=2"] {background: rgba(var(--color-green)) !important;} /* Manga */
.categories .category[href$="category=3"] {background: rgba(var(--color-purple)) !important;} /* Light Novels */
.categories .category[href$="category=4"] {background: rgba(var(--color-purple),0.75) !important;} /* Visual Novels */
.categories .category[href$="category=5"] {background: rgba(var(--color-orange),0.75) !important;} /* Release Discussion */
.categories .category[href$="category=7"] {
    box-shadow: inset 0 0 0 2px rgb(222,222,222) !important;
    background: none !important;
    color: rgba(var(--color-text)) !important; } /* General */
.site-theme-dark .categories .category[href$="category=7"] {box-shadow: inset 0 0 0 2px rgb(111,111,111) !important;}
.categories .category[href$="category=8"] {background: rgba(var(--color-pink),0.75) !important;} /* News */
.categories .category[href$="category=9"] {background: rgb(125,125,125) !important;} /* Music */
.categories .category[href$="category=10"] {background: rgb(175,175,175) !important;} /* Gaming */
.categories .category[href$="category=11"] {background: linear-gradient(to right, rgba(var(--color-pink),0.25) 0%,
                                                        rgba(var(--color-pink),0.50) 33%, rgba(var(--color-pink),0.75) 66%,
                                                        rgba(var(--color-pink),1) 100%) !important; } /* Site Feedback */
.categories .category[href$="category=12"] {background: rgba(var(--color-pink)) !important;} /* Bug Reports */
.categories .category[href$="category=13"] {background: rgba(var(--color-pink),0.75) !important;} /* Site Announcements */
.categories .category[href$="category=14"] {background: rgb(0,0,0) !important;} /* List Customisation */
.categories .category[href$="category=15"] {background: rgba(var(--color-orange)) !important;} /* Recommendations */
.categories .category[href$="category=16"] {background: linear-gradient(to right, rgba(var(--color-blue),0.25) 0%,
                                                        rgba(var(--color-pink),0.50) 50%,
                                                        rgba(var(--color-purple),0.75) 100%) !important; }  /* Forum Games */
.categories .category[href$="category=17"] {background: rgba(var(--color-gray)) !important;} /* Misc */
.categories .category[href$="category=18"] {background: rgb(50,50,50) !important;} /* AniList Apps */
.categories .category.media.ANIME {background: rgba(var(--color-blue),0.65) !important;} /* Specific Anime */
.categories .category.media.MANGA {background: rgba(var(--color-green),0.65) !important;} /* Specific Manga */

/* Graph colours */
.ct-series-a .ct-bar, .ct-series-a .ct-line, .ct-series-a .ct-point, .ct-series-a .ct-slice-donut {stroke: rgba(var(--color-blue)) !important;}
.ct-series-a .ct-area {fill: rgba(var(--color-blue),0.5) !important;}

/* Brighter 'No Image' */
a[style*="default.jpg"], img[src*="default.jpg"], .image[style*="default.jpg"] {filter: invert(1) brightness(1.4) grayscale(100%);}
.site-theme-dark a[style*="default.jpg"], .site-theme-dark img[src*="default.jpg"], .site-theme-dark .image[style*="default.jpg"] {filter: grayscale(100%);}

/* Brighten Banner (Editing list entries) */
.list-editor-wrap .header {box-shadow: inset 0 0 250px rgba(var(--color-bw)) !important;}
.list-editor-wrap .header:after {background: none !important;}
.list-editor-wrap .header .title {font-size: 1.8rem !important; letter-spacing: 2px !important; text-shadow: 1px 1px 4px #181818;}

/* Characters & Staff (Hide black edges from MAL images) */
.character .cover, .staff .cover {background-size: 105% !important;}

/* Rankings */
.rankings .ranking, .rankings-wrap .ranking {padding: 7px 12px 9px !important;}

/* Blockquotes */
.markdown blockquote {
    background: rgba(var(--color-bw)) !important;
    border-left: solid 7px rgba(var(--color-blue)) !important;
    box-shadow: var(--box-shadow);
    color: rgba(var(--color-text)) !important; }

/* Code */
code {
    background: rgba(var(--color-foreground-blue)) !important;
    border-radius: 3px !important;
    border: 1px solid rgba(var(--color-gray)) !important;
    color: rgba(var(--color-text-light)) !important;
    font-size: 1.3rem !important; padding: 0 2px !important; }
.site-theme-dark code {border-color: rgba(var(--color-text)) !important;}

/* Spoilers */
.markdown .markdown-spoiler:before {
    content: "Spoiler (Click to view)" !important;
    color: rgba(var(--color-text-white)) !important;
    background: rgba(var(--color-pink)) !important;
    font-size: 1.2rem !important;
    padding: 6px 8px 8px !important; }
.user .markdown .markdown-spoiler:before {background: rgba(var(--color-blue)) !important;}
.markdown .markdown-spoiler.spoiler-visible span {background: none !important;}
.markdown .markdown-spoiler.spoiler-visible i.hide-spoiler {color: rgba(var(--color-pink)) !important;}
.markdown .markdown-spoiler.spoiler-visible:hover i.hide-spoiler {opacity: 1 !important;}

/* Horizontal rule */
.markdown hr {border: solid 2px rgba(var(--color-blue),.5) !important;}

/* Bottom */
.logo, .page-content + .wrap .footer h2, .page-content + .wrap .footer .links, .wrap .footer-wrap {display: none !important;}
.page-content + .wrap .footer {min-height: auto !important; padding: 10px !important;}
.page-content + .wrap .footer .container {opacity: 0; transition: 0.5s;}
.page-content + .wrap .footer .container:hover {opacity: 1;}
.page-content + .wrap .footer {background: rgba(var(--color-black)) !important;}
}

@-moz-document url-prefix("https://anilist.co/search/"), url-prefix("https://anilist.co/studio/") {
/* Remove search background */
.search, .studio {background: none !important;}

/* Cover view */
.results .cover .list-btns-wrap {bottom: 60px !important;} /* Move hover edit options up */

/* Chart view */
.results .description {text-align: justify;}
.results.chart .media-card .overlay {background: rgba(var(--color-overlay),.8) !important;}
.results.chart .media-card .overlay:before {background: none !important;}

/* Table view */
.results.table .media-card .content {grid-template-columns: minmax(auto,calc(100% - 420px)) 130px 130px 130px !important;} /* More space for title */

/* Default browse page */
.search-landing {margin-top: -10px !important;}
.landing .title.link {
    background: rgba(var(--color-green)) !important;
    box-shadow: var(--box-shadow) !important;
    border-radius: 4px !important;
    padding: 10px 15px !important; }
.site-theme-dark .landing .title.link {background: rgba(var(--color-foreground)) !important;}
.landing .title.link h3 {font-family: Segoe UI !important; font-weight: normal !important; text-transform: none !important;}
.landing .title.link h3, .landing .title.link .expand {color: rgba(var(--color-text-white)) !important;}
.site-theme-dark .landing .title.link h3, .site-theme-dark .landing .title.link .expand {color: rgba(var(--color-text-lighter)) !important;}

/* Media cards (List status) */
.results.cover .media-card .title, .landing-section:not(.top) .media-card .title, .studio-page-unscoped .media-card .title {
    background: rgba(var(--color-overlay),.6) !important;
    border-radius: 3px 3px 0 0 !important;
    font-size: 1.3rem !important;
    height: 40px !important; width: 100% !important;
    margin-top: 0 !important; padding: 12px !important;
    position: absolute !important; top: 0 !important; z-index: 5;
    white-space: nowrap; overflow: hidden; text-overflow: ellipsis;
    display: block !important; /* Fixes ellipsis not working */ }
.media-card .title, .media-card a.title:hover {color: rgba(var(--color-text-white)) !important;}
.media-card .cover {border-radius: 3px 3px 6px 6px !important; box-shadow: inset 0 -28px 0 0 rgba(var(--color-foreground-blue));}
.media-card {box-shadow: var(--box-shadow) !important;}
.media-card .list-status {
    border-radius: 0 !important; box-shadow: none !important;
    height: 40px !important; width: 100% !important;
    position: absolute; left: 0 !important; top: 0 !important;
    opacity: .8; z-index: -1; }
.media-card .list-status[status=Watching], .list-btns-wrap .btn[status=Watching] {background: rgba(var(--color-green)) !important;}
.media-card .list-status[status=Completed], .list-btns-wrap .btn[status=Completed] {background: rgba(var(--color-blue)) !important;}
.media-card .list-status[status=Paused], .list-btns-wrap .btn[status=Paused] {background: rgba(var(--color-orange)) !important;}
.media-card .list-status[status=Dropped], .list-btns-wrap .btn[status=Dropped] {background: rgba(var(--color-pink)) !important;}
.media-card .list-status[status=Planning], .list-btns-wrap .btn[status=Planning] {background: rgba(var(--color-purple)) !important;}

/* Undo hover for info */
.hover-data {
    background: none !important;
    box-shadow: none !important;
    border-radius: 0 !important;
    min-width: inherit !important;
    top: inherit !important;
    padding: 0 !important;
    opacity: 1 !important;
    pointer-events: inherit !important;
    animation: none !important; }
.hover-data:before {display: none !important;}
.hover-data.left, .hover-data.right {height: 0 !important; margin: 0 !important; left: inherit !important; right: inherit !important;}

.hover-data .header {
    background: rgba(var(--color-overlay),.35) !important;
    color: rgba(var(--color-text-white)) !important;
    font-size: 1rem !important;
    margin-top: 40px !important;
    padding: 4px 12px !important;
    grid-template-columns: auto 40px !important; }
.hover-data .header .score .icon {display: none !important;}
.hover-data .header .score {text-align: right;}
.hover-data .header .date.airing {width: 120%;}

.hover-data .info {
    background: rgba(var(--color-foreground-grey-dark)) !important;
    color: var(--color-text);
    font-size: 1rem !important;
    position: relative; top: 151px !important;
    padding: 6px 12px !important; height: 22px;
    text-align: center; letter-spacing: .05em; }
.site-theme-dark .hover-data .info {background: rgba(35,35,35) !important;}

.hover-data .genres {
    background: rgba(var(--color-background)) !important;
    font-size: 1rem !important;
    position: relative; top: 129px !important;
    padding: 5px !important; height: 28px;
    border-radius: 0 0 6px 6px; }
.site-theme-dark .hover-data .genres {background: rgba(var(--color-foreground)) !important;}
.hover-data .genres .genre {
    background: none; color: var(--color-text);
    font-size: 1rem; font-weight: 600;
    height: 24px; line-height: 0;
    width: 50%; padding: 10px 0; margin-right: 0;
