css:
/* i really want this to be global */
.abcde.fghij.klmno{content:"null";}
@-moz-document url-prefix("https://www.kingkong.com.tw/") {

/* Version : 2018/07/04 19*/
/* 去掉金刚跑马灯 */
/*[[WinGameLight]]*/div.section.section-video > div.video > div.notice-box{display: none !important;}
/* 去掉自定义 banner */
/*[[DiyBanner]]*/div.view.view-live.theme-anchor.theme-fullscreen > div.head > div{display: none !important;}
/* 整体背景颜色 */
body > div.container{background : #1f222d;}
/* 恢复普通用户位置 */
body > div.container > div.view.view-live.theme-anchor.theme-fullscreen{margin-top: 84px;}
/* 恢复普通用户背景色 */
body > div.container > div.view.view-live.theme-anchor.theme-fullscreen{  background : rgba(0,0,0,0);}
/* 去掉热门推荐游戏 */
/*[[HotGames]]*/div.view.view-live> div.view-content > div.live-intro.hot-anchors-content.same-content{display: none !important;}
/* 页脚精简 */
body > div.container > div.footer{height: auto  !important;}
body > div.container > div.footer > div{padding: 10px !important;}
/* 行距 */
div.section.section-message > div > div.message-box>ul>li:nth-child(n){ margin: 1px;}
xdiv.section.section-video > div.gift-content > div.my-gold > div{    text-align: left;}
/* 去除顶端栏最上层显示 */
/*body > div.view-header{z-index:1 !important;}
body > div.view-header:hover{z-index:9800 !important;}*/
body > div.view-header{    position: absolute;}
/* 修改最上层显示间隔 */
body > div.container > div.view.view-live > div.view-content{margin-top: -10px;}
/* 向左移动 */
body > div.container > div.view.view-live > div.view-content{margin-right: 100px;}
/* 扩展左边聊天对话框 */
body > div.container > div.view.view-live > div.view-content > div.main > div.section.section-message{right: auto;width: 456px;height: 100%;}
/* 视频窗口间隔 */
body > div.container > div.view.view-live > div.view-content > div.main > div.section.section-video > div.video{    margin-top: 58px;margin-bottom: 58px;}
/* 显示数字ID */
/*[[ShowID]]*/div.section.section-message > div > div.message-box>ul>li:hover:after {
			border: 1px solid #fc0;
			padding-left: 0px;
            padding-right: 0px;
			background: #fffea1;
			content: "ID : "attr(data-id);
			font-size: 0.7em;
			position: absolute;
			left: 2px;
			top: -18px;
            z-index:30678;
		}
/* 聊天框位置修正 */
div.section.section-message > div > div.message-box{padding: 0 !important;}
/* 关闭金刚任务 */
/*[[KingKongTask]]*/.video>#player > div.view-gg-container{display: none !important;}
/* 聊天输入框宽度 */
div.section.section-message > div > form > div > input[type="text"]{width: 92.5% !important;padding: 0 !important;}
div.main > div.section.section-message > div > form > div > a{width: 7.5% !important;font-weight: 900 !important;}
/* 直播边框设置 */
div>div.video>div#player:hover{border:0.5px solid #000;} 
/* 屏蔽金刚公告 */
div.container > div.view.view-live > div.notice-boards{display: none !important;}
/* 抽奖公告优化 */
body > div.container > div.view.view-live > div.lottery-users-dialog > div.lottery-users-wrap,body > div.container > div.view.view-live.theme-anchor.theme-fullscreen > div.lottery-users-dialog > div.lottery-users-wrap > div.lottery-users-content > div{width: 666px;}
body > div.container > div.view.view-live > div.lottery-users-dialog > div.lottery-users-wrap > div.lottery-users-content > div > div.lottery-result-lists{padding-bottom: 10px;}
body > div.container > div.view.view-live > div.lottery-users-dialog > div.lottery-users-wrap > div.lottery-users-content > div > div.lottery-result-lists > div{width: auto;}
body > div.container > div.view.view-live > div.lottery-users-dialog > div.lottery-users-wrap > div.lottery-users-content > div > div.lottery-result-lists > div > ul > li:nth-child(n) > div.result-user-name{ width: auto;max-width: none;}
/* 抖内框设置 */
body > div.container > div.view.view-live > div.view-content > div.main > div.section.section-message > div > div.message-box{    margin: 147px auto 0;      height: calc(100% - 213px);  z-index: 9453;}
body > div.container > div.view.view-live > div.view-content > div.main > div.section.section-message > div > a{z-index: 9999;}
body > div.container > div.view.view-live > div.view-content > div.main > div.section.section-message > div > div.ranks-wrapper:hover{z-index: 10000;}
/* 用户追踪框置顶 */
body > div.container > div.view.view-live > div.view-content > div.main > div.section.section-message > div > div.user-info.active{z-index:11111;}
/* 优化金刚网页 */
