css:
@-moz-document domain("rinconmatematico.com") {
/* Estilo mezcla de tonos pasteles pero muy oscuro */
body {
    background-color: rgba(26,26,26,0.98) !important;
}
.post,
b>b,
td.middletext,
.spoilerbody,
td.windowbg > table > tbody > tr > td {
    color: rgb(202,202,200) !important;
    font-size: small !important
}
.code {
    background-color: rgba(124,154,236,0.89) !important;
    color: rgb(4,2,22) !important;
    font-size: small !important
}
.windowbg {
    background-color: rgb(45,48,49) !important;
    color: rgb(173,173,173) !important;
}
.windowbg2 {
    background-color: rgb(46,44,47) !important;
    color: rgb(173,173,173) !important;
}
.windowbg3 {
    background-color: rgb(49,45,45) !important;
    color: rgb(173,173,173) !important;
}
.catbg,
tr.catbg td,
.catbg3,
tr.catbg3 td,
.catbg2,
tr.catbg2 td {
    background: rgb(80,83,92) !important;
    color: #e6e67d !important;
}
.titlebg,
tr.titlebg th,
tr.titlebg td,
.titlebg2,
tr.titlebg2 td {
    background: rgb(35,35,41) !important;
    color: rgb(228,238,139) !important;
}
.menu2,
.sub,
.fly,
.mid ul {
    background-color: rgb(17,17,11) !important;
}
.mid ul a:hover,
.sub a:hover {
    background-color: rgba(253,253,253,0.11) !important;
}
a:link {
    color: rgb(161,232,157) !important;
}
a:visited {
    color: rgb(139,151,181) !important;
}
a:hover {
    color: rgb(91,255,107) !important;
    text-decoration: underline;
}
.quote {
    color: rgb(232,228,228) !important;
    background-color: rgba(68,68,58,0.8) !important;
    font-size: small !important
}
.nav,
.nav:link,
.nav:visited {
    color: rgb(215,213,130) !important;
}
.quoteheader,
.codeheader {
    color: rgb(152,157,170) !important;
    background: rgba(0,0,0,0.36) !important;
}
.mirrortab_back,
.maintab_back,
.maintab_back a,
.mirrortab_back a {
    color: black !important;
    font-weight: bold !important
}
.maintab_back,
.maintab_first,
.maintab_last,
.mirrortab_back,
.mirrortab_first,
.mirrortab_last {
    filter: hue-rotate(-70deg) saturate(50%) !important;
}
.maintab_back a:hover,
.mirrortab_back a:hover {
    color: rgb(255,255,255) !important;
}
a.nav:hover {
    color: rgb(155,182,187) !important;
}
.smalltext {
    color: rgb(192,192,182) !important;
}
textarea,
input,
select,
button {
    background: rgb(17,17,22) !important;
    color: rgb(173,173,173) !important;
}
.tborder {
    background-color: rgb(26,26,21) !important;
}
#bodyarea > table .middletext,
#quickModForm .middletext {
    color: rgb(215,215,207) !important;
}
#MathJax_Zoom {
    background-color: rgb(0,0,0) !important;
}
#ajax_in_progress {
    background: grey !important;
    color: white !important;
}
}

@-moz-document url("http://rinconmatematico.com/instructivolatex/formulas.htm"), url("http://www.rinconmatematico.com/instructivolatex/formulas.htm"), url-prefix("http://rinconmatematico.com/foros/index.php?action=printpage") {
*, .quote, .code, body, .quoteheader, .codeheader {
    color: black !important;
    background-color:white !important;
    background: white !important
}
a:link {
    color:blue !important
}
hr {
    background-color: black !important
}
}

@-moz-document url("http://rinconmatematico.com/foros/"), url("http://rinconmatematico.com/foros/index.php") {
.windowbg2 tr:nth-of-type(2n-1) {
    background-color:rgb(30,29,37) !important
}
}
