css:
@-moz-document domain("quora.com") {
*, .ContentWrapper, .SiteHeader, div, .web_page, .AskQuestionButton, .LookupBarSelector, .selector_input.text {
    background-color: #111111 !important;
    color: #E1E1E1;
    background-clip: content-box, padding-box;
}

.threaded_comments {
    background-color: #222222;
    color: #E1E1E1;
    border: black 1px solid;
}

.BlogMain .BoardItem .author .user, .BlogItemMain .BoardItem .author .user, .BlogMain .BoardStandaloneItem .author .user, .BlogItemMain .BoardStandaloneItem .author .user {
    color: #E1E1E1;
}

.Button .TwoStateButton .BlogPost .Upvote .pressed .primary_action .main_button {
    background-color: blue;
}

textarea {
    background-color: #6d6d6d;
    color: #E1E1E1;
}

.AddQuestionLookupBarButton {
    /*background-color: #3a3a3a;*/
    background-color: #6d6d6d;
    color: #E1E1E1;
}

.windows_badge {
    background-color: red !important;
}

.HyperLinkFeedStory {
    display: none;
}

.js_notif_dropdown > *, .Notif > *, .notif_item > *, .SentNotif > * {
   color: white !important;
}

.AskQuestionButton {
    /* background-color: #b92b27; */
    border: 1px solid #962520;
}

.user {
    color: #E1E1E1;
    text-decoration: underline;
}

.Upvote {
    background-color: #6d6d6d;
}

.hover_menu_contents.lazy {
    background-color: #777777;
}

.hover_menu_contents.lazy:hover {
    background-color: #aaaaaa;
}

.author_name {
    color: #E1E1E1;
}
}
