css:
@-moz-document domain("www.brick-hill.com") {
:root {
--bkg-color: /*[[masterC]]*/;
--text: /*[[textC]]*/;
}
/* Holy hell this banner thing took WAY too long to make so you better thank me :) */
div#banner:after {
    content: url(https://i.imgur.com/v63S0cl.png);
    margin-bottom: 75px;
    mix-blend-mode: luminosity;
    position:absolute;
    margin-top: -8px;
    margin-left: 192px;
    color: transparent;
    z-index: 2;
}
div#banner:before {
    content: '..........................................................................................';
    -webkit-mask-image: url(http://techsworld.hol.es/image2vector1.svg?r=5);
    transform: scale(1.2899);
    background: var(--bkg-color);
    padding-bottom: 61px;
    position:absolute;
    margin-top: 3px;
    margin-left: 250.3px;
    color: transparent;
    shape-rendering: geometricprecision;
    image-rendering: auto;
    z-index: 1;
    /* filter: blur(3px); */
}  
#welcome {
    position: absolute;
    z-index: 3;
}
body {
    background: -webkit-linear-gradient(left, #ffffff66 0%,var(--bkg-color) 50%,#ffffff66 100%)!important;
    background-color: var(--bkg-color)!important;
}
#navbar, input[type="button"], input[type="submit"], input[type="reset"], input[type="file"]::-webkit-file-upload-button, button:not(.colorPallete), select, .button-style, div[style="float:left;text-align:center;margin-right:10px;padding-bottom: 100px;background-color:#77B9FF;border-right:1px solid black;"], .crateSideBarButton, #welcome, #info, #navlower, #footer, .shopSideBarButton, .thread-title, #panel, th, .blue-button, .spoiler  {
background-color: var(--bkg-color)!important;
color: var(--text)!important;
}
blockquote {
    border-color: var(--bkg-color)!important;
}
#navlower {
    background: -webkit-linear-gradient(left, #ffffff66 0%, #ffffff66 100% )
}
#footer {
    background: -webkit-linear-gradient(left, #00000021 0%, #00000021 100% )

}
a.nav, #navbar a, #footer span {
color: var(--text)!important;
}
}
