css:
@-moz-document url-prefix("https://discordapp.com/channels/") {
/* 
**********************************************
**********************************************
**********************************************

Make Discord Notifications Stand Out

**********************************************
**********************************************
**********************************************/

/* SERVERS
***************************/
/* Faux Notification */
.guilds-wrapper .guilds .guild.unread:before {
    background: #f04747;
    display: block;
    background-color: #f04747;
    position: absolute;
    right: -2px;
	bottom: -2px;
    top: auto;
	left: auto;
	z-index: 1;
	padding: 3px;
	text-align: center;
    border-radius: 3px;
	height: auto;
    box-shadow: 0 1px 0 rgba(0,0,0,.25), inset 0 1px 0 hsla(0,0%,100%,.15);
	content: '1'; 
	display: block; 
	color: #fff; 
	text-shadow: 0 1px 0 rgba(0,0,0,.25);
	font-size: 12px;
	font-weight: 500; 
}

/* Real Notification */
.guilds-wrapper .guilds .guild .badge {
    bottom: -2px;
    right: -2px;
    pointer-events: none;
    position: absolute;
    z-index: 1;
	background: #f04747;
}




/* SERVER CHANNELS
***************************/
/* Faux Notifications */
.unread-1Dp-OI { 
    display: block;
    background-color: #f04747;
    position: absolute;
    right: 8px;
	left: auto;
	z-index: 1;
    top: calc(50% - 5px);
	width: 16px; height: 18px;
	text-align: center;
    border-radius: 3px;
    box-shadow: 0 1px 0 rgba(0,0,0,.25), inset 0 1px 0 hsla(0,0%,100%,.15);
}

.unread-1Dp-OI:after { 
	content: '1'; 
	display: block; 
	color: #fff; 
	padding: 3px 2px; 
	text-shadow: 0 1px 0 rgba(0,0,0,.25);
	font-size: 12px;
	font-weight: 500; 
}


/* Real Notifications */
.wrapper-232cHJ { 
	position: relative; 
	z-index: 2; 
}
}
