css:
@-moz-document domain("translationnoob.blogspot.com") {
div {background-color: #2a2a2a ; color: #ccc !important;}
body {background-color: #2a2a2a; color: #ccc}
div {background-color: transparent;}
* {color: #ccc !important;}

span {background: none !important;}
.Blog .blog-posts .post-outer-container:last-child {background-color: #2a2a2a ; color: #ccc ;}
.centered-top-container.sticky {background-color: #2a2a2a ;}
.page_body .widget {background-color: #2a2a2a ;}


a {color: #C9C4C4;}
a:hover {color: #C4B88A;}
a.timestamp-link {color: #aaf !important; background: #55a;}
site-navigation {background: none !important;}
site-navigation {background-color: #131313 !important;}
.sidebar_top .widget.Profile {background-color: #2a2a2a;}
.sidebar-container {background-color: #2a2a2a;}
.sidebar-container .navigation {background-color: #2a2a2a ; color: #afafaf;}
.sidebar-container .sidebar_bottom {background-color: #2a2a2a ; color: #afafaf;}
.sidebar-container .widget .title {color: #ccc;}
.sidebar-container {position: unset;}
.page_body .centered {max-width: 100%;}
.MsoNormal {background-color: #2a2a2a !important;}
.widget.Profile .individual {background-color: #2a2a2a;}
.widget.Profile .individual .profile-link {color: #ccc;}
comments {background-color: #241d1d !important; color: #afafaf;}
#comments .comment .comment-header .user a {color: #aaf;}
#page_body .FeaturedPost, .Blog .blog-posts .post-outer-container {background-color: #2a2a2a ;}
.post-title {color: #afafaf;}
.post-title, .post-title a {color: #afafaf;}
.PopularPosts .post-title, .PopularPosts .post-title a {color: #afafaf;}
}
