css:
@-moz-document domain("ameblo.jp"), domain("news.ameba.jp") {
/* Ameblo Force Meiryo  ver.2020.08.20 */
/* ブログ主要部のフォント指定 ********/
#ambHeader,
.bfl-bigfooter,
._iI3GpNIa,
footer,
.skin-page .skin-blogBody,
.skinBody .skinContentsFrame,
.ReactModalPortal,
#frame #wrap,
[data-reactroot] {
    font-family: 'ヒラギノ角ゴ Pro W3', 'Hiragino Kaku Gothic Pro', メイリオ, Meiryo, 'ＭＳ Ｐゴシック', 'MS PGothic', sans-serif; }

/* いいねボタン */
._SRPdpszF {
    font-family: メイリオ, Meiryo }

._21qDw0hn {
    margin-left: -6px; }

/* アメーバ絵文字位置補正 */
img[src*="emoji.ameba.jp"] {
    vertical-align: -2px !important; }

img[src*=char2] {
    vertical-align: -2px !important; }

img[src*=char3] {
    vertical-align: -3px !important;
    margin-top: -4px !important; }

/* twemoji位置補正 */
img[src*="twemoji"] {
    vertical-align: -2px !important; }

/* 修飾表示 共通指定 ***************/
._1a0_hTML {
    padding: 0 60px 0 0; }

._1a0_hTML::after {
    content: "Meiryo";
    color: white;
    position: absolute;
    right: 8px;
    top: 8px;
    padding: 1px 4px 0;
    font: bold 14px Meiryo;
    border: 1px solid #fff;
    border-radius: 5px;
    background: red; }
}

@-moz-document regexp("ameblo.jp"), regexp("news.ameba.jp") {
/* 修飾表示 amebloヘッダー反転色 背景黒 */
/* ヘッダー全体 ******************/
._1NHfCU0F {
    background: #000; }

/* ヘッダー左側 ******************/
/* アメーバマーク */
._jm4LLuUd {
    padding: 1px 5px;
    border-radius: 4px;
    background: #fffffffc; }

/* ヘッダー左側リンク */
._1wfBdJLw a {
    color: #fff !important; }

/* ヘッダー右側 ******************/
/* Ambトピックス */
._226P45V8 span {
    color: #fff; }

._2sVZUQ_M span {
    color: #fff; }

/* Ambトピックス プルダウンメニュー */
._2VAk1gaA {
    background: #000;
    border: 1px solid #009688; }

._1a5TPYGq {
    color: #fff; }

._3CWi855x {
    color: #fff; }

._1InKdA7W a {
    color: #fff !important; }

._1InKdA7W a:hover {
    opacity: 1;
    outline: 1px solid #fff; }

/* 右ユーザー表示 */
._1a0_hTML {
    background: transparent; }

._2ZOYA4pw {
    color: #fff; }

._32TL3I9j {
    background: #000; }

._3ulyPSpN {
    color: #fff; }

._1Vsf8Fvv,
._1Vsf8Fvv:link,
._1Vsf8Fvv:visited {
    color: #fff; }

._1Vsf8Fvv:hover,
._1Vsf8Fvv:link:hover,
._1Vsf8Fvv:visited:hover {
    background-color: #676767; }
}

@-moz-document regexp("ameblo.jp"), regexp("news.ameba.jp") {
/* 修飾表示 文字赤・背景白 */
._1a0_hTML {
    padding: 0 60px 0 0; }

._1a0_hTML::after {
    content: "Meiryo";
    color: red;
    position: absolute;
    right: 8px;
    top: 8px;
    padding: 1px 4px 0;
    font: bold 14px Meiryo;
    border: 1px solid #ccc;
    border-radius: 4px;
    background: #fff; }
}

@-moz-document regexp("ameblo.jp"), regexp("news.ameba.jp") {
/* 修飾表示 文字グレー・背景白 */
._1a0_hTML {
    padding: 0 60px 0 0; }

._1a0_hTML::after {
    content: "Meiryo";
    color: #888;
    position: absolute;
    right: 8px;
    top: 8px;
    padding: 1px 4px 0;
    font: bold 14px Meiryo;
    border: 1px solid #ccc;
    border-radius: 4px;
    background: #fff; }
}

@-moz-document regexp("ameblo.jp"), regexp("news.ameba.jp") {
/* 修飾表示 文字白・背景赤 */
._1a0_hTML {
    padding: 0 60px 0 0; }

._1a0_hTML::after {
    content: "Meiryo";
    color: #fff;
