css:
@-moz-document regexp("https://devrant.com/rants/(.*)"), regexp("https://devrant.com/users/(.*)") {
.rant-banner, .profile-banner { background-color: var(--colorDarkBlue) !important; }
.rant-avatar-scroll img, .profile-avatar-bg img { border-radius: 50%; }
.notif-badge { border-color: var(--colorDarkBlue) !important; }
.icon-rantsemoticon-base1, .icon-rantsemoticon-emoji1 { opacity: 1 !important; }
.icon-rantsemoticon2 { opacity: 0 !important; }
}
