css:
@-moz-document domain("www.runoob.com"){
body{
background: url("data:image/png;base64,");
}
.logo-search+.navigation {
    /* background: #96b97d; */
    background: linear-gradient(to right,#41b7da,#2ecec7);
    box-shadow: 1px 1px 13px 0px #61cad4;
    padding: 1px;
}
.codelist a.item-top h4 {
    color: #36afa5;
    font-family: -webkit-body;
    background-image: -webkit-gradient(linear, right 0, 0 0, from(rgb(0, 255, 78)), to(rgb(21, 170, 228)));
    -webkit-text-fill-color: transparent;
    -webkit-background-clip: text;
    text-shadow: 0.3px 0.5px 0.5px #279eb147;
    margin-bottom: 13px;
    font-size: 15px;
}
.codelist a.item-top:hover {
    /* background-color: #f1f1f1; */
    
    background-color: #f9f9f9;
    box-shadow: 1px 4px 20px 1px rgba(186, 95, 255, 0.32), 0px 2px 2px 1px rgba(144, 48, 216, 0.11);
}
.codelist a.item-top {
    width: 27%;
    margin: 0 10px 14px 10px;
    transition: 0.3s;
    transition-delay: 0.05s;
    border-radius: 8px;
    height: 85px;
    background: linear-gradient(125deg,#f5f5f5c2,#f3f3f3);
    padding: 6px 16px;
    box-shadow: 1px 1px 3px 0px #e6e6e6;
}
.search form {
    width: 50%;
    float: right;
    background: unset;
}
.search input {
    line-height: 34px;
    left: 9px;
    top: 0;
    text-align: initial;
    white-space: nowrap;
    right: 9px;
    height: 35px;
    width: 94%;
    padding: 0 3% 0 3%;
    background-color: #fff;
    border: 1px solid #cccccc;
    border-radius: 3px;
    color: #ccc;
    font-weight: 400;
    font-size: 1.2em;
    background-color: #fbfbfb;
    box-shadow: 1px 2px 8px 0px #c3d3d6b8;
    transition: 0.4s;
}
.search input:focus {
    color: #cccccc;
    opacity: .8;
    box-shadow: 1px 1px 20px 1px #27d6e2;
}
.logo-search {
    margin: unset;
    padding-bottom: 15px;
    padding-top: 17px;
    
}
.fourcol, .left-column {
    width: 16%;
    display: block;
    box-shadow: 2px 0px 6px #d2dbdc;
}
.gallery-list {
    padding: 0;
    margin-bottom: unset;
}
.tab{
	opacity: .9;
	padding: 5px 10px;
	background: linear-gradient(135deg,#e0e0e0eb,#f9f9f9);
	
}
a[target="_top"][style]{
	background: linear-gradient(145deg,#1ebcea,#13efdb) !important;
}
.article-intro h1 span {
    font-family: -webkit-body;
    background-image: -webkit-gradient(linear, right 0, 0 0, from(rgb(63, 226, 121)), to(rgb(44, 171, 220)));
    -webkit-text-fill-color: transparent;
    -webkit-background-clip: text;
    text-shadow: 0.3px 0.5px 0.5px #279eb147;
    font-size: 1em;
    
}
.design[id="leftcolumn"]{
	border: 1px solid #eaeaea;
	padding: unset;
}
.design[id="leftcolumn"] a{
	line-height: 22px;
    font-size: 1.05em;
    /* background-color: #f1f1f1; */
    text-decoration: none;
    background: linear-gradient(125deg,#efefef,#f9f9f9);
    display: block;
    /* color: red; */
    padding: 7px 10px 7px 20px;
    border-bottom: 1px dashed #bdbdbd;
    color: #465252;
    transition: 0.25s;
}
.design[id="leftcolumn"] a:hover{
	background: linear-gradient(145deg,#1ebcea,#13efdb) !important;
	color: #000000;
    padding: 8px;
    padding-left: 25px;
    font-size: 14px;
}
.fourcol, .left-column {
    width: 16%;
    display: block;
    box-shadow: 1px 1px 17px 1px #81a1ab45;
    border-radius: 10px;
}
.left-column>.tab{
	border-top: 1px solid rgba(0,0,0,.1);
}
.sidebar-tree {
        border: unset;
    margin: 0;
    padding: 0;
    border-radius: 8px;
    /* box-shadow: 1px 1px 9px #bdbdbd94; */
    /* z-index: 9999; */
}
.sidebar-tree ul li {
    border-bottom: 1px dashed #dedede;
    
}
.sidebar-box.sidebar-cate {
    padding: 0;
    margin: 0;
    background: linear-gradient(125deg,#f5f5f55c,#a5a5a54f);
    /* box-shadow: 1px 1px 20px #88848457; */
    /* background-color: #e0e0e0; */
    /* border-radius: 20px; */
}
.ad-box-large {
    /* background-color: #fff; */
    border-radius: 8px;
    /* display: block; */
    box-shadow: 2px 2px 11px 0px #30ccc959, 0px 1px 1px 1px rgba(55, 128, 130, 0.11);
    overflow: hidden;
    margin-left:5px; 
}
.ad-600160 {
    display: none;
}

.article {
    background: linear-gradient(135deg,#f5fbfb,#ffffff);
    box-shadow: 2px 2px 30px 1px #2decff3d;
}
div.example{
	margin: 15px 0 5px 0;
    padding: 8px 10px 5px;
    box-sizing: border-box;
    /* background: linear-gradient(125deg,#e2e2e2db,#f9f9f9); */
    background: linear-gradient(145deg,#bbc1c352,#65bad436);
    border: 2px dashed #b5b9b9;
    border-radius: 5px;
}
div.example_code{
	padding: 6px;
    border: 1px solid #e4e4e4fa;
    font-size: 1.05em;
    margin-left: 2px;
    border-radius: 6px;
}
h2[class*="example"]{
	font-size: 1.3em;
    font-family: -webkit-body;
    background-image: -webkit-gradient(linear, right 0, 0 0, from(rgb(19, 195, 179)), to(rgb(73, 153, 253)));
    -webkit-text-fill-color: transparent;
    -webkit-background-clip: text;
    text-shadow: 0.3px 0.5px 0.5px #279eb147;
    /*width: 44px;*/
    margin-left: 3px;
    letter-spacing: 3px;
    display: inline-block;
}
[class*="example"]{
	border-radius: 6px;
}
a.showbtn, a.showbtn:link, a.showbtn:visited, a.tryitbtn, a.tryitbtn:link, a.tryitbtn:visited {
    display: inline-block;
    color: #FFF;
    /* background-color: #8ac007; */
    font-weight: 700;
    font-size: 12px;
    background: linear-gradient(145deg,#2ac2ef,#18e6d4);
    text-align: center;
    padding-left: 10px;
    padding-right: 10px;
    padding-top: 5px;
    padding-bottom: 4px;
    text-decoration: none;
    margin-left: 6px;
    /* margin-top: 0; */
    margin-bottom: 5px;
     border: unset; 
    border-radius: 5px;
    white-space: nowrap;
    box-shadow: 1px 1px 4px #cacaca9e;
        transition: 0.3s;
}
a.showbtn:active, a.showbtn:hover, a.tryitbtn:active, a.tryitbtn:hover {
    color: #ffffff;
    box-shadow: 1px 1px 15px #42e5ff;
}
#comments .altblock {
    background: linear-gradient(145deg,#26bae6,#10f9e4);
    background-size:unset !important;
}
