    background-image: -webkit-gradient(linear, right 0, 0 0, from(rgb(19, 195, 179)), to(rgb(73, 153, 253)));
    -webkit-text-fill-color: transparent;
    -webkit-background-clip: text;
    text-shadow: 0.3px 0.5px 0.5px #279eb147; 
}*/
a{
	text-decoration-line: none !important;
}
p{
	background: unset !important;
}
.logo>h1{
	-webkit-background-clip: unset;
	background-image: url("data:image/png;base64,");
}
h1{
	font-family: -webkit-body;
    background-image: -webkit-gradient(linear, right 0, 0 0, from(rgb(19, 195, 179)), to(rgb(73, 153, 253)));
    -webkit-text-fill-color: transparent;
    -webkit-background-clip: text;
    text-shadow: 0.3px 0.5px 0.5px #279eb147;
    margin-left: 3px;
    letter-spacing: 1px;
    display: inline-block;
}
h2.left{
	padding: 0px 5px 5px 15px;
	font-size: 16px;
}
a[target="_blank"]>strong,p>strong{
	font-size: 1.1em;
    background-image: -webkit-gradient(linear, right 0, 0 0, from(rgb(198, 74, 220)), to(rgb(48, 162, 243)));
    -webkit-text-fill-color: transparent;
    -webkit-background-clip: text;
    text-shadow: 0.3px 0.5px 0.5px #7b869447;
    margin-left: 3px;
    letter-spacing: 0.5px;
}
blockquote {
    background-color: #267aa921;
    padding: 8px;
    margin: 10px;
    font-size: 13px;
    width: 94%;
    line-height: 2em;
    border-radius: 5px;
}
.marked {
    background-color: #aba6a62e;
}
ul>li p>a{
	background-image: -webkit-gradient(linear, right 0, 0 0, from(rgb(19, 195, 179)), to(rgb(73, 153, 253)));
    -webkit-text-fill-color: transparent;
    -webkit-background-clip: text;
    text-shadow: 1px 1px 1px #70c3b947;
    font-size: 1.1em;
    font-weight: bold;
}
}
