css:
@-moz-document domain("imoutosite.wordpress.com") {
article p {
	font-size: 1.1em;
	letter-spacing: .06em;
	line-height: 2em;
	text-rendering: optimizelegibility;
}
article p[style*="background:white"] {
	background: none !important;
}
article p [style*="color"] {
	color: #8a755c !important;
}
article p[style*="padding"],
article p[style*="padding"] span,
article p[style*="padding"] [style*="color:#44"] {
	color: hsl(33, 0%, 54%) !important;
}

}

@-moz-document domain("imoutosite.wordpress.com") {
body {
	background-color: #333;
	color: #8a755c;
}
.entry-content-wrapper {
	background-color: hsl(33, 20%, 5%) !important;
	padding: 15px;
}
.comment-author .fn a,
.comment-author .fn a:visited {
	filter: brightness(100%) invert(100%) hue-rotate(180deg) !important;
}
}
