css:
@-moz-document url-prefix("https://720yun.com/") {
/** 720yun截图辅助工具 **/

/* 隐藏部分元素 */
[class^=TopContainer_container_],
[class^=Theme1_right_],
[class^=LBButton_title_],
[class^=LBButton_size_],
[class^=LBButton_icon_]{
    display: none
}

/* 隐藏评论 */
#krpanoSWFObject > div > div:nth-child(2){
	display: none
}

/* 使用触发角还原场景选择功能 */
[class^=LBButton_button_]{
	transition: .2s
}
[class^=LBButton_button_]:hover{
	background: #fff
}
}
