css:
@-moz-document domain("github.com") {
/* ==UserStyle==
@name         GitHub 98
@namespace    github.com/AtomicBlom
@version      1.0.0
@description  Themes GitHub to look like Windows 98 - Inspired by a tweet by 
@author       AtomicBlom (http://twitter.com/AtomicBlom)
@license      CC-BY-SA-4.0
==/UserStyle== */
body {
  background-color: #d6d3ce;
  font-family: "MS Sans Serif";
}
body .Header {
  background: #000080;
  /* Old browsers */
  background: -moz-linear-gradient(left, #000080 0%, #a2c8f5 100%);
  /* FF3.6-15 */
  background: -webkit-linear-gradient(left, #000080 0%, #a2c8f5 100%);
  /* Chrome10-25,Safari5.1-6 */
  background: linear-gradient(to right, #000080 0%, #a2c8f5 100%);
  /* W3C, IE10+, FF16+, Chrome26+, Opera12+, Safari7+ */
  color: white;
}
body .Header .header-search-wrapper {
  border: inset 2px;
  border-radius: 0;
  background: white;
}
body .Header a {
  text-decoration: underline;
}
body .pagehead {
  background-color: #d6d3ce !important;
  border-bottom: none !important;
  margin-bottom: 0 !important;
}
body .pagehead .reponav {
  top: 2px;
}
body .pagehead .reponav-item {
  background: #d6d3ce;
  border-radius: 6px 6px 0 0;
  border: 2px outset White;
  border-bottom: none 0;
  padding: 0 4px;
  color: black;
  margin-top: 4px;
  margin-bottom: 2px;
}
body .pagehead .reponav-item.selected {
  background: #d6d3ce;
  padding: 4px 2px 2px 2px;
  border-color: White;
  margin-top: 0;
  margin-bottom: 0;
}
body .pagehead .reponav-item > svg {
  display: none;
}
body .container {
  width: 1000px;
}
body .container .repository-content {
  border: outset 2px;
  padding: 1em;
}
body .container .repository-content .file-wrap {
  border: inset 2px white;
}
body .container .repository-content .files td {
  border-top: none;
  background: transparent;
  padding-top: 3px;
  padding-bottom: 3px;
}
body .container .repository-content .files td a {
  color: black;
}
body .container .repository-content .files td a .simplified-path {
  color: black;
}
body .container .repository-content .files td.content a {
  text-decoration: underline;
}
body .container .repository-content .readme > h3 {
  background: #000080;
  /* Old browsers */
  background: -moz-linear-gradient(left, #000080 0%, #a2c8f5 100%);
  /* FF3.6-15 */
  background: -webkit-linear-gradient(left, #000080 0%, #a2c8f5 100%);
  /* Chrome10-25,Safari5.1-6 */
  background: linear-gradient(to right, #000080 0%, #a2c8f5 100%);
  /* W3C, IE10+, FF16+, Chrome26+, Opera12+, Safari7+ */
  color: white;
  padding: 2px 8px;
  border-radius: 0;
}
body .container .repository-content .issues-listing .table-list-header {
  background: #d6d3ce;
}
body .container .repository-content .issues-listing .table-list-header > label {
  border: 2px outset White;
  background: #d6d3ce;
  width: 40px;
  padding: 0 0 0 14px !important;
}
body .container .repository-content .issues-listing .table-list-header > label > input {
  margin-top: 6px !important;
}
body .container .repository-content .issues-listing .table-list-header .btn-link {
  padding-top: 2px;
  padding-bottom: 2px;
  color: black;
}
body .container .repository-content .issues-listing .table-list-header .table-list-filters .states {
  border: 2px outset White;
  background: #d6d3ce;
  padding-right: 16px;
}
body .container .repository-content .issues-listing .table-list-header .table-list-filters > div > div, body .container .repository-content .issues-listing .table-list-header .table-list-filters > div > details {
  border: 2px outset White;
  background: #d6d3ce;
}
body .container .repository-content .issues-listing .js-issue-row {
  background-color: white;
  border: none;
}
body .container .repository-content .issues-listing .timeline-comment-header.clearfix::after {
  display: inline;
}
body .container .repository-content .issues-listing .timeline-comment-header {
  background: #808080;
  /* Old browsers */
  background: -moz-linear-gradient(left, #808080 0%, #b5b5b5 100%);
  /* FF3.6-15 */
  background: -webkit-linear-gradient(left, #808080 0%, #b5b5b5 100%);
  /* Chrome10-25,Safari5.1-6 */
  background: linear-gradient(to right, #808080 0%, #b5b5b5 100%);
  /* W3C, IE10+, FF16+, Chrome26+, Opera12+, Safari7+ */
  color: #C0C0C0;
  border-radius: 0;
}
body .container .repository-content .issues-listing .timeline-comment-header-text {
  padding-top: 2px;
  padding-bottom: 2px;
}
body .container .repository-content .issues-listing .timeline-comment-label {
  margin: 1px 0 0 0;
  padding: 2px 5px 1px 5px;
}
body .container .repository-content .issues-listing .timeline-comment-action {
  padding-top: 2px;
  padding-bottom: 2px;
}
body .container .repository-content .issues-listing .timeline-comment.current-user .timeline-comment-header {
  background: #000080;
  /* Old browsers */
  background: -moz-linear-gradient(left, #000080 0%, #a2c8f5 100%);
  /* FF3.6-15 */
  background: -webkit-linear-gradient(left, #000080 0%, #a2c8f5 100%);
  /* Chrome10-25,Safari5.1-6 */
  background: linear-gradient(to right, #000080 0%, #a2c8f5 100%);
  /* W3C, IE10+, FF16+, Chrome26+, Opera12+, Safari7+ */
  color: white;
}
body .container .repository-content .issues-listing .timeline-comment-wrapper {
  border: inset 1px white;
}
body .container .repository-content .stats-switcher-viewport {
  height: 4em;
  border: inset 2px White;
}
body .container .repository-content .stats-switcher-viewport .numbers-summary {
  background-color: White;
  height: 4em;
  padding-left: 32px;
}
body .container .repository-content .stats-switcher-viewport .numbers-summary li {
  padding-top: 0.5em;
  background-repeat: no-repeat;
  background-position: 15px 10px;
  text-align: left;
  padding-left: 56px;
}
body .container .repository-content .stats-switcher-viewport .numbers-summary li:nth-child(1) {
  background-image: url(data:data:image/png;base64,);
}
body .container .repository-content .stats-switcher-viewport .numbers-summary li:nth-child(2) {
  background-image: url(data:image/png;base64,);
}
body .container .repository-content .stats-switcher-viewport .numbers-summary li:nth-child(3) {
  background-image: url(data:image/png;base64,);
}
body .container .repository-content .stats-switcher-viewport .numbers-summary li:nth-child(4) {
  background-image: url(data:image/png;base64,);
}
body .container .repository-content .stats-switcher-viewport .numbers-summary li:nth-child(5) {
  background-image: url(data:image/png;base64,);
}
body .container .repository-content .stats-switcher-viewport .numbers-summary li a {
  text-decoration: underline;
}
body .container .repository-content .stats-switcher-viewport .numbers-summary li svg {
  display: none;
}
body .container .repository-content .repository-lang-stats {
  height: 4em;
  background-color: White;
}
body .container .repository-content .repository-lang-stats-graph {
  padding: 0 1px;
  border-radius: 0;
}
body .container .repository-content .repository-lang-stats-graph .language-color {
  display: none;
}
body .container .repository-content .repository-lang-stats-graph .language-color:nth-child(1) {
  display: table-cell;
  width: 100%;
  background: #003557;
  background: -moz-linear-gradient(left, #003557 0%, #99c5e2 49%, #003557 100%);
  background: -webkit-linear-gradient(left, #003557 0%, #99c5e2 49%, #003557 100%);
  background: linear-gradient(to right, #003557 0%, #99c5e2 49%, #003557 100%);
}
body .container .repository-content .discussion-sidebar-heading {
  color: black;
}
body .container .repository-content .discussion-sidebar .sidebar-notifications .thread-subscription-status .reason {
  background-color: #d6d3ce;
  margin: 0;
  padding-top: 10px;
}
body .social-count {
  background: #d6d3ce;
  padding: 3px;
  border: outset 2px white;
  margin-left: -2px;
  border-radius: 0;
}

.btn, button {
  background: #d6d3ce;
  border-radius: 0;
  border: outset 2px white;
  color: black;
}
.btn:hover, button:hover {
  background: #d6d3ce;
  border-radius: 0;
  border: outset 2px;
  color: black;
}
.btn:active, button:active {
  border-style: inset;
}

.btn-primary, .btn-primary:hover {
  border: outset 2px #A0A0A0;
}

.btn:focus, .btn-primary:focus {
  box-shadow: none;
}

.discussion-timeline-actions {
  background-color: #d6d3ce;
}

.Counter {
  border-radius: 0;
  background-color: transparent;
  padding: 0;
  font-size: 14;
  font-weight: 400;
}
.Counter::before {
  content: "(";
}
.Counter::after {
  content: ")";
}

.list-topics-container > a, .topics-row-container > a {
  border: inset 1px White;
  border-radius: 0;
  background-color: #eeebe2;
  color: black;
  font-size: 11pt;
  padding: 0.2em 0.5em;
}

#partial-discussion-sidebar {
  border: outset 2px white;
  padding: 0 6px;
  padding-bottom: 10px;
}

/*# sourceMappingURL=github.css.map */

}
