css:
@-moz-document domain("tildes.net") {
  /* Main */
  body {
    background-color: #3a3a3a;
    color: #d0d0d0;
  }
  body main {
    background-color: #444343;
  }

  /* Misc. */
  body #sidebar {
    background-color: #444343;
  }
  body .form-select:not([multiple]):not([size]) {
    background-color: #868181;
  }
  body .label-topic-tag {
    color: #9ea7c7;
  }
  body .form-input, .form-input:not(:focus) {
    background-color: #5f5f5f;
    color: white;
  }

  /* Topics */
  body .topic-listing > li {
    background-color: #636363;
  }
  body .topic-listing > li:nth-of-type(2n) {
    background-color: #565656;
    color: #2b2b2b;
  }
  body .is-topic-official h1 a, .is-topic-official h1 a:visited {
    color: #ff7136;
  }
  body .topic-full-byline {
    color: #b3d1d8;
  }
  body .topic-title a {
    color: whitesmoke;
  }
  body .topic-title a:visited {
    color: #a9a9a9;
  }
  body .topic-text-excerpt[open] {
    color: whitesmoke;
  }

  /* Comments */
  body .is-comment-mine > .comment-itself {
    border-left: 3px solid !important;
  }
  body .is-comment-mine > .comment-itself > header .link-user {
    background-color: #357519;
    border-radius: 2px;
    padding: 0 2px;
    color: white;
  }
  body .is-comment-by-op > .comment-itself > header .link-user {
    background-color: #54678e;
    border-radius: 2px;
    padding: 0 2px;
    color: white;
  }
  body .comment header {
    background-color: #868181;
    color: #fbfbfb;
  }
  body .is-comment-collapsed header {
    background-color: #868181;
    color: #fbfbfb;
  }
  body .is-comment-collapsed header a.link-user, .is-comment-collapsed header a.link-user:visited {
    color: #c1bc80;
  }
  body blockquote {
    background-color: #5f5f5f;
    border-color: #222;
  }
  body code, body pre {
    background-color: #777777;
    color: whitesmoke;
  }

  /* Actions */
  body .btn {
    border-color: #aacbe2;
    color: #aacbe2;
  }
  body .post-button {
    color: #aacbe2;
  }
  body .post-button-used {
    color: #e6a44f;
    text-decoration: none;
  }
  body  .post-button:hover {
    color: #60798a;
  }
  body .post-button-used:hover {
    color: #a7783c;
  }
  body a {
    color: #c3be69;
    text-decoration: none;
  }
  body a:visited {
    color: #9c9a7c;
  }
  body a.link-user, a.link-user:visited {
    font-weight: bold;
    color: whitesmoke;
  }
}
