}


.tweet .stats {
    border-bottom: 1px solid #eaeaea;
    border-top: 1px solid #eaeaea;
}

.permalink.self-thread-permalink-with-descendant .inline-reply-tweetbox {
    border: 1px solid #eaeaea;
}

.permalink .in-reply-to .tweet, .permalink .replies-to .tweet, .permalink .replies-to .ThreadedConversation--loneTweet .stream-tombstone-item {
    border-bottom: 1px solid #eaeaea;
}

.ReplyingToContextBelowAuthor {
    color: #807770;
}

.ThreadedConversation, .ThreadedConversation-moreReplies:after {
    border-bottom: 1px solid #eaeaea;
}




















