@-moz-document domain("standing69.com"), domain("www.standing69.com") {
:root {
/*     This is where you set all the options for the skin. */
    
/*     This is the bottom transparency, its applied to most of the container units. Setting it to 1 disables transparency */
    --transparency-bottom-level: .65;    
/*     This is handles the post and comment overlays.  Generally want a lower number, since these things stack on each other */
    --transparency-secondary-level: .55;
    
/*     This is the base background color, used in headers and very low containers */
    --base-bg-color: 0,0,0;
/*     This is the objects that sit on top of there. Buttons, overlays and the like */
    --objects-bg-color: 85,85,97;
    

/*     Base font used, you can use Rationale, Merienda One or any font installed on your computer */
    --base-font: Rationale;
    --base-font-size: 16px;
    --base-font-color: #DDD;
    --header-font: Rationale;
    --header-font-size: 24px;
    --header-font-color: #e1f00c;
    --objects-font-color: #FFF;
    --username-font-size: 18px;

/*     Background control. For a wallpaper just change the url, and make sure fixed says fixed for background-fixed, cover for background-size and background-repeat: none;.
    For no background set a color for background-color (rgb(), hex, and rgba will work here), and make sure it says background:none; for the first line  
     Default configuration is for tiled image
*/
    --background:url('https://standing69.com/filedata/fetch?id=482356&d=1520033601');
    --background-color:#000;
    --background-fixed: none;
    --background-size: auto;
    --background-repeat: repeat;
}

@font-face {
  font-family: 'Rationale';
  font-style: normal;
  font-weight: 400;
  src: local('Rationale'), local('Rationale-Regular'), url(https://fonts.gstatic.com/s/rationale/v8/9XUnlJ92n0_JFxHIfHcccVtN.woff2) format('woff2');
  unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02BB-02BC, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2122, U+2191, U+2193, U+2212, U+2215, U+FEFF, U+FFFD;
}
/* latin */
@font-face {
  font-family: 'Merienda One';
  font-style: normal;
  font-weight: 400;
  src: local('Merienda One'), local('MeriendaOne-Regular'), url(https://fonts.gstatic.com/s/meriendaone/v8/H4cgBXaMndbflEq6kyZ1ht6ohYaz.woff2) format('woff2');
